(43, '&lt;?php\r\n//GET TIME AND DATE\r\n\r\n//TODAY \r\n$today = strtotime ( ''-0 day'' , time() ) ;//\r\n$today_f = showDateAs($today);\r\n\r\n//THREE DAYS AGO\r\n$threeDaysAgo = strtotime ( ''-3 day'' , time() ) ;\r\n$threeDaysAgo_f = showDateAs($threeDaysAgo);\r\n\r\n//EXAMPLE DATE:\r\n$exampleDate=&quot;2009-06-26 16:58:20&quot;;\r\n$exampleDate=strtotime($exampleDate);\r\n$exampleDate = strtotime ( ''-0 day'' , $exampleDate ) ;//\r\n$exampleDate_f = showDateAs($exampleDate);\r\n\r\n\r\n//gets days ago\r\n//$daysAgo = $today-$threeDaysAgo;\r\n//$daysAgo = strtotime ( ''-0 day'' , $daysAgo ) ;//\r\n\r\n//$daysAgo = showDateAs($daysAgo);\r\n\r\n\r\necho(''&lt;br&gt;Today is...''.$today_f) ;  \r\necho(''&lt;br&gt;3 days ago was ...''.$threeDaysAgo_f) ; \r\necho(''&lt;br&gt;Example Date ...''.$exampleDate_f) ; \r\n//echo(''&lt;br&gt;Days Ago ...''.$daysAgo) ; \r\n\r\n//FORMAT YOUR DATE\r\nfunction showDateAs($date){\r\n		//$date = date ( ''Y-m-d H:i:s'' , $date );\r\n		$date = date ( ''Y M d H:i:s'' , $date );\r\n		return $date;\r\n}\r\n\r\n//=====		DATE DIFFERENCE\r\n	\r\n	$date1 = &quot;2010-08-9 16:58:20&quot;;\r\n	$date2 = &quot;2010-08-11 16:58:20&quot;;\r\n	$a_date_diff = date_difference($date1,$date2);\r\n	foreach($a_date_diff as $k=&gt;$v){\r\n		echo(''&lt;br&gt;''.$k.'' ''.$v);\r\n	}\r\n	/*\r\n	$diff = abs(strtotime($date2) - strtotime($date1));\r\n	$years = floor($diff / (365*60*60*24));\r\n	$months = floor(($diff - $years * 365*60*60*24) / (30*60*60*24));\r\n	$days = floor(($diff - $years * 365*60*60*24 - $months*30*60*60*24)/ (60*60*24));\r\n	printf(&quot;%d years, %d months, %d days\\n&quot;, $years, $months, $days);\r\n	*/\r\n	function date_difference($date1,$date2){\r\n			\r\n		$diff = abs(strtotime($date2) - strtotime($date1));\r\n		$years = floor($diff / (365*60*60*24));\r\n		$months = floor(($diff - $years * 365*60*60*24) / (30*60*60*24));\r\n		$days = floor(($diff - $years * 365*60*60*24 - $months*30*60*60*24)/ (60*60*24));\r\n		//printf(&quot;%d years, %d months, %d days\\n&quot;, $years, $months, $days);\r\n		$a_date_diff = array(&quot;Years:&quot;=&gt;$years,&quot;Months:&quot;=&gt;$months,&quot;Days:&quot;=&gt;$days);\r\n		return $a_date_diff;\r\n	}\r\n// end DATE DIFFERENCE\r\n\r\n\r\n\r\n\r\n\r\n?&gt;', 'date- days ago', 'php'),
(44, '\r\n&lt;?PHP\r\nfunction payweek($year){\r\n	if(date(&quot;L&quot;) == &quot;1&quot;){$stateday = 0;$enddo = 366;}else{$stateday = 1;$enddo = 365;}//Decide if Leap Year or Not.\r\n	$currentday = date(&quot;z&quot;);\r\n	//$currentday\r\n	$blonk = &quot;Pay Week:&lt;br&gt;&lt;select&gt;&quot;;\r\n	for($a=$stateday; $a&lt;= $enddo; $a++)\r\n	{\r\n	$monday = &quot;&quot;;\r\n	$monday = date(&quot;D&quot;, mktime(0,0,0,01,$a,$year));\r\n		if($monday == &quot;Mon&quot;)\r\n		{\r\n			//User Friendly Dates\r\n			$display_date = date(&quot;M j&quot;, mktime(0,0,0,01,$a,$year));\r\n			$display_date_end = date(&quot;M j (Y)&quot;, mktime(0,0,0,01,$a+6,$year));\r\n\r\n			//format return date values so that they''ll work with mysql\r\n			$value_date = date(&quot;Y-m-d H:i:s&quot;, mktime(0,0,0,01,$a,$year));\r\n			$value_date_end = date(&quot;Y-m-d H:i:s&quot;, mktime(23,59,59,01,$a+6,$year));//last second of Sunday night\r\n\r\n			$display_value =$value_date.&quot;,&quot;.$value_date_end;// comma delimited value that can later be passed\r\n														// to a POST variable, then exploded on the comma to\r\n														// reveal Start and End date.\r\n\r\n			$blonk .= &quot;&lt;option value=''$display_value''&gt;$display_date - $display_date_end&quot;;\r\n		}\r\n	}\r\n	$blonk .=&quot;&lt;/select&gt;&quot;;\r\n	//echo   $blonk;\r\n	return $blonk;\r\n	//echo date(&quot;H:i:s&quot;, mktime(0,0,9000,01,01,2009));\r\n}\r\n\r\n$menu = payweek(2014);\r\necho $menu;\r\n?&gt;\r\n\r\n\r\n\r\n', 'date- work week by year', 'php'),
