(435, 95, 'TRUNK GROUP SEARCH BY TRUNK GROUP NAME:\r\nSELECT tg.tgid, tg.trunkName as ''Trunkgroup'', (SELECT group_concat(gw.description ORDER BY tgg.theOrder ASC) FROM mv4.trunkgroupGateways tgg LEFT JOIN mv4.dr_gateways gw ON (gw.gwid = tgg.gwid) WHERE tgg.tgid = tg.tgid) as ''Gateway List'' FROM mv4.trunkgroups tg WHERE 1=1 AND tg.tgid IN (SELECT subtgg.tgid FROM trunkgroupGateways subtgg WHERE subtgg.gwid IN (SELECT gwid FROM dr_gateways WHERE type=''20'')) AND tg.trunkName LIKE ''%3130IQI1-CUBE-SP%'' ORDER BY tg.trunkName'),
(436, 95, 'POTENTIAL SQL CULPRIT:\r\nSELECT tg.tgid, tg.trunkName as ''Trunkgroup'', \r\n(SELECT group_concat(gw.description ORDER BY tgg.theOrder ASC) \r\nFROM \r\nmv4.trunkgroupGateways tgg \r\nLEFT JOIN mv4.dr_gateways gw ON (gw.gwid = tgg.gwid) WHERE tgg.tgid = tg.tgid) as ''Gateway List'' \r\nFROM mv4.trunkgroups tg \r\nWHERE 1=1 AND tg.tgid IN \r\n(SELECT subtgg.tgid FROM trunkgroupGateways subtgg WHERE subtgg.gwid IN (SELECT gwid FROM dr_gateways WHERE type=''20''))\r\nAND tg.trunkName LIKE ''%3130IQI-CUBE-SP%'' ORDER BY tg.trunkName'),
