(101, '\r\n// open a database\r\nfunction openDB() {\r\n  var request = indexedDB.open(DBNAME, DBVER);\r\n\r\n  request.onupgradeneeded = function (e) {\r\n    console.log(&quot;Upgrading...&quot;);\r\n    var thisDB = e.target.result;\r\n    var store = null;\r\n    if (!thisDB.objectStoreNames.contains(&quot;people&quot;)) {\r\n      // create objectStore as keyPath=&quot;email&quot;\r\n      store = thisDB.createObjectStore(&quot;people&quot;, {\r\n        keyPath: &quot;email&quot;\r\n      });\r\n      //thisDB.createObjectStore(&quot;people&quot;, { autoIncrement: true });\r\n      \r\n      // create index to ''name'' for conditional search\r\n      store.createIndex(''name'', ''name'', {\r\n        unique: false\r\n      });\r\n      //store.deleteIndex(''name'');\r\n    }\r\n  };\r\n\r\n  request.onsuccess = function (e) {\r\n    console.log(&quot;openDB success!&quot;);\r\n    db = e.target.result;\r\n  };\r\n\r\n  request.onerror = function (e) {\r\n    console.log(&quot;openDB error&quot;);\r\n  };\r\n}\r\n', 'indexedDB: open a datbase', 'html5'),
(102, '\r\n// add data\r\nfunction add(o) {\r\n  var tx = db.transaction([&quot;people&quot;], &quot;readwrite&quot;);\r\n  var store = tx.objectStore(&quot;people&quot;);\r\n  // add ''created'' param\r\n  o.created = new Date();\r\n  // add to store\r\n  var request = store.add(o);\r\n  request.onsuccess = function (e) {\r\n    console.log(&quot;Add ''person'' successful! person=&quot; + JSON.stringify(o));\r\n  };\r\n  request.onerror = function (e) {\r\n    console.log(&quot;Add error&quot;, e.target.error.name);\r\n  };\r\n}\r\n', 'indexedDB: add record', 'html5'),
(103, '/ find by key(email)\r\nfunction findByKey(key) {\r\n  var tx = db.transaction([&quot;people&quot;], &quot;readonly&quot;);\r\n  var store = tx.objectStore(&quot;people&quot;);\r\n  var request = store.get(key);\r\n  request.onsuccess = function (e) {\r\n    console.log(e.target.result);\r\n  };\r\n}', 'indexedDB: find by key (read/get by id)', 'html5'),
(104, '\r\n// find all\r\nfunction findAll() {\r\n  var tx = db.transaction([&quot;people&quot;], &quot;readonly&quot;);\r\n  var objectStore = tx.objectStore(&quot;people&quot;);\r\n  var cursor = objectStore.openCursor();\r\n\r\n  cursor.onsuccess = function (e) {\r\n    var res = e.target.result;\r\n    if (res) {\r\n      console.log(&quot;key&quot;, res.key);\r\n      console.log(&quot;value&quot;, res.value);\r\n      res.\r\n      continue ();\r\n    }\r\n  };\r\n}', 'indexedDB: find all (get/read all)', 'html5'),
(105, '\r\n// find by range\r\nfunction findByRange(from, to) {\r\n  var tx = db.transaction([&quot;people&quot;], &quot;readonly&quot;);\r\n  var objectStore = tx.objectStore(&quot;people&quot;);\r\n  // find by range. condition: from &lt;= ''name'' &lt; to \r\n  var range = IDBKeyRange.bound(from, to, false, true);\r\n  var cursor = objectStore.index(''name'').openCursor(range);\r\n\r\n  cursor.onsuccess = function (e) {\r\n    var res = e.target.result;\r\n    if (res) {\r\n      console.log(&quot;key&quot;, res.key);\r\n      console.log(&quot;value&quot;, res.value);\r\n      res.continue();\r\n    }\r\n  };\r\n}', 'indexedDB: findByRange (loop through cursor)', 'html5'),
(106, '\r\n// remove by key(email)\r\nfunction removeByKey(key) {\r\n  var tx = db.transaction([&quot;people&quot;], &quot;readwrite&quot;);\r\n  var store = tx.objectStore(&quot;people&quot;);\r\n  var request = store.delete(key);\r\n  //var request = store.clear(); // delete all from the store\r\n  request.onsuccess = function (e) {\r\n    // calls even when nothing to remove.\r\n    console.log(&quot;removeByKey success!&quot;);\r\n  };\r\n  request.onerror = function (e) {\r\n    console.log(&quot;removeByKey error!&quot;);\r\n  };\r\n}', 'indexedDB: removeByKey (delete by id)', 'html5'),
(107, '\r\n// update by key(email)\r\nfunction updateByKey(key) {\r\n  var tx = db.transaction([&quot;people&quot;], &quot;readwrite&quot;);\r\n  var store = tx.objectStore(&quot;people&quot;);\r\n  store.get(key).onsuccess = function (e) {\r\n    console.log(&quot;store.get&quot;, key);\r\n    var data = e.target.result;\r\n    if (!data) {\r\n      console.log(&quot;nothing matched.&quot;);\r\n      return;\r\n    }\r\n    // modify ''name'' to upperCase\r\n    data.name = data.name.toUpperCase();\r\n    var request = store.put(data);\r\n    request.onsuccess = function (e) {\r\n      console.log(&quot;put success!&quot;);\r\n    };\r\n    request.onerror = function (e) {\r\n      console.log(&quot;put error!&quot;);\r\n    };\r\n  };\r\n}\r\n', 'indexedDB: updateByKey ( update by id)', 'html5'),
(108, '\r\n// delete db\r\nfunction deleteDB(dbname) {\r\n  var request = indexedDB.deleteDatabase(dbname);\r\n  request.onsuccess = function (e) {\r\n    console.log(&quot;deleteDB success!&quot;);\r\n  };\r\n  request.onerror = function (e) {\r\n    console.log(&quot;deleteDB error!&quot;);\r\n  };\r\n}', 'indexedDB: delete database (drop database )', 'html5'),
(109, 'document.getElementById(''btn_open'').onclick = function (e) {\r\n  openDB();\r\n};\r\ndocument.getElementById(''btn_add'').onclick = function (e) {\r\n  var name = document.getElementById(&quot;name&quot;).value;\r\n  var email = document.getElementById(&quot;email&quot;).value;\r\n  add({ name: name, email: email});\r\n};\r\ndocument.getElementById(''btn_find'').onclick = function (e) {\r\n  var key = document.getElementById(''key'').value;\r\n  findByKey(key);\r\n};\r\ndocument.getElementById(''btn_findAll'').onclick = function (e) {\r\n  findAll();\r\n};\r\ndocument.getElementById(''btn_findByRange'').onclick = function (e) {\r\n  findByRange(&quot;A&quot;, &quot;C&quot;);\r\n};\r\ndocument.getElementById(''btn_remove'').onclick = function (e) {\r\n  var key = document.getElementById(''key4remove'').value;\r\n  removeByKey(key);\r\n};\r\ndocument.getElementById(''btn_update'').onclick = function (e) {\r\n  var key = document.getElementById(''key4update'').value;\r\n  updateByKey(key);\r\n};\r\ndocument.getElementById(''btn_deleteDB'').onclick = function (e) {\r\n  deleteDB(DBNAME);\r\n};\r\n', 'indexedDB: UI prepare UI elements, form elements', 'html5'),
(110, '$(&quot;#finalize&quot;).click(function() {\r\n\r\n		var totalMinutes = 0;\r\n		$.each($(&quot;.data_minutes&quot;), function(i, e) {\r\n			totalMinutes = totalMinutes + parseFloat(e.value);\r\n		});\r\n		sessionStorage.totalMinutes = totalMinutes;\r\n\r\n		var totalTask = 0;\r\n		$.each($(&quot;.data_tasks&quot;), function(i, e) {\r\n			totalTask = totalTask + parseFloat(e.value);\r\n		});\r\n		sessionStorage.totalTask = totalTask;\r\n\r\n		var totalMaterial = 0;\r\n		$.each($(&quot;.data_materials&quot;), function(i, e) {\r\n			totalMaterial = totalMaterial + parseFloat(e.value);\r\n		});\r\n		sessionStorage.totalMaterial = totalMaterial;\r\n\r\n		var totalTasksAndMaterials = totalTask + totalMaterial;\r\n		sessionStorage.totalCost = totalTasksAndMaterials;\r\n\r\n		//populate the SUMMARY  with values clear orphans\r\n		$(&quot;#estimateName&quot;).text(sessionStorage.estimate_name);\r\n\r\n		var cleanTaskTotal = Math.round(sessionStorage.totalTask * 100) / 100;\r\n		$(&quot;#taskCost&quot;).text(&quot;$&quot; + cleanTaskTotal);\r\n\r\n		var cleanMaterialTotal = Math.round(sessionStorage.totalMaterial * 100) / 100;\r\n		$(&quot;#materialCost&quot;).text(&quot;$&quot; + cleanMaterialTotal);\r\n\r\n		var est_hours = Math.round((sessionStorage.totalMinutes / 60) * 100) / 100;\r\n		//alert(est_hours);\r\n		$(&quot;#est_time&quot;).text(est_hours + &quot;hrs&quot;);\r\n		var cleanCostTotal = Math.round(sessionStorage.totalCost * 100) / 100;\r\n		$(&quot;#totalCost&quot;).text(&quot;$&quot; + cleanCostTotal);\r\n\r\n		var summaryDiv = document.getElementById(''summaryDiv'');\r\n		summaryDiv.style.display = &quot;block&quot;;\r\n\r\n	});', 'jquery: loop through and gather data', 'javascript'),
