(25, 'iOS', 'AnimationDynamicX', ' \r\n#import "FBFViewController.h"\r\n\r\n//THANKS TO: http://www.raywenderlich.com/50197/uikit-dynamics-tutorial\r\n\r\n// should redo this tutorial at a few different stages...\r\n\r\n\r\n/*\r\n @protocol UIDynamicItem <NSObject>\r\n \r\n @property (nonatomic, readwrite) CGPoint center;\r\n @property (nonatomic, readonly) CGRect bounds;\r\n @property (nonatomic, readwrite) CGAffineTransform transform;\r\n \r\n @end\r\n */\r\n@interface FBFViewController () <UICollisionBehaviorDelegate>\r\n{\r\n	UIDynamicAnimator *_animator;\r\n	UIGravityBehavior *_gravity;\r\n	UICollisionBehavior *_collision;\r\n    \r\n    BOOL _firstContact;\r\n}\r\n\r\n@end\r\n\r\n@implementation FBFViewController\r\n\r\n- (void)viewDidLoad {\r\n	[super viewDidLoad];\r\n    \r\n    _collision.collisionDelegate = self;\r\n    \r\n    \r\n    //square\r\n	UIView *square = [[UIView alloc] initWithFrame:\r\n	                  CGRectMake(100, 100, 100, 100)];\r\n	square.backgroundColor = [UIColor grayColor];\r\n	[self.view addSubview:square];\r\n    \r\n    //barrier\r\n    UIView* barrier = [[UIView alloc] initWithFrame:CGRectMake(0, 300, 130, 20)];\r\n    barrier.backgroundColor = [UIColor redColor];\r\n    [self.view addSubview:barrier];\r\n    \r\n    \r\n    \r\n    \r\n    //gravity\r\n	_animator = [[UIDynamicAnimator alloc] initWithReferenceView:self.view];\r\n	_gravity = [[UIGravityBehavior alloc] initWithItems:@[square]];\r\n	[_animator addBehavior:_gravity];\r\n    \r\n	//collision\r\n	//_collision = [[UICollisionBehavior alloc] initWithItems:@[square]];\r\n    //_collision = [[UICollisionBehavior alloc] initWithItems:@[square, barrier]]; //interesting collision\r\n    _collision = [[UICollisionBehavior alloc] initWithItems:@[square]];\r\n	_collision.translatesReferenceBoundsIntoBoundary = YES;\r\n	[_animator addBehavior:_collision];\r\n    \r\n    // add a boundary that coincides with the top edge\r\n    CGPoint rightEdge = CGPointMake(barrier.frame.origin.x +\r\n                                    barrier.frame.size.width, barrier.frame.origin.y);\r\n    [_collision addBoundaryWithIdentifier:@"barrier"\r\n                                fromPoint:barrier.frame.origin\r\n                                  toPoint:rightEdge];\r\n    \r\n    _collision.action =  ^{\r\n        NSLog(@"%@, %@",\r\n              NSStringFromCGAffineTransform(square.transform),\r\n              NSStringFromCGPoint(square.center));\r\n        \r\n    };\r\n    \r\n    //dynamic behavior\r\n    UIDynamicItemBehavior* itemBehaviour = [[UIDynamicItemBehavior alloc] initWithItems:@[square]];\r\n    itemBehaviour.elasticity = 0.6;\r\n    [_animator addBehavior:itemBehaviour];\r\n    \r\n    /*\r\n     \r\n     elasticity  determines how elastic collisions will be, i.e. how bouncy or rubbery the item behaves in collisions.\r\n     friction  determines the amount of resistance to movement when sliding along a surface.\r\n     density  when combined with size, this will give the overall mass of an item. The greater the mass, the harder it is to accelerate or decelerate an object.\r\n     resistance  determines the amount of resistance to any linear movement. This is in contrast to friction, which only applies to sliding movements.\r\n     angularResistance  determines the amount of resistance to any rotational movement.\r\n     allowsRotation  this is an interesting one that doesnt model any real-world physics property. With this property set to NO the object will not rotate at all, regardless of any rotational forces that occur.\r\n     */\r\n    \r\n}\r\n\r\n- (void)collisionBehavior:(UICollisionBehavior *)behavior beganContactForItem:(id<UIDynamicItem>)item\r\n   withBoundaryIdentifier:(id<NSCopying>)identifier atPoint:(CGPoint)p {\r\n    NSLog(@"Boundary contact occurred - %@", identifier);\r\n    UIView* view = (UIView*)item;\r\n    view.backgroundColor = [UIColor yellowColor];\r\n    [UIView animateWithDuration:0.3 animations:^{\r\n        view.backgroundColor = [UIColor grayColor];\r\n    }];\r\n    \r\n    if (!_firstContact)\r\n    {\r\n        _firstContact = YES;\r\n        \r\n        UIView* square = [[UIView alloc] initWithFrame:CGRectMake(30, 0, 100, 100)];\r\n        square.backgroundColor = [UIColor grayColor];\r\n        [self.view addSubview:square];\r\n        \r\n        [_collision addItem:square];\r\n        [_gravity addItem:square];\r\n        \r\n        UIAttachmentBehavior* attach = [[UIAttachmentBehavior alloc] initWithItem:view\r\n                                                                   attachedToItem:square];\r\n        [_animator addBehavior:attach];\r\n    }\r\n    \r\n    \r\n    \r\n}\r\n\r\n- (void)didReceiveMemoryWarning {\r\n	[super didReceiveMemoryWarning];\r\n	// Dispose of any resources that can be recreated.\r\n}\r\n\r\n@end', '2014-03-02 01:24:54'),
(26, ' iOS', ' CalendarEventX', ' \r\n\r\n//FRAMEWORKS\r\nEventKit\r\nEventKitUI\r\n\r\n// .h\r\n#import <UIKit/UIKit.h>\r\n#import <EventKit/EventKit.h>\r\n#import <EventKitUI/EventKitUI.h>\r\n \r\n\r\n@interface FBFViewController : UIViewController <EKEventEditViewDelegate>\r\n\r\n@property (nonatomic, strong) EKEventStore *ekEventStore;\r\n@property (nonatomic, strong) EKCalendar *ekCalendar;\r\n@property (nonatomic, strong) NSMutableArray *eventsArray;\r\n@property (weak, nonatomic) IBOutlet UIBarButtonItem *addButton;\r\n\r\n- (IBAction)addEvent:(id)sender ;\r\n@end\r\n\r\n\r\n// .m\r\n\r\n\r\n@implementation FBFViewController\r\n\r\n- (void)viewDidLoad {\r\n	[super viewDidLoad];\r\n	/*   FRAMEWORKS: EventKit and EventKitUI  */\r\n	self.ekEventStore = [[EKEventStore alloc] init];\r\n	self.eventsArray = [[NSMutableArray alloc] initWithCapacity:0];\r\n	self.addButton.enabled = NO;\r\n}\r\n\r\n- (void)viewDidAppear:(BOOL)animated {\r\n	[super viewDidAppear:animated];\r\n	[self checkEventStoreAccessForCalendar];\r\n}\r\n\r\n- (void)prepareForSegue:(UIStoryboardSegue *)segue sender:(id)sender {\r\n	if ([[segue identifier] isEqualToString:@"showEventViewController"]) {\r\n		EKEventViewController *eventViewController = (EKEventViewController *)[segue destinationViewController];\r\n		eventViewController.allowsEditing = YES;\r\n	}\r\n}\r\n\r\n#pragma mark -\r\n#pragma mark Calendar Permission\r\n\r\n- (void)checkEventStoreAccessForCalendar {\r\n	EKAuthorizationStatus status = [EKEventStore authorizationStatusForEntityType:EKEntityTypeEvent];\r\n\r\n	switch (status) {\r\n		case EKAuthorizationStatusAuthorized:[self accessGrantedForCalendar];\r\n			break;\r\n\r\n		case EKAuthorizationStatusNotDetermined:[self requestCalendarAccess];\r\n			break;\r\n\r\n		case EKAuthorizationStatusDenied:\r\n		case EKAuthorizationStatusRestricted:\r\n		{\r\n			UIAlertView *alert = [[UIAlertView alloc] initWithTitle:@"Privacy Warning" message:@"Permission was not granted for Calendar"\r\n			                                               delegate:nil\r\n			                                      cancelButtonTitle:@"OK"\r\n			                                      otherButtonTitles:nil];\r\n			[alert show];\r\n		}\r\n		break;\r\n\r\n		default:\r\n			break;\r\n	}\r\n}\r\n\r\n- (void)requestCalendarAccess {\r\n	[self.ekEventStore requestAccessToEntityType:EKEntityTypeEvent completion: ^(BOOL granted, NSError *error)\r\n	{\r\n	    if (granted) {\r\n	        FBFViewController *__weak weakSelf = self;\r\n	        dispatch_async(dispatch_get_main_queue(), ^{\r\n	            [weakSelf accessGrantedForCalendar];\r\n			});\r\n		}\r\n	}];\r\n}\r\n\r\n- (void)accessGrantedForCalendar {\r\n	self.ekCalendar = self.ekEventStore.defaultCalendarForNewEvents;\r\n	self.addButton.enabled = YES;\r\n	self.eventsArray = [self getCalendarEventsForLast24Hours];\r\n	//[self.tableView reloadData];\r\n}\r\n\r\n#pragma mark -\r\n#pragma mark Get Calendar Events\r\n- (NSMutableArray *)getCalendarEventsForLast24Hours {\r\n	NSDate *startDate = [NSDate date];\r\n	NSDateComponents *tomorrowDateComponents = [[NSDateComponents alloc] init];\r\n	tomorrowDateComponents.day = 1;\r\n	NSDate *endDate = [[NSCalendar currentCalendar] dateByAddingComponents:tomorrowDateComponents\r\n	                                                                toDate:startDate\r\n	                                                               options:0];\r\n	NSArray *calendarArray = [NSArray arrayWithObject:self.ekCalendar];\r\n	NSPredicate *predicate = [self.ekEventStore predicateForEventsWithStartDate:startDate\r\n	                                                                    endDate:endDate\r\n	                                                                  calendars:calendarArray];\r\n	NSMutableArray *events = [NSMutableArray arrayWithArray:[self.ekEventStore eventsMatchingPredicate:predicate]];\r\n\r\n	return events;\r\n}\r\n\r\n#pragma mark -\r\n#pragma mark Add an event\r\n\r\n- (IBAction)addEvent:(id)sender {\r\n	EKEventEditViewController *ekEventEditViewController = [[EKEventEditViewController alloc] init];\r\n	ekEventEditViewController.eventStore = self.ekEventStore;\r\n	ekEventEditViewController.editViewDelegate = self;\r\n	[self presentViewController:ekEventEditViewController animated:YES completion:nil];\r\n}\r\n\r\n#pragma mark -\r\n#pragma mark EKEventEditViewDelegate\r\n\r\n- (void)eventEditViewController:(EKEventEditViewController *)controller\r\n          didCompleteWithAction:(EKEventEditViewAction)action {\r\n	FBFViewController *__weak weakSelf = self;\r\n	[self dismissViewControllerAnimated:YES completion: ^\r\n	{\r\n	    if (action != EKEventEditViewActionCanceled) {\r\n	        dispatch_async(dispatch_get_main_queue(), ^{\r\n	            weakSelf.eventsArray = [self getCalendarEventsForLast24Hours];\r\n			});\r\n		}\r\n	}];\r\n}\r\n\r\n- (EKCalendar *)eventEditViewControllerDefaultCalendarForNewEvents:(EKEventEditViewController *)controller {\r\n	return self.ekCalendar;\r\n}\r\n\r\n@end\r\n', '2014-03-02 01:31:00'),
(27, ' iOS', 'CameraX', ' // .h\r\n\r\n#import <UIKit/UIKit.h>\r\n\r\n@interface FBFViewController : UIViewController <UIImagePickerControllerDelegate, UINavigationControllerDelegate>\r\n@property (strong, nonatomic) IBOutlet UIImageView *imageView;\r\n\r\n- (IBAction)takePhoto:  (UIButton *)sender;\r\n- (IBAction)selectPhoto:(UIButton *)sender;\r\n@end\r\n\r\n\r\n// .m\r\n\r\n\r\n@interface FBFViewController ()\r\n\r\n@end\r\n\r\n@implementation FBFViewController\r\n\r\n- (void)viewDidLoad {\r\n	[super viewDidLoad];\r\n	// Do any additional setup after loading the view, typically from a nib.\r\n    \r\n	if (![UIImagePickerController isSourceTypeAvailable:UIImagePickerControllerSourceTypeCamera]) {\r\n		UIAlertView *myAlertView = [[UIAlertView alloc] initWithTitle:@"Error"\r\n		                                                      message:@"Device has no camera"\r\n		                                                     delegate:nil\r\n		                                            cancelButtonTitle:@"OK"\r\n		                                            otherButtonTitles:nil];\r\n        \r\n		[myAlertView show];\r\n	}\r\n}\r\n\r\n- (void)viewDidAppear:(BOOL)animated {\r\n	[super viewDidAppear:animated];\r\n}\r\n\r\n- (IBAction)takePhoto:(UIButton *)sender {\r\n	UIImagePickerController *picker = [[UIImagePickerController alloc] init];\r\n	picker.delegate = self;\r\n	picker.allowsEditing = YES;\r\n	picker.sourceType = UIImagePickerControllerSourceTypeCamera;\r\n    \r\n	[self presentViewController:picker animated:YES completion:NULL];\r\n}\r\n\r\n- (IBAction)selectPhoto:(UIButton *)sender {\r\n	UIImagePickerController *picker = [[UIImagePickerController alloc] init];\r\n	picker.delegate = self;\r\n	picker.allowsEditing = YES;\r\n	picker.sourceType = UIImagePickerControllerSourceTypePhotoLibrary;\r\n    \r\n	[self presentViewController:picker animated:YES completion:NULL];\r\n}\r\n\r\n#pragma mark - Image Picker Controller delegate methods\r\n\r\n- (void)imagePickerController:(UIImagePickerController *)picker didFinishPickingMediaWithInfo:(NSDictionary *)info {\r\n	UIImage *chosenImage = info[UIImagePickerControllerEditedImage];\r\n	self.imageView.image = chosenImage;\r\n    \r\n	[picker dismissViewControllerAnimated:YES completion:NULL];\r\n}\r\n\r\n- (void)imagePickerControllerDidCancel:(UIImagePickerController *)picker {\r\n	[picker dismissViewControllerAnimated:YES completion:NULL];\r\n}\r\n\r\n\r\n@end', '2014-03-02 01:32:52'),
(28, ' iOS', ' ContactsX', ' \r\n\r\n//FRAMEWORKS\r\nAddressBook\r\nAddressBookUI\r\n\r\n\r\n// VC.h\r\n\r\n#import <UIKit/UIKit.h>\r\n#import <AddressBook/AddressBook.h>\r\n#import <AddressBookUI/AddressBookUI.h>\r\n\r\n@interface FBFViewController : UIViewController <ABPeoplePickerNavigationControllerDelegate,ABPersonViewControllerDelegate,\r\nABNewPersonViewControllerDelegate, ABUnknownPersonViewControllerDelegate>\r\n{\r\n    \r\n}\r\n\r\n-(IBAction)createPerson:(id)sender;\r\n-(IBAction)readContacts:(id)sender;\r\n-(NSArray *)getAllContacts;\r\n@end\r\n\r\n\r\n// VC.m\r\n#import "Person.h"\r\n\r\n\r\n@interface FBFViewController (){\r\n    //read all objects\r\n    NSMutableArray *_peopleObjects;\r\n    ABAddressBookRef _addressBook;\r\n}\r\n\r\n\r\n@property (nonatomic, retain) IBOutlet UIButton *createContactButton;\r\n@property (nonatomic, retain) IBOutlet UIButton *readContactsButton;\r\n\r\n@end\r\n\r\n@implementation FBFViewController\r\n\r\n@synthesize   createContactButton;\r\n- (void)viewDidLoad {\r\n	[super viewDidLoad];\r\n	 \r\n}\r\n\r\n- (void)didReceiveMemoryWarning {\r\n	[super didReceiveMemoryWarning];\r\n	// Dispose of any resources that can be recreated.\r\n}\r\n\r\n- (IBAction)readContacts:(id)sender {\r\n    [self readAllContacts];\r\n    \r\n    // VIEW ALL CONTACTS IN THE LOG.\r\n    \r\n    for(id object in _peopleObjects){\r\n        ABRecordRef ref =  (__bridge ABRecordRef)(object);\r\n        NSString *compositeName  =(__bridge  NSString*) ABRecordCopyCompositeName(ref);\r\n        NSLog(@"compositeName: %@", compositeName);\r\n        \r\n        NSString *phoneNumber = @"";\r\n        ABMultiValueRef phoneNumbers = ABRecordCopyValue(ref, kABPersonPhoneProperty);\r\n        if(ABMultiValueGetCount(phoneNumbers)>0){\r\n            phoneNumber = (__bridge NSString *) ABMultiValueCopyValueAtIndex(phoneNumbers, 0);\r\n        }\r\n        NSLog(@"phoneNumber: %@", phoneNumber);\r\n        \r\n        NSString *email= @"";\r\n        ABMultiValueRef emails = ABRecordCopyValue(ref, kABPersonEmailProperty);\r\n        if(ABMultiValueGetCount(emails)>0){\r\n            email= (__bridge NSString *) ABMultiValueCopyValueAtIndex(emails, 0);\r\n        }\r\n        NSLog(@"email: %@", email);\r\n    }\r\n    \r\n    \r\n    \r\n}\r\n\r\n-(void)readAllContacts{\r\n    CFErrorRef error;\r\n    _addressBook = ABAddressBookCreateWithOptions(NULL, &error);\r\n    [self checkAddressBookAccess];\r\n    \r\n    _peopleObjects = (__bridge NSMutableArray *)ABAddressBookCopyArrayOfAllPeople(_addressBook);\r\n    \r\n    \r\n}\r\n#pragma mark -\r\n#pragma mark Address Book Access\r\n// Check the authorization status of our application for Address Book\r\n-(void)checkAddressBookAccess\r\n{\r\n    switch (ABAddressBookGetAuthorizationStatus())\r\n    {\r\n            // Update our UI if the user has granted access to their Contacts\r\n        case  kABAuthorizationStatusAuthorized:\r\n            _peopleObjects = (__bridge NSMutableArray *)ABAddressBookCopyArrayOfAllPeople(_addressBook);\r\n           // [self.tableView reloadData];\r\n            break;\r\n            // Prompt the user for access to Contacts if there is no definitive answer\r\n        case  kABAuthorizationStatusNotDetermined :\r\n            [self requestAddressBookAccess];\r\n            break;\r\n            // Display a message if the user has denied or restricted access to Contacts\r\n        case  kABAuthorizationStatusDenied:{\r\n            \r\n            \r\n            UIAlertView *alert = [[UIAlertView alloc] initWithTitle:@"Privacy Warning"\r\n                                                            message:@"Permission was not granted for Contacts. Please grant permission by going to \\nSettings->Privacy->Contacts and enabling Whozoo to access your contacts."\r\n                                                           delegate:nil\r\n                                                  cancelButtonTitle:@"Cancel"\r\n                    