h FragmentTransaction.TRANSIT_FRAGMENT_*.\r\n     Both methods allow you to define an entry animation and an existing animation.\r\n     */\r\n\r\n\r\n\r\n    /** Called when the activity is first created. */\r\n\r\n    @Override\r\n    public void onCreate(Bundle savedInstanceState) {\r\n        super.onCreate(savedInstanceState);\r\n        setContentView(R.layout.activity_main);\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n    }\r\n\r\n    public void startAnimation(View view) {\r\n        float dest = 0;\r\n        ImageView animatedImageView = (ImageView) findViewById(R.id.imageView1);\r\n        switch (view.getId()) {\r\n\r\n            case R.id.Button01:\r\n                dest = 360;\r\n                if (animatedImageView.getRotation() == 360) {\r\n                    System.out.println(animatedImageView.getAlpha());\r\n                    dest = 0;\r\n                }\r\n                ObjectAnimator animation1 = ObjectAnimator.ofFloat(animatedImageView,\r\n                        "rotation", dest);\r\n                animation1.setDuration(2000);\r\n                animation1.start();\r\n                // Show how to load an animation from XML\r\n                // Animation animation1 = AnimationUtils.loadAnimation(this,\r\n                // R.anim.myanimation);\r\n                // animation1.setAnimationListener(this);\r\n                // animatedView1.startAnimation(animation1);\r\n                break;\r\n\r\n            case R.id.Button02:\r\n                // shows how to define a animation via code\r\n                // also use an Interpolator (BounceInterpolator)\r\n                Paint paint = new Paint();\r\n                TextView animatedTextView = (TextView) findViewById(R.id.textView1);\r\n                float measureTextCenter = paint.measureText(animatedTextView.getText()\r\n                        .toString());\r\n                dest = 0 - measureTextCenter;\r\n                if (animatedTextView.getX() < 0) {\r\n                    dest = 0;\r\n                }\r\n                ObjectAnimator animation2 = ObjectAnimator.ofFloat(animatedTextView,\r\n                        "x", dest);\r\n                animation2.setDuration(2000);\r\n                animation2.start();\r\n                break;\r\n\r\n            case R.id.Button03:\r\n                // demonstrate fading and adding an AnimationListener\r\n\r\n                dest = 1;\r\n                if (animatedImageView.getAlpha() > 0) {\r\n                    dest = 0;\r\n                }\r\n                ObjectAnimator animation3 = ObjectAnimator.ofFloat(animatedImageView,\r\n                        "alpha", dest);\r\n                animation3.setDuration(2000);\r\n                animation3.start();\r\n                break;\r\n\r\n            case R.id.Button04:\r\n\r\n                ObjectAnimator fadeOut = ObjectAnimator.ofFloat(animatedImageView, "alpha",\r\n                        0f);\r\n                fadeOut.setDuration(2000);\r\n                ObjectAnimator mover = ObjectAnimator.ofFloat(animatedImageView,\r\n                        "translationX", -500f, 0f);\r\n                mover.setDuration(2000);\r\n                ObjectAnimator fadeIn = ObjectAnimator.ofFloat(animatedImageView, "alpha",\r\n                        0f, 1f);\r\n                fadeIn.setDuration(2000);\r\n                AnimatorSet animatorSet = new AnimatorSet();\r\n\r\n                animatorSet.play(mover).with(fadeIn).after(fadeOut);\r\n                animatorSet.start();\r\n                break;\r\n\r\n            default:\r\n                break;\r\n        }\r\n\r\n    }\r\n\r\n\r\n    @Override\r\n    public boolean onCreateOptionsMenu(Menu menu) {\r\n        getMenuInflater().inflate(R.menu.main, menu);\r\n        return super.onCreateOptionsMenu(menu);\r\n    }\r\n\r\n    @Override\r\n    public boolean onOptionsItemSelected(MenuItem item) {\r\n        Intent intent = new Intent(this, HitActivity.class);\r\n        startActivity(intent);\r\n        return true;\r\n    }\r\n\r\n}\r\n\r\n\r\n// JAVA hit activity\r\n\r\npublic class HitActivity extends Activity implements View.OnClickListener{\r\n\r\n    private ObjectAnimator animationXAxis;\r\n    private ObjectAnimator animationYAxis;\r\n    private Button button;\r\n    private Random random;\r\n    private int widthWindow;\r\n    private int heightWindow;\r\n    private AnimatorSet set;\r\n\r\n    private TextView mScore;\r\n    int mPoints = 0;\r\n\r\n    @Override\r\n    protected void onCreate(Bundle savedInstanceState) {\r\n        super.onCreate(savedInstanceState);\r\n        setContentView(R.layout.target);\r\n        widthWindow = getWindowManager().getDefaultDisplay().getWidth();\r\n        heightWindow = getWindowManager().getDefaultDisplay().getHeight();\r\n        random = new Random();\r\n\r\n        mScore = (TextView) findViewById(R.id.score);\r\n        mScore.setText(String.valueOf(mPoints));\r\n\r\n        set = createAnimation();\r\n        set.start();\r\n        set.addListener(new AnimatorListenerAdapter() {\r\n\r\n            @Override\r\n            public void onAnimationEnd(Animator animation) {\r\n                int nextX = random.nextInt(widthWindow);\r\n                int nextY = random.nextInt(heightWindow);\r\n                animationXAxis = ObjectAnimator.ofFloat(button, "x", button.getX(),\r\n                        nextX);\r\n                animationXAxis.setDuration(1400);\r\n                animationYAxis = ObjectAnimator.ofFloat(button, "y", button.getY(),\r\n                        nextY);\r\n                animationYAxis.setDuration(1400);\r\n                set.playTogether(animationXAxis, animationYAxis);\r\n                set.start();\r\n            }\r\n        });\r\n    }\r\n\r\n    public void onClick(View view) {\r\n\r\n        mPoints = mPoints +1;\r\n        mScore.setText(String.valueOf(mPoints));\r\n\r\n    }\r\n\r\n    private AnimatorSet createAnimation() {\r\n        int nextX = random.nextInt(widthWindow);\r\n        int nextY = random.nextInt(heightWindow);\r\n        button = (Button) findViewById(R.id.button1);\r\n        button.setOnClickListener(this);\r\n        animationXAxis = ObjectAnimator.ofFloat(button, "x", nextX);\r\n        animationXAxis.setDuration(1400);\r\n        animationYAxis = ObjectAnimator.ofFloat(button, "y", nextY);\r\n        animationYAxis.setDuration(1400);\r\n        AnimatorSet set = new AnimatorSet();\r\n        set.playTogether(animationXAxis, animationYAxis);\r\n        return set;\r\n    }\r\n}\r\n\r\n\r\n\r\n', '2014-03-03 19:59:30');
