lse if (eventType == XmlPullParser.END_DOCUMENT) {\r\n\r\n					} else if (eventType == XmlPullParser.START_TAG) {\r\n\r\n						mCurrentStartTag = xpp.getName();\r\n						if (mCurrentStartTag.equals("code")) {\r\n							onTableFields = true;\r\n							// reinstantiate a new model object\r\n							geoName = new GeoName();\r\n						}\r\n					} else if (eventType == XmlPullParser.END_TAG) {\r\n						mCurrentEndTag = xpp.getName();\r\n						if (mCurrentEndTag.equals("code")) {\r\n							onTableFields = false;\r\n						}\r\n					} else if (eventType == XmlPullParser.TEXT) {\r\n\r\n						if (mCurrentStartTag.equals("code")) {\r\n							onTableFields = true;\r\n						}\r\n\r\n						if (onTableFields) {\r\n							Log.d("DEBUG xpp.getText()", xpp.getText());\r\n\r\n							if (mCurrentStartTag.equals("postalcode")) {\r\n								geoName.setPostalCode(xpp.getText());\r\n							}\r\n							if (mCurrentStartTag.equals("adminCode3")) {\r\n								geoName.setAdminCode3(xpp.getText());\r\n							}\r\n							if (mCurrentStartTag.equals("adminCode2")) {\r\n								geoName.setAdminCode2(xpp.getText());\r\n							}\r\n							if (mCurrentStartTag.equals("adminCode1")) {\r\n								geoName.setAdminCode1(xpp.getText());\r\n							}\r\n							if (mCurrentStartTag.equals("adminName2")) {\r\n								geoName.setAdminName2(xpp.getText());\r\n							}\r\n							if (mCurrentStartTag.equals("adminName1")) {\r\n								geoName.setAdminName1(xpp.getText());\r\n							}\r\n							if (mCurrentStartTag.equals("lat")) {\r\n								geoName.setLat(xpp.getText());\r\n							}\r\n							if (mCurrentStartTag.equals("lng")) {\r\n								geoName.setLng(xpp.getText());\r\n							}\r\n							if (mCurrentStartTag.equals("countryCode")) {\r\n								geoName.setCountryCode(xpp.getText());\r\n							}\r\n							if (mCurrentStartTag.equals("name")) {\r\n								geoName.setPlaceName(xpp.getText());\r\n							}\r\n						}\r\n						if (mCurrentEndTag.equals("code")) {\r\n\r\n							mListOfGeoName.add(geoName);\r\n						}\r\n					}\r\n					try {\r\n						eventType = xpp.next();\r\n					}\r\n					catch (Exception e) {\r\n						Log.d("DEBUG", "throws an IOException");\r\n					}\r\n				}\r\n				//SUCCESS RETURN\r\n				mAsyncInterface.asyncDownloadSuccess(mListOfGeoName);\r\n\r\n			}\r\n			catch (XmlPullParserException e) {\r\n				e.printStackTrace();\r\n				//FAIL RETURN\r\n				mAsyncInterface.asyncDownloadFailure();\r\n			}\r\n\r\n		}\r\n\r\n	}\r\n\r\n}\r\n-- UniversalURL\r\n\r\npublic class UniversalURL {\r\n\r\n   // FOR XML\r\n    //private HashMap<String, String> hashMapOfQueryKeysAndValues;\r\n    //private String mQueryString;\r\n\r\n    /**  ---------------------------------------------------------------------------------------------------------------------\r\n     * XML\r\n     * @param baseURL\r\n     * @param hashMapQueryKeysAndValues\r\n\r\n    public UniversalURL(String baseURL, HashMap<String, String> hashMapQueryKeysAndValues) {\r\n        this.hashMapOfQueryKeysAndValues = hashMapQueryKeysAndValues;\r\n        String queryString = baseURL;\r\n        Iterator iterator = hashMapOfQueryKeysAndValues.keySet().iterator();\r\n        while (iterator.hasNext()) {\r\n            String key = (String) iterator.next();\r\n            String value = hashMapOfQueryKeysAndValues.get(key);\r\n            if(key.equals("username")){\r\n                queryString += "maxRows=10&" +key + "=" + value;\r\n            }else{\r\n                queryString += key + "=" + value + "&";\r\n            }\r\n\r\n        }\r\n        this.mQueryString = queryString;\r\n    }\r\n\r\n    public String getQueryString() {\r\n        return mQueryString;\r\n    }\r\n     */\r\n\r\n\r\n    /**  ---------------------------------------------------------------------------------------------------------------------\r\n     * JSON\r\n     * @param baseURL\r\n     * @param hashMapQueryKeysAndValues\r\n     * @return\r\n     */\r\n    public static JSONObject getJSONObjectFromUrl(String baseURL, HashMap<String, String> hashMapQueryKeysAndValues) {\r\n        String queryString = baseURL;\r\n        Iterator iterator = hashMapQueryKeysAndValues.keySet().iterator();\r\n        while (iterator.hasNext()) {\r\n            String key = (String) iterator.next();\r\n            String value = hashMapQueryKeysAndValues.get(key);\r\n            queryString += key + "=" + value + "&";\r\n        }\r\n        String response = "";\r\n        DefaultHttpClient client = new DefaultHttpClient();\r\n        HttpGet httpGet = new HttpGet(queryString);\r\n        try {\r\n            HttpResponse execute = client.execute(httpGet);\r\n            InputStream content = execute.getEntity().getContent();\r\n            BufferedReader buffer = new BufferedReader(new InputStreamReader(content));\r\n            String s;\r\n            while ((s = buffer.readLine()) != null) {\r\n                response += s;\r\n            }\r\n            return new JSONObject(response);\r\n        }\r\n        catch (Exception e) {\r\n            e.printStackTrace();\r\n            return null;\r\n        }\r\n\r\n    }\r\n\r\n\r\n}\r\n\r\n\r\n\r\n// JAVA activities\r\n\r\n-- mainActivity\r\n\r\npublic class MainActivity extends Activity implements View.OnClickListener, AsyncInterface {\r\n\r\n	private EditText mInputPostalCode;\r\n	private Button mBtnSubmit;\r\n	private AsyncJSONDownload mAsyncJSONDownload;\r\n	private AsyncXMLDownload mAsyncXMLDownload;\r\n	private Button mBtnJson;\r\n	private Button mBtnXml;\r\n    boolean json = false;\r\n\r\n	@Override\r\n	public void onCreate(Bundle savedInstanceState) {\r\n		super.onCreate(savedInstanceState);\r\n		setContentView(R.layout.activity_main);\r\n\r\n		mInputPostalCode = (EditText) findViewById(R.id.btnPostalCode);\r\n\r\n		mBtnSubmit = (Button) findViewById(R.id.btnSubmit);\r\n		mBtnSubmit.setOnClickListener(this);\r\n		mBtnSubmit.setEnabled(true);\r\n\r\n		mBtnJson = (Button) findViewById(R.id.btnJson);\r\n		mBtnJson.setOnClickListener(this);\r\n\r\n		mBtnXml = (Button) findViewById(R.id.btnXml);\r\n		mBtnXml.setOnClickListener(this);\r\n	}\r\n\r\n	@Override\r\n	public void onClick(View view) {\r\n\r\n		switch (view.getId()) {\r\n			case R.id.btnJson:\r\n				Log.d("DEBUG", "select: json");\r\n				json = true;\r\n				break;\r\n\r\n			case R.id.btnXml:\r\n				Log.d("DEBUG", "select: xml");\r\n				json = false;\r\n				break;\r\n\r\n			case R.id.btnSubmit:\r\n				if (!mInputPostalCode.getText().toString().equals("")) {\r\n					if (json) {\r\n						Log.d("DEBUG", "responseType:  J S O N !");\r\n						String baseURL = "http://api.geonames.org/postalCodeLookupJSON?";\r\n						// prevent empty search\r\n                        HashMap<String, String> hashMapQueryKeysAndValues = new HashMap<String, String>();\r\n                        hashMapQueryKeysAndValues.put("postalcode", mInputPostalCode.getText().toString());\r\n                        hashMapQueryKeysAndValues.put("username", "indatustest");\r\n\r\n                        mAsyncJSONDownload = new AsyncJSONDownload(this, MainActivity.this, hashMapQueryKeysAndValues);\r\n                        mAsyncJSONDownload.execute(baseURL);\r\n\r\n					} else {\r\n						Log.d("DEBUG", "responseType:  X M L !");\r\n						String baseURL = "http://api.geonames.org/postalCodeSearch?";\r\n						HashMap<String, String> hashMapQueryKeysAndValues = new HashMap<String, String>();\r\n						hashMapQueryKeysAndValues.put("postalcode", mInputPostalCode.getText().toString());\r\n						hashMapQueryKeysAndValues.put("username", "indatustest");\r\n                        mAsyncXMLDownload = new AsyncXMLDownload(this, MainActivity.this,hashMapQueryKeysAndValues);\r\n                        mAsyncXMLDownload.execute(baseURL);\r\n					}\r\n\r\n				}\r\n\r\n				break;\r\n		}\r\n\r\n	}\r\n\r\n	@Override\r\n	public void asyncDownloadSuccess(ArrayList<GeoName> arrayListOfGeoName) {\r\n		//SUCCESS\r\n		Intent intent = new Intent(MainActivity.this, ResultActivity.class);\r\n		ArrayList<GeoName> geoNameList = (ArrayList<GeoName>) arrayListOfGeoName;\r\n		intent.putParcelableArrayListExtra("data", geoNameList);\r\n		startActivity(intent);\r\n	}\r\n\r\n	@Override\r\n	public void asyncDownloadFailure() {\r\n		//FAIL\r\n	}\r\n\r\n}\r\n\r\n\r\n-- ResultActivity\r\n\r\npublic class ResultActivity extends Activity {\r\n\r\n	private ListView mListView;\r\n	private List<GeoName> mListOfGeoName;\r\n\r\n	@Override\r\n	protected void onCreate(Bundle savedInstanceState) {\r\n		super.onCreate(savedInstanceState);\r\n		setContentView(R.layout.activity_result);\r\n		mListOfGeoName = getIntent().getParcelableArrayListExtra(getString(R.string.data));\r\n        mListView = (ListView) findViewById(R.id.hashMap_list);\r\n        ArrayOfObjectsListAdapter arrayOfObjectsListAdapter = new ArrayOfObjectsListAdapter(ResultActivity.this, mListOfGeoName);\r\n        mListView.setAdapter(arrayOfObjectsListAdapter);\r\n	}\r\n\r\n}\r\n\r\n', '2014-03-03 20:08:07'),
(50, ' android', 'AudioX', ' \r\n\r\n// manifest\r\n <uses-permission android:name="android.permission.WRITE_EXTERNAL_STORAGE"/>\r\n    <uses-permission android:name="android.permission.RECORD_AUDIO" />\r\n\r\n// XML activity main\r\n    <?xml version="1.0" encoding="utf-8"?>\r\n    <LinearLayout xmlns:android="http://schemas.android.com/apk/res/android"\r\n        android:layout_width="match_parent"\r\n        android:layout_height="match_parent"\r\n        android:orientation="vertical" >\r\n\r\n        <Button\r\n            android:id="@+id/start"\r\n            android:layout_width="wrap_content"\r\n            android:layout_height="wrap_content"\r\n            android:text="Start Recording"\r\n            android:onClick="startRecording" />\r\n\r\n\r\n\r\n        <Button\r\n            android:id="@+id/stop"\r\n            android:layout_width="wrap_content"\r\n            android:layout_height="wrap_content"\r\n            android:text="Stop Recording"\r\n            android:enabled="false"\r\n            android:onClick="stopRecording"\r\n            />\r\n\r\n        <Button\r\n            android:layout_width="wrap_content"\r\n            android:layout_height="wrap_content"\r\n            android:text="Play Back"\r\n            android:id="@+id/playBack"\r\n            android:layout_gravity="center_vertical" />\r\n\r\n\r\n    </LinearLayout>\r\n\r\n  // JAVA main activity\r\n\r\n\r\npublic class MainActivity extends Activity implements View.OnClickListener {\r\n\r\n	MediaRecorder recorder;\r\n	File audiofile = null;\r\n	private static final String TAG = "SoundRecordingActivity";\r\n	private View startButton;\r\n	private View stopButton;\r\n	private Button playBack;\r\n	private Uri recordingUri;\r\n\r\n	@Override\r\n	public void onCreate(Bundle savedInstanceState) {\r\n		super.onCreate(savedInstanceState);\r\n		setContentView(R.layout.activity_main);\r\n		startButton = findViewById(R.id.start);\r\n		stopButton = findViewById(R.id.stop);\r\n		playBack = (Button) findViewById(R.id.playBack);\r\n		playBack.setOnClickListener(this);\r\n\r\n		// Set the hardware buttons to control the music\r\n		this.setVolumeControlStream(AudioManager.STREAM_MUSIC);\r\n\r\n	}\r\n\r\n	public void startRecording(View view) throws IOException {\r\n\r\n		startButton.setEnabled(false);\r\n		stopButton.setEnabled(true);\r\n\r\n		File sampleDir = Environment.getExternalStorageDirectory();\r\n		try {\r\n			audiofile = File.createTempFile("sound", ".3gp", sampleDir);\r\n		}\r\n		catch (IOException e) {\r\n			Log.e(TAG, "sdcard access error");\r\n			return;\r\n		}\r\n		recorder = new MediaRecorder();\r\n		recorder.setAudioSource(MediaRecorder.AudioSource.MIC);\r\n		recorder.setOutputFormat(MediaRecorder.OutputFormat.THREE_GPP);\r\n		recorder.setAudioEncoder(MediaRecorder.AudioEncoder.AMR_NB);\r\n		recorder.setOutputFile(audiofile.getAbsolutePath());\r\n		recorder.prepare();\r\n		recorder.start();\r\n	}\r\n\r\n	public void stopRecording(View view) {\r\n		startButton.setEnabled(true);\r\n		stopButton.setEnabled(false);\r\n		recorder.stop();\r\n		recorder.release();\r\n		addRecordingToMediaLibrary();\r\n	}\r\n\r\n	protected void addRecordingToMediaLibrary() {\r\n		ContentValues values = new ContentValues(4);\r\n		long current = System.currentTimeMillis();\r\n		values.put(MediaStore.Audio.Media.TITLE, "audio" + audiofile.getName());\r\n		values.put(MediaStore.Audio.Media.DATE_ADDED, (int) (current / 1000));\r\n		values.put(MediaStore.Audio.Media.MIME_TYPE, "audio/3gpp");\r\n		values.put(MediaStore.Audio.Media.DATA, audiofile.getAbsolutePath());\r\n		ContentResolver contentResolver = getContentResolver();\r\n\r\n		Uri base = MediaStore.Audio.Media.EXTERNAL_CONTENT_URI;\r\n		Uri newUri = contentResolver.insert(base, values);\r\n\r\n		sendBroadcast(new Intent(Intent.ACTION_MEDIA_SCANNER_SCAN_FILE, newUri));\r\n		Toast.makeText(this, "Added File " + newUri, Toast.LENGTH_LONG).show();\r\n		recordingUri = newUri;\r\n		Log.d("DEBUG: recording URI to string:", String.valueOf(newUri));\r\n		// URI looks like this-> //content://media/external/audio/media/949\r\n	}\r\n\r\n	@Override\r\n	public void onClick(View v) {\r\n		switch (v.getId()) {\r\n			case R.id.playBack:\r\n				PlayAudio();\r\n				break;\r\n		}\r\n	}\r\n\r\n	public void PlayAudio() {\r\n		MediaPlayer mPlayer = new MediaPlayer();\r\n		mPlayer.setAudioStreamType(AudioManager.STREAM_MUSIC);\r\n		//Set the data source as a content Uri:\r\n		try {\r\n			mPlayer.setDataSource(getApplicationContext(), recordingUri);\r\n		}\r\n		catch (IllegalArgumentException e) {\r\n			Toast.makeText(getApplicationContext(), "You might not set the URI correctly!", Toast.LENGTH_LONG).show();\r\n		}\r\n		catch (SecurityException e) {\r\n			Toast.makeText(getApplicationContext(), "You might not set the URI correctly!", Toast.LENGTH_LONG).show();\r\n		}\r\n		catch (IllegalStateException e) {\r\n			Toast.makeText(getApplicationContext(), "You might not set the URI correctly!", Toast.LENGTH_LONG).show();\r\n		}\r\n		catch (IOException e) {\r\n			e.printStackTrace();\r\n		}\r\n\r\n		//Prepare the player for playback, synchronously:\r\n		try {\r\n			mPlayer.prepare();\r\n		}\r\n		catch (IllegalStateException e) {\r\n			Toast.makeText(getApplicationContext(), "You might not set the URI correctly!", Toast.LENGTH_LONG).show();\r\n		}\r\n		catch (IOException e) {\r\n			Toast.makeText(getApplicationContext(), "You might not set the URI correctly!", Toast.LENGTH_LONG).show();\r\n		}\r\n		//Start the player:\r\n		mPlayer.start();\r\n\r\n	}\r\n}\r\n', '2014-03-03 20:10:44'),
(51, ' android', 'CalendarEventX', ' \r\n\r\n// JAVA main activity\r\n\r\n\r\npublic class MainActivity extends Activity {\r\n\r\n	@Override\r\n	protected void onCreate(Bundle savedInstanceState) {\r\n		super.onCreate(savedInstanceState);\r\n		setContentView(R.layout.activity_main);\r\n\r\n		//USER INSERTED EVENT via Default\r\n		// ACTION_INSERT does not work on all phones\r\n		// use  Intent.ACTION_EDIT in this case\r\n		/*\r\n		 * Intent intentUser = new Intent(Intent.ACTION_INSERT); intentUser.setData(CalendarContract.Events.CONTENT_URI); startActivity(intentUser);\r\n		 */\r\n		/* */\r\n		Intent intent = new Intent(Intent.ACTION_INSERT);\r\n		intent.setType("vnd.android.cursor.item/event");\r\n		intent.putExtra(CalendarContract.Events.TITLE, "Learn Android");\r\n		intent.putExtra(CalendarContract.Events.EVENT_LOCATION, "Home sweet home");\r\n		intent.putExtra(CalendarContract.Events.DESCRIPTION, "Download Examples");\r\n\r\n		// Setting dates\r\n		GregorianCalendar calDate = new GregorianCalendar(2014,1,28); // months are 0-11\r\n       // GregorianCalendar calxDate = new GregorianCalendar(2014,2,28);\r\n		intent.putExtra(CalendarContract.EXTRA_EVENT_BEGIN_TIME, calDate.getTimeInMillis());\r\n		intent.putExtra(CalendarContract.EXTRA_EVENT_END_TIME, calDate.getTimeInMillis());\r\n\r\n		// make it a full day event\r\n		intent.putExtra(CalendarContract.EXTRA_EVENT_ALL_DAY, true);\r\n\r\n		// make it a recurring Event\r\n		//intent.putExtra(CalendarContract.Events.RRULE, "FREQ=WEEKLY;COUNT=11;WKST=SU;BYDAY=TU,TH");\r\n\r\n		// Making it private and shown as busy\r\n		intent.putExtra(CalendarContract.Events.ACCESS_LEVEL, CalendarContract.Events.ACCESS_PRIVATE);\r\n		intent.putExtra(CalendarContract.Events.AVAILABILITY, CalendarContract.Events.AVAILABILITY_BUSY);\r\n\r\n		startActivity(intent);\r\n	}\r\n\r\n}\r\n\r\n', '2014-03-03 20:14:31'),
(52, ' android', 'CalendarX(datepicker)', ' // ( date picker )\r\n\r\n\r\n// GRADLE\r\n\r\ndependencies {\r\n    compile -----com.android.support:support-v4:19.0.1---- replace with single apostrophes\r\n}\r\n\r\n\r\n\r\n// XML\r\n\r\n<LinearLayout xmlns:android="http://schemas.android.com/apk/res/android"\r\n    android:orientation="vertical"\r\n    android:layout_width="fill_parent"\r\n    android:layout_height="fill_parent"\r\n    android:layout_gravity="center_horizontal|top">\r\n    <EditText\r\n        android:layout_width="fill_parent"\r\n        android:layout_height="wrap_content"\r\n        android:inputType="date"\r\n        android:ems="10"\r\n        android:id="@+id/add_work_inputDate"\r\n        android:layout_gravity="center_horizontal"\r\n        android:hint="Date" />\r\n    <Button\r\n        android:layout_width="match_parent"\r\n        android:layout_height="wrap_content"\r\n        android:text="OK"\r\n        android:id="@+id/add_work_btnCreate" />\r\n\r\n\r\n</LinearLayout>\r\n\r\n\r\n// JAVA\r\n\r\nimport android.app.Activity;\r\nimport android.app.AlertDialog;\r\nimport android.app.DatePickerDialog;\r\nimport android.app.Dialog;\r\nimport android.app.DialogFragment;\r\nimport android.content.DialogInterface;\r\nimport android.os.Bundle;\r\nimport android.util.Log;\r\nimport android.view.MotionEvent;\r\nimport android.view.View;\r\nimport android.widget.Button;\r\nimport android.widget.DatePicker;\r\nimport android.widget.EditText;\r\n\r\nimport java.util.Calendar;\r\n\r\npublic class MainActivity extends Activity implements View.OnClickListener, View.OnTouchListener  {\r\n\r\n    private Button btnCreate;\r\n    private EditText inputDate;\r\n    private boolean mFired = false;\r\n\r\n    /**\r\n     * This method sets up the UI, including a "Date Dialog".\r\n     *\r\n     * @param savedInstanceState\r\n     */\r\n    @Override\r\n    protected void onCreate(Bundle savedInstanceState) {\r\n        super.onCreate(savedInstanceState);\r\n        setContentView(R.layout.activity_main);\r\n        btnCreate = (Button) findViewById(R.id.add_work_btnCreate);\r\n        btnCreate.setOnClickListener(this);\r\n        inputDate = (EditText) findViewById(R.id.add_work_inputDate);\r\n        inputDate.setFocusable(false);\r\n        inputDate.setClickable(true);\r\n        inputDate.setOnTouchListener(this);\r\n    }\r\n\r\n    /**\r\n     * This method is the OnClickListener delegate method that receives the user input, parses the data accordingly, and if requirements are met, creates a new\r\n     * Hours record. Otherwise it prompts the user to correct their errors.\r\n     *\r\n     * @param v\r\n     */\r\n    @Override\r\n    public void onClick(View v) {\r\n        String str_date = String.valueOf(inputDate.getText());\r\n            //Screen Input Data\r\n            if (!str_date.equals("")) {\r\n                // DO SOMETHING WITH THE DATE FROM THE TEXT FIELD\r\n                Log.d("DEBUG",str_date);\r\n            } else {\r\n                alertUserToFixDataEntered();\r\n            }\r\n    }\r\n\r\n    /**\r\n     * This method creates and displays the inputDate dialog.\r\n     */\r\n    public void alertUserToFixDataEntered() {\r\n        AlertDialog.Builder alertDialog = new AlertDialog.Builder(MainActivity.this);\r\n        alertDialog.setTitle("Alert");\r\n        alertDialog.setMessage("Make sure the data you entered is correct.Thank you.");\r\n        alertDialog.setNegativeButton("Cancel", new DialogInterface.OnClickListener() {\r\n            public void onClick(DialogInterface dialog, int which) {\r\n                //Now users are aware that they need to re-enter a piece of data.\r\n            }\r\n        });\r\n        alertDialog.show();\r\n    }\r\n\r\n    /**\r\n     * This method creates a date picker when the input text for date is clicked.\r\n     *\r\n     * @param v\r\n     * @param event\r\n     * @return\r\n     */\r\n    @Override\r\n    public boolean onTouch(View v, MotionEvent event) {\r\n        showDatePickerDialog(v);\r\n        return false;\r\n    }\r\n\r\n    /**\r\n     * NEW DATE PICKER\r\n     */\r\n\r\n    public void showDatePickerDialog(View v) {\r\n        if (mFired) {\r\n            Log.d("DEBUG", "Double showDatePickerDialog dismiss duplicate fire.(per android Issue 64895: Issue 34860 )");\r\n        } else {\r\n            mFired = true;\r\n            DialogFragment newFragment = new DatePickerFragment();\r\n            newFragment.show(getFragmentManager(), "datePicker");\r\n        }\r\n    }\r\n\r\n    public class DatePickerFragment extends DialogFragment implements DatePickerDialog.OnDateSetListener {\r\n\r\n        @Override\r\n        public Dialog onCreateDialog(Bundle savedInstanceState) {\r\n            final Calendar c = Calendar.getInstance();\r\n            int year = c.get(Calendar.YEAR);\r\n            int month = c.get(Calendar.MONTH);\r\n            int day = c.get(Calendar.DAY_OF_MONTH);\r\n            return new DatePickerDialog(getActivity(), this, year, month, day);\r\n\r\n        }\r\n\r\n        public void onDateSet(DatePicker view, int year, int month, int day) {\r\n            String time = Integer.toString(month + 1) + " / " + Integer.toString(day) + " / " + Integer.toString(year);\r\n            inputDate.setText(time);\r\n\r\n            mFired = false;\r\n        }\r\n    }\r\n\r\n}\r\n', '2014-03-03 20:18:50');
