(53, ' android', 'CameraX', ' This app starts based on info here, using existing camera app.\r\nhttp://developer.android.com/guide/topics/media/camera.html#intents\r\n\r\nManifest:\r\n<!--\r\nQuick Camera Manifest Requirements:\r\n\r\n <uses-permission android:name="android.permission.CAMERA" />\r\n    <uses-feature android:name="android.hardware.camera" />\r\n    <uses-feature android:name="android.hardware.camera" android:required="false" />\r\n    <uses-permission android:name="android.permission.WRITE_EXTERNAL_STORAGE" />\r\n    <uses-permission android:name="android.permission.RECORD_AUDIO" />\r\n    <uses-permission android:name="android.permission.ACCESS_FINE_LOCATION" />\r\n-->\r\n\r\n\r\n//JAVA CLASS\r\npackage com.mocura.camerax;\r\n\r\nimport android.app.Activity;\r\nimport android.content.Intent;\r\nimport android.graphics.Bitmap;\r\nimport android.os.Bundle;\r\nimport android.view.View;\r\nimport android.widget.Button;\r\nimport android.widget.ImageView;\r\n\r\n/**\r\n * Created by BForte on 1/30/14.\r\n */\r\npublic class MyCameraActivity extends Activity {\r\n    private static final int CAMERA_REQUEST = 1888;\r\n    private ImageView imageView;\r\n\r\n    @Override\r\n    public void onCreate(Bundle savedInstanceState) {\r\n        super.onCreate(savedInstanceState);\r\n        setContentView(R.layout.main);\r\n        this.imageView = (ImageView)this.findViewById(R.id.imageView1);\r\n        Button photoButton = (Button) this.findViewById(R.id.button1);\r\n\r\n        photoButton.setOnClickListener(new View.OnClickListener() {\r\n\r\n            @Override\r\n            public void onClick(View v) {\r\n                Intent cameraIntent = new Intent(android.provider.MediaStore.ACTION_IMAGE_CAPTURE);\r\n                startActivityForResult(cameraIntent, CAMERA_REQUEST);\r\n            }\r\n        });\r\n    }\r\n\r\n    protected void onActivityResult(int requestCode, int resultCode, Intent data) {\r\n        if (requestCode == CAMERA_REQUEST && resultCode == RESULT_OK) {\r\n            Bitmap photo = (Bitmap) data.getExtras().get("data");\r\n            imageView.setImageBitmap(photo);\r\n        }\r\n    }\r\n}\r\n\r\n\r\n// XML\r\n<?xml version="1.0" encoding="utf-8"?>\r\n<LinearLayout xmlns:android="http://schemas.android.com/apk/res/android"\r\n    android:orientation="vertical"\r\n    android:layout_width="fill_parent"\r\n    android:layout_height="fill_parent"\r\n    >\r\n    <Button android:id="@+id/button1" android:layout_width="wrap_content" android:layout_height="wrap_content" android:text="photo"></Button>\r\n    <ImageView android:id="@+id/imageView1" android:layout_height="wrap_content" android:src="@drawable/ic_launcher" android:layout_width="wrap_content"></ImageView>\r\n\r\n</LinearLayout>', '2014-03-03 20:19:47'),
(54, ' android', 'ContactsX', ' \r\n\r\n\r\n\r\n// manifest\r\n<uses-permission android:name="android.permission.READ_CONTACTS" />\r\n<uses-permission android:name="android.permission.WRITE_CONTACTS" />\r\n\r\n\r\n//JAVA\r\n    public void addAContact() {\r\n\r\n    		String DisplayName = "Zeus";\r\n    		String MobileNumber = "333-333-3333";\r\n    		String HomeNumber = "502-234-4563";\r\n    		String WorkNumber = "2222";\r\n    		String emailID = "email@nomail.com";\r\n    		String company = "bad";\r\n    		String jobTitle = "abcd";\r\n\r\n    		ArrayList<ContentProviderOperation> ops = new ArrayList<ContentProviderOperation>();\r\n\r\n    		ops.add(ContentProviderOperation.newInsert(ContactsContract.RawContacts.CONTENT_URI).withValue(ContactsContract.RawContacts.ACCOUNT_TYPE, null)\r\n    				.withValue(ContactsContract.RawContacts.ACCOUNT_NAME, null).build());\r\n\r\n    		//------------------------------------------------------ Names\r\n    		if (DisplayName != null) {\r\n    			ops.add(ContentProviderOperation.newInsert(ContactsContract.Data.CONTENT_URI).withValueBackReference(ContactsContract.Data.RAW_CONTACT_ID, 0)\r\n    					.withValue(ContactsContract.Data.MIMETYPE, ContactsContract.CommonDataKinds.StructuredName.CONTENT_ITEM_TYPE)\r\n    					.withValue(ContactsContract.CommonDataKinds.StructuredName.DISPLAY_NAME, DisplayName).build());\r\n    		}\r\n\r\n    		//------------------------------------------------------ Mobile Number\r\n    		if (MobileNumber != null) {\r\n    			ops.add(ContentProviderOperation.newInsert(ContactsContract.Data.CONTENT_URI).withValueBackReference(ContactsContract.Data.RAW_CONTACT_ID, 0)\r\n    					.withValue(ContactsContract.Data.MIMETYPE, ContactsContract.CommonDataKinds.Phone.CONTENT_ITEM_TYPE)\r\n    					.withValue(ContactsContract.CommonDataKinds.Phone.NUMBER, MobileNumber)\r\n    					.withValue(ContactsContract.CommonDataKinds.Phone.TYPE, ContactsContract.CommonDataKinds.Phone.TYPE_MOBILE).build());\r\n    		}\r\n\r\n    		//------------------------------------------------------ Home Numbers\r\n    		if (HomeNumber != null) {\r\n    			ops.add(ContentProviderOperation.newInsert(ContactsContract.Data.CONTENT_URI).withValueBackReference(ContactsContract.Data.RAW_CONTACT_ID, 0)\r\n    					.withValue(ContactsContract.Data.MIMETYPE, ContactsContract.CommonDataKinds.Phone.CONTENT_ITEM_TYPE)\r\n    					.withValue(ContactsContract.CommonDataKinds.Phone.NUMBER, HomeNumber)\r\n    					.withValue(ContactsContract.CommonDataKinds.Phone.TYPE, ContactsContract.CommonDataKinds.Phone.TYPE_HOME).build());\r\n    		}\r\n\r\n    		//------------------------------------------------------ Work Numbers\r\n    		if (WorkNumber != null) {\r\n    			ops.add(ContentProviderOperation.newInsert(ContactsContract.Data.CONTENT_URI).withValueBackReference(ContactsContract.Data.RAW_CONTACT_ID, 0)\r\n    					.withValue(ContactsContract.Data.MIMETYPE, ContactsContract.CommonDataKinds.Phone.CONTENT_ITEM_TYPE)\r\n    					.withValue(ContactsContract.CommonDataKinds.Phone.NUMBER, WorkNumber)\r\n    					.withValue(ContactsContract.CommonDataKinds.Phone.TYPE, ContactsContract.CommonDataKinds.Phone.TYPE_WORK).build());\r\n    		}\r\n\r\n    		//------------------------------------------------------ Email\r\n    		if (emailID != null) {\r\n    			ops.add(ContentProviderOperation.newInsert(ContactsContract.Data.CONTENT_URI).withValueBackReference(ContactsContract.Data.RAW_CONTACT_ID, 0)\r\n    					.withValue(ContactsContract.Data.MIMETYPE, ContactsContract.CommonDataKinds.Email.CONTENT_ITEM_TYPE)\r\n    					.withValue(ContactsContract.CommonDataKinds.Email.DATA, emailID)\r\n    					.withValue(ContactsContract.CommonDataKinds.Email.TYPE, ContactsContract.CommonDataKinds.Email.TYPE_WORK).build());\r\n    		}\r\n\r\n    		//------------------------------------------------------ Organization\r\n    		if (!company.equals("") && !jobTitle.equals("")) {\r\n    			ops.add(ContentProviderOperation.newInsert(ContactsContract.Data.CONTENT_URI).withValueBackReference(ContactsContract.Data.RAW_CONTACT_ID, 0)\r\n    					.withValue(ContactsContract.Data.MIMETYPE, ContactsContract.CommonDataKinds.Organization.CONTENT_ITEM_TYPE)\r\n    					.withValue(ContactsContract.CommonDataKinds.Organization.COMPANY, company)\r\n    					.withValue(ContactsContract.CommonDataKinds.Organization.TYPE, ContactsContract.CommonDataKinds.Organization.TYPE_WORK)\r\n    					.withValue(ContactsContract.CommonDataKinds.Organization.TITLE, jobTitle)\r\n    					.withValue(ContactsContract.CommonDataKinds.Organization.TYPE, ContactsContract.CommonDataKinds.Organization.TYPE_WORK).build());\r\n    		}\r\n\r\n    		// Asking the Contact provider to create a new contact\r\n    		try {\r\n    			getContentResolver().applyBatch(ContactsContract.AUTHORITY, ops);\r\n    			Toast.makeText(MainActivity.this, "SUCCESSFULLY ADDED CONTACT  name XYZ ? ", Toast.LENGTH_SHORT).show();\r\n    		}\r\n    		catch (Exception e) {\r\n    			e.printStackTrace();\r\n    			Toast.makeText(MainActivity.this, "Exception: " + e.getMessage(), Toast.LENGTH_SHORT).show();\r\n    		}\r\n    	}\r\n\r\n\r\n    	public void readAllContacts(){\r\n                Cursor cursor = getContentResolver().query(ContactsContract.Contacts.CONTENT_URI,null, null, null, null);\r\n\r\n                while (cursor.moveToNext()) {\r\n                    String contactId = cursor.getString(cursor.getColumnIndex(ContactsContract.Contacts._ID));\r\n                    String name      = cursor.getString(cursor.getColumnIndex(ContactsContract.Contacts.DISPLAY_NAME));\r\n\r\n                    System.out.println( contactId.toString() + "   " + name);\r\n                }\r\n                cursor.close();\r\n            }\r\n', '2014-03-03 20:20:23'),
(55, ' android', 'DataX', ' // ACTIVITY\r\npublic class MainActivity extends ListActivity {\r\n    public CommentsSQLiteHelperDeluxe mCommentsSQLiteHelperDeluxe;\r\n    @Override\r\n    public void onCreate(Bundle savedInstanceState) {\r\n        super.onCreate(savedInstanceState);\r\n        setContentView(R.layout.activity_main);\r\n\r\n        mCommentsSQLiteHelperDeluxe = new CommentsSQLiteHelperDeluxe(this);\r\n        mCommentsSQLiteHelperDeluxe.open();\r\n\r\n        List<Comment> values = mCommentsSQLiteHelperDeluxe.getAllComments();\r\n\r\n        // use the SimpleCursorAdapter to show the elements in a ListView\r\n        ArrayAdapter<Comment> adapter = new ArrayAdapter<Comment>(this,\r\n                android.R.layout.simple_list_item_1, values);\r\n        setListAdapter(adapter);\r\n    }\r\n\r\n\r\n    public void onClick(View view) {\r\n        @SuppressWarnings("unchecked")\r\n        ArrayAdapter<Comment> adapter = (ArrayAdapter<Comment>) getListAdapter();\r\n        Comment comment = null;\r\n        switch (view.getId()) {\r\n            case R.id.add:\r\n                String[] comments = new String[] { "Cool", "Very nice", "Hate it" };\r\n                int nextInt = new Random().nextInt(3);\r\n                // save the new comment to the database\r\n                comment = mCommentsSQLiteHelperDeluxe.createComment(comments[nextInt]);\r\n                adapter.add(comment);\r\n                break;\r\n            case R.id.delete:\r\n                if (getListAdapter().getCount() > 0) {\r\n                    comment = (Comment) getListAdapter().getItem(0);\r\n                    mCommentsSQLiteHelperDeluxe.deleteComment(comment);\r\n                    adapter.remove(comment);\r\n                }\r\n                break;\r\n        }\r\n        adapter.notifyDataSetChanged();\r\n    }\r\n\r\n    @Override\r\n    protected void onResume() {\r\n        mCommentsSQLiteHelperDeluxe.open();\r\n        super.onResume();\r\n    }\r\n\r\n    @Override\r\n    protected void onPause() {\r\n        mCommentsSQLiteHelperDeluxe.close();\r\n        super.onPause();\r\n    }\r\n\r\n}\r\n\r\n\r\n// MODEL\r\npublic class Comment {\r\n    private long mId;\r\n    private String mComment;\r\n\r\n    public long getId() {\r\n        return mId;\r\n    }\r\n\r\n    public void setId(long id) {\r\n        this.mId = id;\r\n    }\r\n\r\n    public String getComment() {\r\n        return mComment;\r\n    }\r\n\r\n    public void setComment(String comment) {\r\n        this.mComment = comment;\r\n    }\r\n\r\n    // Will be used by the ArrayAdapter in the ListView\r\n    @Override\r\n    public String toString() {\r\n        return mComment;\r\n    }\r\n}\r\n\r\n// SQLiteHelper\r\n\r\n\r\npublic class CommentsSQLiteHelperDeluxe extends SQLiteOpenHelper {\r\n\r\n    public static final String TABLE_COMMENTS = "comments";\r\n    public static final String COLUMN_ID = "_id";\r\n    public static final String COLUMN_COMMENT = "comment";\r\n\r\n    private static final String DATABASE_NAME = "commments.db";\r\n    private static final int DATABASE_VERSION = 1;\r\n\r\n    // Database creation sql statement\r\n    private static final String DATABASE_CREATE = "create table "\r\n            + TABLE_COMMENTS + "(" + COLUMN_ID\r\n            + " integer primary key autoincrement, " + COLUMN_COMMENT\r\n            + " text not null);";\r\n\r\n\r\n   private SQLiteDatabase mSQLiteDatabase;\r\n\r\n    public CommentsSQLiteHelperDeluxe(Context context) {\r\n        super(context, DATABASE_NAME, null, DATABASE_VERSION);\r\n\r\n    }\r\n\r\n    @Override\r\n    public void onCreate(SQLiteDatabase database) {\r\n        database.execSQL(DATABASE_CREATE);\r\n    }\r\n\r\n    @Override\r\n    public void onUpgrade(SQLiteDatabase db, int oldVersion, int newVersion) {\r\n        Log.w("CommentsSQLiteHelperDeluxe",\r\n                "Upgrading database from version " + oldVersion + " to "\r\n                        + newVersion + ", which will destroy all old data");\r\n        db.execSQL("DROP TABLE IF EXISTS " + TABLE_COMMENTS);\r\n        onCreate(db);\r\n    }\r\n\r\n\r\n\r\n    private String[] mAllColumns = {  COLUMN_ID,\r\n             COLUMN_COMMENT };\r\n\r\n    public void open() throws SQLException {\r\n       mSQLiteDatabase = getWritableDatabase();\r\n    }\r\n\r\n    public void close() {\r\n        close();\r\n    }\r\n\r\n\r\n\r\n    public Comment createComment(String comment) {\r\n        ContentValues values = new ContentValues();\r\n        values.put(COLUMN_COMMENT, comment);\r\n        long insertId = mSQLiteDatabase.insert(TABLE_COMMENTS, null,\r\n                values);\r\n        Cursor cursor = mSQLiteDatabase.query(TABLE_COMMENTS,\r\n                mAllColumns, COLUMN_ID + " = " + insertId, null,\r\n                null, null, null);\r\n        cursor.moveToFirst();\r\n        Comment newComment = cursorToComment(cursor);\r\n        cursor.close();\r\n        return newComment;\r\n    }\r\n\r\n    public void deleteComment(Comment comment) {\r\n        long id = comment.getId();\r\n        System.out.println("Comment deleted with id: " + id);\r\n        mSQLiteDatabase.delete(TABLE_COMMENTS, COLUMN_ID\r\n                + " = " + id, null);\r\n    }\r\n\r\n    public List<Comment> getAllComments() {\r\n        List<Comment> comments = new ArrayList<Comment>();\r\n\r\n        Cursor cursor = mSQLiteDatabase.query(TABLE_COMMENTS,\r\n                mAllColumns, null, null, null, null, null);\r\n\r\n        cursor.moveToFirst();\r\n        while (!cursor.isAfterLast()) {\r\n            Comment comment = cursorToComment(cursor);\r\n            comments.add(comment);\r\n            cursor.moveToNext();\r\n        }\r\n        // make sure to close the cursor\r\n        cursor.close();\r\n        return comments;\r\n    }\r\n\r\n    private Comment cursorToComment(Cursor cursor) {\r\n        Comment comment = new Comment();\r\n        comment.setId(cursor.getLong(0));\r\n        comment.setComment(cursor.getString(1));\r\n        return comment;\r\n    }\r\n\r\n}\r\n\r\n\r\n// XML\r\n<?xml version="1.0" encoding="utf-8"?>\r\n<LinearLayout xmlns:android="http://schemas.android.com/apk/res/android"\r\n    android:layout_width="match_parent"\r\n    android:layout_height="match_parent"\r\n    android:orientation="vertical" >\r\n\r\n    <LinearLayout\r\n        android:id="@+id/group"\r\n        android:layout_width="wrap_content"\r\n        android:layout_height="wrap_content" >\r\n\r\n        <Button\r\n            android:id="@+id/add"\r\n            android:layout_width="wrap_content"\r\n            android:layout_height="wrap_content"\r\n            android:text="Add New"\r\n            android:onClick="onClick"/>\r\n\r\n        <Button\r\n            android:id="@+id/delete"\r\n            android:layout_width="wrap_content"\r\n            android:layout_height="wrap_content"\r\n            android:text="Delete First"\r\n            android:onClick="onClick"/>\r\n\r\n    </LinearLayout>\r\n\r\n    <ListView\r\n        android:id="@android:id/list"\r\n        android:layout_width="match_parent"\r\n        android:layout_height="wrap_content"\r\n        android:text="string/hello" />\r\n\r\n</LinearLayout>\r\n', '2014-03-03 20:21:20'),
(56, ' android', 'EmailX', ' //java\r\npackage com.example.app;\r\n\r\nimport android.app.Activity;\r\nimport android.content.Intent;\r\nimport android.net.Uri;\r\nimport android.os.Bundle;\r\nimport android.os.Environment;\r\nimport android.view.View;\r\nimport android.widget.Button;\r\n\r\nimport java.io.File;\r\n\r\npublic class MainActivity extends Activity implements View.OnClickListener{\r\n\r\n    Button btnSend;\r\n\r\n    @Override\r\n    protected void onCreate(Bundle savedInstanceState) {\r\n        super.onCreate(savedInstanceState);\r\n        setContentView(R.layout.activity_main);\r\n\r\n        btnSend  = (Button) findViewById(R.id.btnSend);\r\n        btnSend.setOnClickListener(this);\r\n\r\n    }\r\n\r\n    public void emailWithAnAttachment(){\r\n        Intent emailWithAttachmentsIntent = new Intent(Intent.ACTION_SEND);\r\n        emailWithAttachmentsIntent.setType("text/plain");\r\n        emailWithAttachmentsIntent.putExtra(Intent.EXTRA_EMAIL, new String[] {"email@example.com"});\r\n        emailWithAttachmentsIntent.putExtra(Intent.EXTRA_SUBJECT, "subject here");\r\n        emailWithAttachmentsIntent.putExtra(Intent.EXTRA_TEXT, "body text");\r\n        File root = Environment.getExternalStorageDirectory();\r\n        String pathToMyAttachedFile="temp/attachement.xml";\r\n        File file = new File(root, pathToMyAttachedFile);\r\n        if (!file.exists() || !file.canRead()) {\r\n            return;\r\n        }\r\n        Uri uri = Uri.fromFile(file);\r\n        emailWithAttachmentsIntent.putExtra(Intent.EXTRA_STREAM, uri);\r\n        startActivity(Intent.createChooser(emailWithAttachmentsIntent, "Pick an Email provider"));\r\n    }\r\n\r\n    public void sendBasicEmail(){\r\n        Intent intentEmail = new Intent(Intent.ACTION_SEND);\r\n        intentEmail.putExtra(Intent.EXTRA_EMAIL, new String[]{"bforte@indatus.com"});\r\n        intentEmail.putExtra(Intent.EXTRA_SUBJECT, "Android Email");\r\n        intentEmail.putExtra(Intent.EXTRA_TEXT, "sendBasicEmail");\r\n        intentEmail.setType("message/rfc822");\r\n        startActivity(Intent.createChooser(intentEmail, "Choose an email provider :"));\r\n    }\r\n\r\n    @Override\r\n    public void onClick(View v) {\r\n        sendBasicEmail();\r\n    }\r\n}\r\n\r\n\r\n// xml\r\n\r\n<LinearLayout\r\n    android:orientation="vertical"\r\n    android:layout_width="fill_parent"\r\n    android:layout_height="fill_parent"\r\n    xmlns:android="http://schemas.android.com/apk/res/android">\r\n\r\n    <Button\r\n        android:layout_width="wrap_content"\r\n        android:layout_height="wrap_content"\r\n        android:text="Send Email"\r\n        android:id="@+id/btnSend"\r\n        android:layout_gravity="center_horizontal" />\r\n</LinearLayout>', '2014-03-03 20:22:03'),
(57, ' android', 'FileX', ' //Quick File Write and Read\r\n// !! REPLACE --- with single apostrophes\r\n\r\n//manifest\r\n <uses-permission android:name="android.permission.WRITE_EXTERNAL_STORAGE" />\r\n\r\n\r\n //java\r\n public class MainActivity extends Activity {\r\n\r\n     private File root = new File(Environment.getExternalStorageDirectory(), "Notes");\r\n     private String mAbsoluteStoragePath;\r\n     private String mExternalStorageStatus;\r\n     private  TextView tv;\r\n\r\n     @Override\r\n     protected void onCreate(Bundle savedInstanceState) {\r\n         super.onCreate(savedInstanceState);\r\n         setContentView(R.layout.activity_main);\r\n\r\n         tv = (TextView)findViewById(R.id.text_view);\r\n\r\n         // GET ABSOLUTE PATH IF NECESSARY\r\n         mAbsoluteStoragePath =  Environment.getExternalStorageDirectory().getAbsolutePath();\r\n         Log.d("DEBUG mAbsoluteStoragePath:",mAbsoluteStoragePath);\r\n\r\n         //CHECK IF EXTERNAL STORAGE IS AVAILABLE\r\n         mExternalStorageStatus = Environment.getExternalStorageState();\r\n         if(mExternalStorageStatus.equals("mounted")){\r\n             generateNoteOnSD("foofile.txt","Hey there worldlings....");\r\n         }\r\n\r\n         readATextFile();\r\n     }\r\n\r\n     public void generateNoteOnSD(String sFileName, String sBody){\r\n\r\n         try\r\n         {\r\n            root = new File(Environment.getExternalStorageDirectory(), "Notes");\r\n             if (!root.exists()) {\r\n                 root.mkdirs();\r\n             }\r\n             File gpxfile = new File(root, sFileName);\r\n             FileWriter writer = new FileWriter(gpxfile);\r\n             writer.append(sBody);\r\n             writer.flush();\r\n             writer.close();\r\n             Toast.makeText(this, "Saved", Toast.LENGTH_SHORT).show();\r\n         }\r\n         catch(IOException e)\r\n         {\r\n             e.printStackTrace();\r\n         }\r\n     }\r\n\r\n\r\n     public void readATextFile(){\r\n\r\n          File file = new File(root,"foofile.txt");\r\n          StringBuilder text = new StringBuilder();\r\n         try {\r\n             BufferedReader br = new BufferedReader(new FileReader(file));\r\n             String line;\r\n\r\n             while ((line = br.readLine()) != null) {\r\n                 text.append(line);\r\n                 // HEADS UP !! replace --- with single apostrophe text.append(---\r\n---);\r\n             }\r\n         }\r\n         catch (IOException e) {\r\n             //You---ll need to add proper error handling here\r\n         }\r\n          tv.setText(text);\r\n     }\r\n\r\n }\r\n\r\n\r\n//XML\r\n<FrameLayout xmlns:android="http://schemas.android.com/apk/res/android"\r\n    xmlns:tools="http://schemas.android.com/tools"\r\n    android:id="@+id/container"\r\n    android:layout_width="match_parent"\r\n    android:layout_height="match_parent"\r\n    tools:context="com.example.app.MainActivity"\r\n    tools:ignore="MergeRootFrame" >\r\n\r\n    <LinearLayout\r\n        android:orientation="vertical"\r\n        android:layout_width="fill_parent"\r\n        android:layout_height="fill_parent"\r\n        android:layout_gravity="center">\r\n\r\n        <TextView\r\n            android:layout_width="wrap_content"\r\n            android:layout_height="wrap_content"\r\n            android:text="New Text"\r\n            android:id="@+id/text_view"\r\n            android:layout_gravity="center_horizontal" />\r\n    </LinearLayout>\r\n</FrameLayout>\r\n', '2014-03-03 20:24:31'),
(58, ' android', 'FragmentTabNavX', ' // XML\r\n-- tab bar navigation activity\r\n<FrameLayout xmlns:android="http://schemas.android.com/apk/res/android"\r\n    xmlns:tools="http://schemas.android.com/tools"\r\n    android:id="@+id/container"\r\n    android:layout_width="match_parent"\r\n    android:layout_height="match_parent"\r\n    tools:context=".TabBarNavigationActivity"\r\n    tools:ignore="MergeRootFrame" >\r\n\r\n\r\n</FrameLayout>\r\n\r\n\r\n// XML --menu\r\n<menu xmlns:android="http://schemas.android.com/apk/res/android"\r\n    xmlns:tools="http://schemas.android.com/tools"\r\n    tools:context="com.example.app.MainActivity" >\r\n\r\n    <item\r\n        android:id="@+id/action_refresh"\r\n        android:orderInCategory="100"\r\n        android:showAsAction="never"\r\n\r\n        android:title="HOME"/>\r\n\r\n</menu>\r\n\r\n// XML strings\r\n  <!-- TABS -->\r\n    <string name="title_section1">1</string>\r\n    <string name="title_section2">2</string>\r\n    <string name="title_section3">3</string>\r\n\r\n\r\n\r\n\r\n// JAVA\r\n\r\n--main activity\r\n\r\npublic class MainActivity extends Activity implements\r\n        ActionBar.TabListener {\r\n\r\n\r\n    /**\r\n     * The serialization (saved instance state) Bundle key representing the\r\n     * current tab position.\r\n     */\r\n\r\n    private static final String STATE_SELECTED_NAVIGATION_ITEM = "selected_navigation_item";\r\n\r\n    @Override\r\n    protected void onCreate(Bundle savedInstanceState) {\r\n        super.onCreate(savedInstanceState);\r\n        setContentView(R.layout.tabbarnavigation_activity);\r\n\r\n        // Set up the action bar to show tabs.\r\n        final ActionBar actionBar = getActionBar();\r\n        actionBar.setNavigationMode(ActionBar.NAVIGATION_MODE_TABS);\r\n\r\n        // for each of the sections in the app, add a tab to the action bar.\r\n        actionBar.addTab(actionBar.newTab().setText(R.string.title_section1)\r\n                .setTabListener(this));\r\n        actionBar.addTab(actionBar.newTab().setText(R.string.title_section2)\r\n                .setTabListener(this));\r\n        actionBar.addTab(actionBar.newTab().setText(R.string.title_section3)\r\n                .setTabListener(this));\r\n\r\n\r\n\r\n        actionBar.setSubtitle("TabBarNavigation");\r\n        actionBar.setTitle("TabBarX");\r\n\r\n\r\n    }\r\n\r\n    @Override\r\n    public void onRestoreInstanceState(Bundle savedInstanceState) {\r\n        // Restore the previously serialized current tab position.\r\n        if (savedInstanceState.containsKey(STATE_SELECTED_NAVIGATION_ITEM)) {\r\n            getActionBar().setSelectedNavigationItem(savedInstanceState.getInt(STATE_SELECTED_NAVIGATION_ITEM));\r\n        }\r\n    }\r\n\r\n    @Override\r\n    public void onSaveInstanceState(Bundle outState) {\r\n        // Serialize the current tab position.\r\n        outState.putInt(STATE_SELECTED_NAVIGATION_ITEM, getActionBar()\r\n                .getSelectedNavigationIndex());\r\n    }\r\n\r\n    @Override\r\n    public boolean onCreateOptionsMenu(Menu menu) {\r\n\r\n        // Inflate the menu; this adds items to the action bar if it is present.\r\n        getMenuInflater().inflate(R.menu.main, menu);\r\n        return true;\r\n    }\r\n\r\n    @Override\r\n    public boolean onOptionsItemSelected(MenuItem item) {\r\n        switch (item.getItemId()) {\r\n            case R.id.action_refresh:\r\n                Intent homeIntent = new Intent(MainActivity.this, MainActivity.class);\r\n                startActivity(homeIntent);\r\n                break;\r\n\r\n\r\n            default:\r\n                break;\r\n        }\r\n\r\n        return true;\r\n    }\r\n\r\n    @Override\r\n    public void onTabSelected(ActionBar.Tab tab,\r\n                              FragmentTransaction fragmentTransaction) {\r\n\r\n        Log.d("DEBUG", String.valueOf(tab.getPosition()));\r\n        Fragment fragment;\r\n        Bundle args = new Bundle();\r\n        switch (tab.getPosition()) {\r\n\r\n            case 0:\r\n\r\n                fragment = new Tab1Fragment();\r\n                args.putInt(DummySectionFragment.ARG_SECTION_NUMBER,\r\n                        tab.getPosition() + 1);\r\n                fragment.setArguments(args);\r\n                getFragmentManager().beginTransaction()\r\n                        .replace(R.id.container, fragment).commit();\r\n                break;\r\n            case 1:\r\n\r\n                fragment = new Tab2Fragment();\r\n                args.putInt(Tab2Fragment.ARG_SECTION_NUMBER,\r\n                        tab.getPosition() + 1);\r\n                fragment.setArguments(args);\r\n                getFragmentManager().beginTransaction()\r\n                        .replace(R.id.container, fragment).commit();\r\n\r\n                break;\r\n            case 2:\r\n\r\n                fragment = new Tab3Fragment();\r\n                args.putInt(Tab3Fragment.ARG_SECTION_NUMBER,\r\n                        tab.getPosition() + 1);\r\n                fragment.setArguments(args);\r\n                getFragmentManager().beginTransaction()\r\n                        .replace(R.id.container, fragment).commit();\r\n\r\n                break;\r\n\r\n        }\r\n\r\n    }\r\n\r\n    @Override\r\n    public void onTabUnselected(ActionBar.Tab tab,\r\n                                FragmentTransaction fragmentTransaction) {\r\n    }\r\n\r\n    @Override\r\n    public void onTabReselected(ActionBar.Tab tab,\r\n                                FragmentTransaction fragmentTransaction) {\r\n    }\r\n\r\n\r\n    /**\r\n     * A dummy fragment representing a section of the app\r\n     */\r\n\r\n    public static class DummySectionFragment extends Fragment {\r\n        public static final String ARG_SECTION_NUMBER = "placeholder_text";\r\n\r\n        @Override\r\n        public View onCreateView(LayoutInflater inflater, ViewGroup container,\r\n                                 Bundle savedInstanceState) {\r\n            TextView textView = new TextView(getActivity());\r\n            textView.setGravity(Gravity.CENTER);\r\n            textView.setText(Integer.toString(getArguments().getInt(ARG_SECTION_NUMBER)));\r\n            return textView;\r\n        }\r\n    }\r\n\r\n}\r\n\r\n\r\n\r\n\r\n// JAVA tab fragments\r\n\r\npublic class Tab1Fragment  extends Fragment {\r\n\r\n    public static final String ARG_SECTION_NUMBER = "placeholder_text";\r\n\r\n    @Override\r\n    public View onCreateView(LayoutInflater inflater, ViewGroup container,\r\n                             Bundle savedInstanceState) {\r\n        TextView textView = new TextView(getActivity());\r\n        textView.setGravity(Gravity.CENTER);\r\n        textView.setText("tab 1");\r\n        return textView;\r\n    }\r\n}\r\n\r\n\r\n\r\n\r\npublic class Tab2Fragment  extends Fragment {\r\n\r\n    public static final String ARG_SECTION_NUMBER = "placeholder_text";\r\n\r\n    @Override\r\n    public View onCreateView(LayoutInflater inflater, ViewGroup container,\r\n                             Bundle savedInstanceState) {\r\n        TextView textView = new TextView(getActivity());\r\n        textView.setGravity(Gravity.CENTER);\r\n        textView.setText("tab 2");\r\n        return textView;\r\n    }\r\n}\r\n\r\n\r\n\r\npublic class Tab3Fragment  extends Fragment {\r\n\r\n    public static final String ARG_SECTION_NUMBER = "placeholder_text";\r\n\r\n    @Override\r\n    public View onCreateView(LayoutInflater inflater, ViewGroup container,\r\n                             Bundle savedInstanceState) {\r\n        TextView textView = new TextView(getActivity());\r\n        textView.setGravity(Gravity.CENTER);\r\n        textView.setText("tab 3");\r\n        return textView;\r\n    }\r\n}', '2014-03-03 20:29:35'),
(59, ' android', 'GalleryX', ' \r\n// XML activity main\r\n<FrameLayout xmlns:android="http://schemas.android.com/apk/res/android"\r\n    xmlns:tools="http://schemas.android.com/tools"\r\n    android:id="@+id/container"\r\n    android:layout_width="match_parent"\r\n    android:layout_height="match_parent"\r\n    tools:context="com.example.galleryx.MainActivity"\r\n    tools:ignore="MergeRootFrame" >\r\n\r\n    <LinearLayout\r\n        android:orientation="vertical"\r\n        android:layout_width="fill_parent"\r\n        android:layout_height="fill_parent"\r\n        android:layout_gravity="left|center_vertical">\r\n\r\n\r\n        <Gallery\r\n            android:layout_width="wrap_content"\r\n            android:layout_height="wrap_content"\r\n            android:id="@+id/gallery"\r\n            android:layout_gravity="center_horizontal" />\r\n    </LinearLayout>\r\n</FrameLayout>\r\n\r\n\r\n// XML gallery item\r\n<?xml version="1.0" encoding="utf-8"?>\r\n<RelativeLayout xmlns:android="http://schemas.android.com/apk/res/android"\r\n    android:orientation="horizontal"\r\n    android:layout_width="fill_parent"\r\n    android:layout_height="fill_parent"\r\n    android:id="@+id/gallery_item">\r\n\r\n    <TextView\r\n        android:id="@+id/textView"\r\n        android:layout_width="wrap_content"\r\n        android:text="TextView"\r\n        android:layout_height="wrap_content"\r\n        android:layout_alignParentTop="true"\r\n        android:layout_centerHorizontal="true"\r\n        android:layout_marginTop="14dp"></TextView>\r\n\r\n    <ImageView\r\n        android:layout_width="wrap_content"\r\n        android:id="@+id/imageView"\r\n        android:src="@drawable/ic_launcher"\r\n        android:layout_height="wrap_content"\r\n        android:layout_alignParentTop="true"\r\n        android:layout_alignParentRight="true"\r\n        android:layout_marginRight="22dp"></ImageView>\r\n\r\n    <CheckBox\r\n        android:id="@+id/checkBox"\r\n        android:layout_width="wrap_content"\r\n        android:layout_height="wrap_content"\r\n        android:layout_alignParentTop="true"\r\n        android:layout_alignParentLeft="true"\r\n        android:layout_marginLeft="24dp"></CheckBox>\r\n</RelativeLayout>\r\n\r\n\r\n// JAVA main activity\r\n\r\npublic class MainActivity extends Activity {\r\n\r\n\r\n   // Context c;\r\n\r\n\r\n    @Override\r\n    public void onCreate(Bundle savedInstanceState) {\r\n        super.onCreate(savedInstanceState);\r\n        setContentView(R.layout.activity_main);\r\n        // Find our gallery view and set the adapter for it\r\n        Gallery gallery = (Gallery) findViewById(R.id.gallery);\r\n        gallery.setAdapter(new newGalleryAdapter(MainActivity.this));\r\n    }\r\n\r\n    // The new custom Gallery Adapter for our new layout\r\n\r\n    private class newGalleryAdapter extends BaseAdapter {\r\n        public Context context; // needed to create the view\r\n\r\n        public newGalleryAdapter(Context context) {\r\n            this.context = context;\r\n        }\r\n\r\n        // set to three for this example\r\n        // for normal use this should be set to the length of the data\r\n        // structure that contains the items to be displayed\r\n        public int getCount() {\r\n            return 3;\r\n\r\n        }\r\n\r\n        public Object getItem(int position) {\r\n            return position;\r\n        }\r\n\r\n        public long getItemId(int position) {\r\n            return position;\r\n        }\r\n\r\n        public View getView(int position, View convertView, ViewGroup parent) {\r\n            View v;\r\n\r\n            // just a simple optimiztaion -\r\n            // we only inflate a new layout if we do not have one to reuse\r\n\r\n            if (convertView == null) {\r\n                v = LayoutInflater.from(this.context).inflate(R.layout.gallery_item,\r\n                        parent, false);\r\n            }\r\n            else{\r\n                v = convertView;\r\n            }\r\n            // Now that we have our new custom View, find out widgets in the\r\n            // view and set them\r\n            TextView tv = (TextView) v.findViewById(R.id.textView);\r\n\r\n            tv.setText(String.valueOf(position));\r\n            CheckBox cb = (CheckBox) v.findViewById(R.id.checkBox);\r\n\r\n            // this just alternates what the checkbox states are\r\n            if (position % 2 == 0)\r\n                cb.setChecked(true);\r\n            else\r\n                cb.setChecked(false);\r\n            return v;\r\n\r\n        }\r\n\r\n    }\r\n\r\n}\r\n', '2014-03-03 20:32:27'),
