Create(icicle);\r\n		setContentView(R.layout.mediaplayer_1);\r\n		mlocalaudio = (Button) findViewById(R.id.localaudio);\r\n		mlocalaudio.setOnClickListener(mLocalAudioListener);\r\n\r\n        mresourcesaudio = (Button) findViewById(R.id.resourcesaudio);\r\n		mresourcesaudio.setOnClickListener(mResourcesAudioListener);\r\n/*\r\n		mlocalvideo = (Button) findViewById(R.id.localvideo);\r\n		mlocalvideo.setOnClickListener(mLocalVideoListener);\r\n*/\r\n		mstreamvideo = (Button) findViewById(R.id.streamvideo);\r\n		mstreamvideo.setOnClickListener(mStreamVideoListener);\r\n\r\n	}\r\n\r\n	private View.OnClickListener mLocalAudioListener = new View.OnClickListener() {\r\n		public void onClick(View v) {\r\n			Intent intent = new Intent(MediaPlayerDemo.this.getApplication(), MediaPlayerDemo_Audio.class);\r\n			intent.putExtra(MEDIA, LOCAL_AUDIO);\r\n			startActivity(intent);\r\n\r\n		}\r\n	};\r\n\r\n	private View.OnClickListener mResourcesAudioListener = new View.OnClickListener() {\r\n		public void onClick(View v) {\r\n			Intent intent = new Intent(MediaPlayerDemo.this.getApplication(), MediaPlayerDemo_Audio.class);\r\n			intent.putExtra(MEDIA, RESOURCES_AUDIO);\r\n			startActivity(intent);\r\n\r\n		}\r\n	};\r\n    /*\r\n       private View.OnClickListener mLocalVideoListener = new View.OnClickListener() {\r\n           public void onClick(View v) {\r\n               Intent intent = new Intent(MediaPlayerDemo.this, MediaPlayerDemo_Video.class);\r\n               intent.putExtra(MEDIA, LOCAL_VIDEO);\r\n               startActivity(intent);\r\n\r\n           }\r\n       };\r\n          */\r\n       private View.OnClickListener mStreamVideoListener = new View.OnClickListener() {\r\n           public void onClick(View v) {\r\n               Intent intent = new Intent(MediaPlayerDemo.this, MediaPlayerDemo_Video.class);\r\n               intent.putExtra(MEDIA, STREAM_VIDEO);\r\n               startActivity(intent);\r\n\r\n           }\r\n       };\r\n\r\n	@Override\r\n	public void onClick(View v) {\r\n\r\n	}\r\n}\r\n\r\n\r\n// JAVA\r\n\r\npublic class MediaPlayerDemo_Audio extends Activity {\r\n\r\n	private static final String TAG = "MediaPlayerDemo";\r\n	private MediaPlayer mMediaPlayer;\r\n	private static final String MEDIA = "media";\r\n	private static final int LOCAL_AUDIO = 1;\r\n	private static final int STREAM_AUDIO = 2;\r\n	private static final int RESOURCES_AUDIO = 3;\r\n	private static final int LOCAL_VIDEO = 4;\r\n	private static final int STREAM_VIDEO = 5;\r\n	private String path;\r\n\r\n	private TextView tx;\r\n\r\n	@Override\r\n	public void onCreate(Bundle icicle) {\r\n		super.onCreate(icicle);\r\n		tx = new TextView(this);\r\n		setContentView(tx);\r\n		Bundle extras = getIntent().getExtras();\r\n		playAudio(extras.getInt(MEDIA));\r\n	}\r\n\r\n	private void playAudio(Integer media) {\r\n		try {\r\n			switch (media) {\r\n				case LOCAL_AUDIO:\r\n					/**\r\n					 * TODO: Set the path variable to a local audio file path.\r\n					 */\r\n					path = "";\r\n					if (path == "") {\r\n						// Tell the user to provide an audio file URL.\r\n						Toast.makeText(\r\n								MediaPlayerDemo_Audio.this,\r\n								"Please edit MediaPlayer_Audio Activity, " + "and set the path variable to your audio file path."\r\n										+ " Your audio file must be stored on sdcard.", Toast.LENGTH_LONG).show();\r\n\r\n					}\r\n					mMediaPlayer = new MediaPlayer();\r\n					mMediaPlayer.setDataSource(path);\r\n					mMediaPlayer.prepare();\r\n					mMediaPlayer.start();\r\n					break;\r\n				case RESOURCES_AUDIO:\r\n					/**\r\n					 * TODO: Upload a audio file to res/raw folder and provide its resid in MediaPlayer.create() method.\r\n					 */\r\n					mMediaPlayer = MediaPlayer.create(this, R.raw.salamisound);\r\n					mMediaPlayer.start();\r\n			}\r\n			tx.setText("Playing audio...");\r\n		}\r\n		catch (Exception e) {\r\n			Log.e(TAG, "error: " + e.getMessage(), e);\r\n		}\r\n\r\n	}\r\n\r\n	@Override\r\n	protected void onDestroy() {\r\n		super.onDestroy();\r\n		// TODO Auto-generated method stub\r\n		if (mMediaPlayer != null) {\r\n			mMediaPlayer.release();\r\n			mMediaPlayer = null;\r\n		}\r\n\r\n	}\r\n\r\n}\r\n\r\n\r\n\r\n// JAVA\r\n\r\npublic class MediaPlayerDemo_Video  extends Activity implements\r\n        MediaPlayer.OnBufferingUpdateListener, MediaPlayer.OnCompletionListener,\r\n        MediaPlayer.OnPreparedListener, MediaPlayer.OnVideoSizeChangedListener, SurfaceHolder.Callback {\r\n\r\n    private static final String TAG = "MediaPlayerDemo";\r\n    private int mVideoWidth;\r\n    private int mVideoHeight;\r\n    private MediaPlayer mMediaPlayer;\r\n    private SurfaceView mPreview;\r\n    private SurfaceHolder holder;\r\n    private String path;\r\n    private Bundle extras;\r\n    private static final String MEDIA = "media";\r\n    private static final int LOCAL_AUDIO = 1;\r\n    private static final int STREAM_AUDIO = 2;\r\n    private static final int RESOURCES_AUDIO = 3;\r\n    private static final int LOCAL_VIDEO = 4;\r\n    private static final int STREAM_VIDEO = 5;\r\n    private boolean mIsVideoSizeKnown = false;\r\n    private boolean mIsVideoReadyToBePlayed = false;\r\n\r\n    /**\r\n     *\r\n     * Called when the activity is first created.\r\n     */\r\n    @Override\r\n    public void onCreate(Bundle icicle) {\r\n        super.onCreate(icicle);\r\n        setContentView(R.layout.mediaplayer_2);\r\n        mPreview = (SurfaceView) findViewById(R.id.surfaceView);\r\n        holder = mPreview.getHolder();\r\n        holder.addCallback(this);\r\n        holder.setType(SurfaceHolder.SURFACE_TYPE_PUSH_BUFFERS);\r\n        extras = getIntent().getExtras();\r\n\r\n    }\r\n\r\n    private void playVideo(Integer Media) {\r\n        doCleanUp();\r\n        try {\r\n\r\n            switch (Media) {\r\n                case LOCAL_VIDEO:\r\n                    /*\r\n                     * TODO: Set the path variable to a local media file path.\r\n                     */\r\n                    path = "";\r\n                    if (path == "") {\r\n                        // Tell the user to provide a media file URL.\r\n                        Toast\r\n                                .makeText(\r\n                                        MediaPlayerDemo_Video.this,\r\n                                        "Please edit MediaPlayerDemo_Video Activity, "\r\n                                                + "and set the path variable to your media file path."\r\n                                                + " Your media file must be stored on sdcard.",\r\n                                        Toast.LENGTH_LONG).show();\r\n\r\n                    }\r\n                    break;\r\n                case STREAM_VIDEO:\r\n                    /*\r\n                     * TODO: Set path variable to progressive streamable mp4 or\r\n                     * 3gpp format URL. Http protocol should be used.\r\n                     * Mediaplayer can only play "progressive streamable\r\n                     * contents" which basically means: 1. the movie atom has to\r\n                     * precede all the media data atoms. 2. The clip has to be\r\n                     * reasonably interleaved.\r\n                     *\r\n                     */\r\n                    path = "http://www.pocketjourney.com/downloads/pj/video/famous.3gp";\r\n                    if (path == "") {\r\n                        // Tell the user to provide a media file URL.\r\n                        Toast\r\n                                .makeText(\r\n                                        MediaPlayerDemo_Video.this,\r\n                                        "Please edit MediaPlayerDemo_Video Activity,"\r\n                                                + " and set the path variable to your media file URL.",\r\n                                        Toast.LENGTH_LONG).show();\r\n\r\n                    }\r\n\r\n                    break;\r\n\r\n\r\n            }\r\n\r\n            // Create a new media player and set the listeners\r\n            mMediaPlayer = new MediaPlayer();\r\n            mMediaPlayer.setDataSource(path);\r\n            mMediaPlayer.setDisplay(holder);\r\n            mMediaPlayer.prepare();\r\n            mMediaPlayer.setOnBufferingUpdateListener(this);\r\n            mMediaPlayer.setOnCompletionListener(this);\r\n            mMediaPlayer.setOnPreparedListener(this);\r\n            mMediaPlayer.setOnVideoSizeChangedListener(this);\r\n            mMediaPlayer.setAudioStreamType(AudioManager.STREAM_MUSIC);\r\n\r\n\r\n        } catch (Exception e) {\r\n            Log.e(TAG, "error: " + e.getMessage(), e);\r\n        }\r\n    }\r\n\r\n    public void onBufferingUpdate(MediaPlayer arg0, int percent) {\r\n        Log.d(TAG, "onBufferingUpdate percent:" + percent);\r\n\r\n    }\r\n\r\n    public void onCompletion(MediaPlayer arg0) {\r\n        Log.d(TAG, "onCompletion called");\r\n    }\r\n\r\n    public void onVideoSizeChanged(MediaPlayer mp, int width, int height) {\r\n        Log.v(TAG, "onVideoSizeChanged called");\r\n        if (width == 0 || height == 0) {\r\n            Log.e(TAG, "invalid video width(" + width + ") or height(" + height + ")");\r\n            return;\r\n        }\r\n        mIsVideoSizeKnown = true;\r\n        mVideoWidth = width;\r\n        mVideoHeight = height;\r\n        if (mIsVideoReadyToBePlayed && mIsVideoSizeKnown) {\r\n            startVideoPlayback();\r\n        }\r\n    }\r\n\r\n    public void onPrepared(MediaPlayer mediaplayer) {\r\n        Log.d(TAG, "onPrepared called");\r\n        mIsVideoReadyToBePlayed = true;\r\n        if (mIsVideoReadyToBePlayed && mIsVideoSizeKnown) {\r\n            startVideoPlayback();\r\n        }\r\n    }\r\n\r\n    public void surfaceChanged(SurfaceHolder surfaceholder, int i, int j, int k) {\r\n        Log.d(TAG, "surfaceChanged called");\r\n\r\n    }\r\n\r\n    public void surfaceDestroyed(SurfaceHolder surfaceholder) {\r\n        Log.d(TAG, "surfaceDestroyed called");\r\n    }\r\n\r\n\r\n    public void surfaceCreated(SurfaceHolder holder) {\r\n        Log.d(TAG, "surfaceCreated called");\r\n        playVideo(extras.getInt(MEDIA));\r\n\r\n\r\n    }\r\n\r\n    @Override\r\n    protected void onPause() {\r\n        super.onPause();\r\n        releaseMediaPlayer();\r\n        doCleanUp();\r\n    }\r\n\r\n    @Override\r\n    protected void onDestroy() {\r\n        super.onDestroy();\r\n        releaseMediaPlayer();\r\n        doCleanUp();\r\n    }\r\n\r\n    private void releaseMediaPlayer() {\r\n        if (mMediaPlayer != null) {\r\n            mMediaPlayer.release();\r\n            mMediaPlayer = null;\r\n        }\r\n    }\r\n\r\n    private void doCleanUp() {\r\n        mVideoWidth = 0;\r\n        mVideoHeight = 0;\r\n        mIsVideoReadyToBePlayed = false;\r\n        mIsVideoSizeKnown = false;\r\n    }\r\n\r\n    private void startVideoPlayback() {\r\n        Log.v(TAG, "startVideoPlayback");\r\n        holder.setFixedSize(mVideoWidth, mVideoHeight);\r\n        mMediaPlayer.start();\r\n    }\r\n}\r\n', '2014-03-03 20:58:44'),
(65, ' android', 'MotionX', ' \r\n\r\n// JAVA\r\n\r\npublic class MainActivity extends Activity {\r\nprivate AnimView animView;\r\n    @Override\r\n    protected void onCreate(Bundle savedInstanceState) {\r\n        super.onCreate(savedInstanceState);\r\n       // setContentView(R.layout.activity_main);\r\n        animView = new AnimView(MainActivity.this);\r\n        setContentView(animView);\r\n    }\r\n\r\n}\r\n\r\n\r\n// JAVA\r\n\r\npublic class AnimView extends SurfaceView implements SurfaceHolder.Callback, SensorEventListener {\r\n\r\n    //private SurfaceHolder holder;\r\n    private AnimThread animThread;\r\nContext context;\r\n    public AnimView(Context context) {\r\n        super(context);\r\n        this.context = context;\r\n       //  holder = getHolder();\r\n       // this.holder.addCallback(this);\r\n        getHolder().addCallback(this);\r\n        setFocusable(true);\r\n\r\n        SensorManager manager = (SensorManager)context.getSystemService(Context.SENSOR_SERVICE);\r\n        if(manager.getSensorList(Sensor.TYPE_ACCELEROMETER).size() != 0) {\r\n            Sensor accelerometer = manager.getSensorList(Sensor.TYPE_ACCELEROMETER).get(0);\r\n            manager.registerListener(this, accelerometer, SensorManager.SENSOR_DELAY_GAME);\r\n           //YES Log.d("DEBUG","accelerometer");\r\n\r\n\r\n            this.animThread = new AnimThread(getHolder());\r\n        }else{\r\n           // Log.d("DEBUG","NO NO NO accelerometer");\r\n        }\r\n    }\r\n\r\n    @Override\r\n    public void surfaceCreated(SurfaceHolder holder) {\r\n        Log.d("DEBUG","surfaceCreated()");\r\n    }\r\n\r\n    @Override\r\n    public void surfaceChanged(SurfaceHolder holder, int format, int width, int height) {\r\n        Log.d("DEBUG","surfaceChanged()");\r\n    }\r\n\r\n    @Override\r\n    public void surfaceDestroyed(SurfaceHolder holder) {\r\n        Log.d("DEBUG","surfaceDestroyed()");\r\n    }\r\n\r\n    @Override\r\n    public void onSensorChanged(SensorEvent event) {\r\n       //HIT  Log.d("DEBUG","onSensorChanged()");\r\n        if(this.animThread != null) {\r\n            Log.d("DEBUG", "animThread IS");\r\n            Log.d("DEBUG event.values[0]: ", String.valueOf(event.values[0]));\r\n            Log.d("DEBUG event.values[1]: ", String.valueOf(event.values[1]));\r\n\r\n            this.animThread.setTilt(event.values[0], event.values[1]);\r\n        }else{\r\n           // Log.d("DEBUG","animThread IS NOT ");\r\n        }\r\n    }\r\n\r\n    @Override\r\n    public void onAccuracyChanged(Sensor sensor, int accuracy) {\r\n        Log.d("DEBUG","onAccuracyChanged()");\r\n    }\r\n}\r\n\r\n\r\n// JAVA\r\n\r\n\r\npublic class AnimThread  extends Thread{\r\n    private SurfaceHolder holder;\r\n    private boolean running = true;\r\n    private float x = 100;\r\n    private float y = 100;\r\n    private float vx = 0;\r\n    private float vy = 0;\r\n    private float tiltX = 0;\r\n    private float tiltY = 0;\r\n\r\n    public AnimThread(SurfaceHolder holder) {\r\n        this.holder = holder;\r\n       this.setRunning(true);\r\n        Log.d("DEBUG", "AnimThread constructor");\r\n        //run();\r\n    }\r\n\r\n    @Override\r\n    public void run() {\r\n        Log.d("DEBUG", "AnimThread run");\r\n        while(running ) {\r\n            Canvas canvas = null;\r\n\r\n            try {\r\n                Log.d("DEBUG", "canvas try");\r\n                canvas = holder.lockCanvas();\r\n                synchronized (holder) {\r\n                    // update\r\n                    vx -= tiltX * 0.1;\r\n                    vy += tiltY * 0.1;\r\n                    x += vx;\r\n                    y += vy;\r\n\r\n                    // draw\r\n                    canvas.drawColor(Color.WHITE);\r\n                    Paint paint = new Paint();\r\n                    paint.setColor(Color.RED);\r\n                    canvas.drawCircle(x, y, 50, paint);\r\n\r\n                }\r\n            }\r\n            finally {\r\n                Log.d("DEBUG", "canvas finally");\r\n                if (canvas != null) {\r\n                    Log.d("DEBUG", "canvas NOT NULL");\r\n                    holder.unlockCanvasAndPost(canvas);\r\n                }else{\r\n                    Log.d("DEBUG", "canvas IS NULL");\r\n                }\r\n            }\r\n        }\r\n    }\r\n    public void setRunning(boolean b) {\r\n        Log.d("DEBUG", "setRunning()");\r\n        running = b;\r\n    }\r\n\r\n    public void setTilt(float tiltX, float tiltY) {\r\n        Log.d("DEBUG", "setTilt()");\r\n        this.tiltX = tiltX;\r\n        this.tiltY = tiltY;\r\n    }\r\n\r\n}\r\n\r\n', '2014-03-03 21:01:20'),
(66, ' android', 'NavDrawerX', ' \r\n\r\n// Gradle\r\n\r\ndependencies {\r\n    compile ---com.android.support:support-v4:19.0.1--- REPLACE --- with single apostrophe\r\n\r\n}\r\n\r\n\r\n// XML\r\n-- activity_navigationdrawer.xml\r\n\r\n<android.support.v4.widget.DrawerLayout\r\n    xmlns:android="http://schemas.android.com/apk/res/android"\r\n    android:id="@+id/drawer_layout"\r\n    android:layout_width="match_parent"\r\n    android:layout_height="match_parent">\r\n\r\n    <!-- Framelayout to display Fragments -->\r\n    <FrameLayout\r\n        android:id="@+id/frame_container"\r\n        android:layout_width="match_parent"\r\n        android:layout_height="match_parent" />\r\n\r\n    <!-- Listview to display slider menu -->\r\n    <ListView\r\n        android:id="@+id/list_slidermenu"\r\n        android:layout_width="240dp"\r\n        android:layout_height="match_parent"\r\n        android:layout_gravity="start"\r\n        android:choiceMode="singleChoice"\r\n\r\n        android:dividerHeight="1dp"\r\n\r\n        />\r\n</android.support.v4.widget.DrawerLayout>\r\n\r\n    <!--\r\n    extracted list view proerties:\r\n         android:divider="@color/list_divider"\r\n            android:listSelector="@drawable/list_selector"\r\n            android:background="@color/list_background"\r\n                -->\r\n\r\n\r\n// XML\r\n--drawer_list_item.xml\r\n<?xml version="1.0" encoding="utf-8"?>\r\n<RelativeLayout xmlns:android="http://schemas.android.com/apk/res/android"\r\n    android:layout_width="match_parent"\r\n    android:layout_height="48dp"\r\n    android:background="@drawable/list_selector">\r\n<!-- android:src="@drawable/ic_launcher" -->\r\n    <ImageView\r\n        android:id="@+id/icon"\r\n        android:layout_width="25dp"\r\n        android:layout_height="wrap_content"\r\n        android:layout_alignParentLeft="true"\r\n        android:layout_marginLeft="12dp"\r\n        android:layout_marginRight="12dp"\r\n        android:contentDescription="@string/desc_list_item_icon"\r\n\r\n        android:layout_centerVertical="true" />\r\n\r\n    <TextView\r\n        android:id="@+id/title"\r\n        android:layout_width="wrap_content"\r\n        android:layout_height="match_parent"\r\n        android:layout_toRightOf="@id/icon"\r\n        android:minHeight="?android:attr/listPreferredItemHeightSmall"\r\n        android:textAppearance="?android:attr/textAppearanceListItemSmall"\r\n\r\n        android:gravity="center_vertical"\r\n        android:paddingRight="40dp"/>\r\n\r\n    <TextView android:id="@+id/counter"\r\n        android:layout_width="wrap_content"\r\n        android:layout_height="wrap_content"\r\n        android:background="@drawable/counter_bg"\r\n        android:layout_alignParentRight="true"\r\n        android:layout_centerVertical="true"\r\n        android:layout_marginRight="8dp"\r\n        />\r\n\r\n</RelativeLayout>\r\n\r\n    <!--\r\n        extracted:\r\n        android:textColor="@color/list_item_title"\r\n        android:textColor="@color/counter_text_color"\r\n\r\n            -->\r\n\r\n\r\n// XML\r\n--list_item.xml\r\n<?xml version="1.0" encoding="utf-8"?>\r\n<LinearLayout xmlns:android="http://schemas.android.com/apk/res/android"\r\n    android:layout_width="match_parent"\r\n    android:layout_height="match_parent"\r\n    android:orientation="vertical" >\r\n\r\n    <!-- Single ListItem -->\r\n\r\n    <!-- Product Name -->\r\n    <TextView android:id="@+id/product_name"\r\n        android:layout_width="fill_parent"\r\n        android:layout_height="wrap_content"\r\n        android:padding="10dip"\r\n        android:textSize="16dip"\r\n        android:textStyle="bold"/>\r\n\r\n</LinearLayout>\r\n\r\n\r\n\r\n// XML STRINGS\r\n<?xml version="1.0" encoding="utf-8"?>\r\n<resources>\r\n\r\n    <string name="app_name">DrawerX</string>\r\n    <string name="hello_world">Hello world!</string>\r\n    <string name="action_settings">Settings</string>\r\n\r\n    <!-- hive navigation drawer -->\r\n    <string name="drawer_open">Slider Menu Opened</string>\r\n    <string name="drawer_close">Slider Menu Closed</string>\r\n\r\n    <!-- Nav Drawer Menu Items -->\r\n    <string-array name="nav_drawer_items">\r\n        <item >Home</item>\r\n        <item >Find People</item>\r\n        <item >Photos</item>\r\n        <item >Communities</item>\r\n        <item >Pages</item>\r\n        <item >What is Hot</item>\r\n    </string-array>\r\n\r\n    <!-- Nav Drawer List Item Icons -->\r\n    <!-- Keep them in order as the titles are in -->\r\n    <array name="nav_drawer_icons">\r\n        <item>@drawable/drawer_item</item>\r\n        <item>@drawable/drawer_item</item>\r\n        <item>@drawable/drawer_item</item>\r\n        <item>@drawable/drawer_item</item>\r\n        <item>@drawable/drawer_item</item>\r\n        <item>@drawable/drawer_item</item>\r\n    </array>\r\n\r\n    <!-- Content Description -->\r\n    <string name="desc_list_item_icon">Item Icon</string>\r\n\r\n</resources>\r\n\r\n\r\n\r\n// JAVA adapters\r\n\r\npublic class NavDrawerListAdapter extends BaseAdapter {\r\n\r\n    private Context context;\r\n    private ArrayList<NavDrawerItem> navDrawerItems;\r\n\r\n    public NavDrawerListAdapter(Context context, ArrayList<NavDrawerItem> navDrawerItems) {\r\n        this.context = context;\r\n        this.navDrawerItems = navDrawerItems;\r\n    }\r\n\r\n    @Override\r\n    public int getCount() {\r\n        return navDrawerItems.size();\r\n    }\r\n\r\n    @Override\r\n    public Object getItem(int position) {\r\n        return navDrawerItems.get(position);\r\n    }\r\n\r\n    @Override\r\n    public long getItemId(int position) {\r\n        return position;\r\n    }\r\n\r\n    @Override\r\n    public View getView(int position, View convertView, ViewGroup parent) {\r\n        if (convertView == null) {\r\n            LayoutInflater mInflater = (LayoutInflater) context.getSystemService(Activity.LAYOUT_INFLATER_SERVICE);\r\n            convertView = mInflater.inflate(R.layout.drawer_list_item, null);\r\n        }\r\n\r\n        ImageView imgIcon = (ImageView) convertView.findViewById(R.id.icon);\r\n        TextView txtTitle = (TextView) convertView.findViewById(R.id.title);\r\n        TextView txtCount = (TextView) convertView.findViewById(R.id.counter);\r\n\r\n        imgIcon.setImageResource(navDrawerItems.get(position).getIcon());\r\n        txtTitle.setText(navDrawerItems.get(position).getTitle());\r\n\r\n        if (navDrawerItems.get(position).getCounterVisibility()) {\r\n            txtCount.setText(navDrawerItems.get(position).getCount());\r\n        } else {\r\n            // hide the counter view\r\n            txtCount.setVisibility(View.GONE);\r\n        }\r\n\r\n        return convertView;\r\n    }\r\n}\r\n\r\n// JAVA navigationDrawer\r\n\r\npublic class NavDrawerItem {\r\n\r\n    private String title;\r\n    private int icon;\r\n    private String count = "0";\r\n    private boolean isCounterVisible = false;\r\n\r\n    public NavDrawerItem(){}\r\n\r\n    public NavDrawerItem(String title, int icon){\r\n        this.title = title;\r\n        this.icon = icon;\r\n    }\r\n\r\n    public NavDrawerItem(String title, int icon, boolean isCounterVisible, String count){\r\n        this.title = title;\r\n        this.icon = icon;\r\n        this.isCounterVisible = isCounterVisible;\r\n        this.count = count;\r\n    }\r\n\r\n    public String getTitle(){\r\n        return this.title;\r\n    }\r\n\r\n    public int getIcon(){\r\n        return this.icon;\r\n    }\r\n\r\n    public String getCount(){\r\n        return this.count;\r\n    }\r\n\r\n    public boolean getCounterVisibility(){\r\n        return this.isCounterVisible;\r\n    }\r\n\r\n    public void setTitle(String title){\r\n        this.title = title;\r\n    }\r\n\r\n    public void setIcon(int icon){\r\n        this.icon = icon;\r\n    }\r\n\r\n    public void setCount(String count){\r\n        this.count = count;\r\n    }\r\n\r\n    public void setCounterVisibility(boolean isCounterVisible){\r\n        this.isCounterVisible = isCounterVisible;\r\n    }\r\n\r\n}\r\n\r\n\r\n//JAVA main activity\r\n\r\npublic class MainActivity extends Activity implements ListView.OnItemClickListener{\r\n\r\n    private DrawerLayout mDrawerLayout;\r\n    private ListView mDrawerList;\r\n    private ActionBarDrawerToggle mDrawerToggle;\r\n    private CharSequence mDrawerTitle;\r\n    private CharSequence mAppTitle;\r\n    private String[] navMenuTitles;\r\n    private TypedArray navMenuIcons;\r\n    private ArrayList<NavDrawerItem> navDrawerItems;\r\n    private NavDrawerListAdapter adapter;\r\n\r\n    @Override\r\n    protected void onCreate(Bundle savedInstanceState) {\r\n        super.onCreate(savedInstanceState);\r\n        setContentView(com.example.navdrawerx.R.layout.activity_navigationdrawer);\r\n\r\n        mAppTitle = mDrawerTitle = getTitle();\r\n        navMenuTitles = getResources().getStringArray(R.array.nav_drawer_items);\r\n        navMenuIcons = getResources().obtainTypedArray(R.array.nav_drawer_icons);\r\n\r\n        mDrawerLayout = (DrawerLayout) findViewById(R.id.drawer_layout);\r\n        mDrawerList = (ListView) findViewById(R.id.list_slidermenu);\r\n        mDrawerList.setOnItemClickListener(this);\r\n        navDrawerItems = new ArrayList<NavDrawerItem>();\r\n\r\n        navDrawerItems.add(new NavDrawerItem(navMenuTitles[0], navMenuIcons.getResourceId(0, -1)));\r\n        navDrawerItems.add(new NavDrawerItem(navMenuTitles[1], navMenuIcons.getResourceId(1, -1)));\r\n        navDrawerItems.add(new NavDrawerItem(navMenuTitles[2], navMenuIcons.getResourceId(2, -1)));\r\n        navDrawerItems.add(new NavDrawerItem(navMenuTitles[3], navMenuIcons.getResourceId(3, -1), true, "22"));\r\n        navDrawerItems.add(new NavDrawerItem(navMenuTitles[4], navMenuIcons.getResourceId(4, -1)));\r\n        navDrawerItems.add(new NavDrawerItem(navMenuTitles[5], navMenuIcons.getResourceId(5, -1), true, "50+"));\r\n\r\n        // Recycle the typed array\r\n        navMenuIcons.recycle();\r\n\r\n        adapter = new NavDrawerListAdapter(getApplicationContext(), navDrawerItems);\r\n        mDrawerList.setAdapter(adapter);\r\n\r\n        // enabling action bar app icon and behaving it as toggle button\r\n        getActionBar().setDisplayHomeAsUpEnabled(true);\r\n        getActionBar().setHomeButtonEnabled(true);\r\n\r\n        mDrawerToggle = new ActionBarDrawerToggle(this, mDrawerLayout, R.drawable.toggle, //nav menu toggle icon\r\n                R.string.app_name, // nav drawer open - description for accessibility\r\n                R.string.app_name // nav drawer close - description for accessibility\r\n        ) {\r\n            public void onDrawerClosed(View view) {\r\n                getActionBar().setTitle(mAppTitle);\r\n                // calling onPrepareOptionsMenu() to show action bar icons\r\n                invalidateOptionsMenu();\r\n            }\r\n\r\n            public void onDrawerOpened(View drawerView) {\r\n                getActionBar().setTitle(mDrawerTitle);\r\n                // calling onPrepareOptionsMenu() to hide action bar icons\r\n                invalidateOptionsMenu();\r\n            }\r\n        };\r\n        mDrawerLayout.setDrawerListener(mDrawerToggle);\r\n\r\n        if (savedInstanceState == null) {\r\n            // on first time display view for first nav item\r\n            // !!!!!!   displayView(0);\r\n        }\r\n    }\r\n\r\n    @Override\r\n    public boolean onCreateOptionsMenu(Menu menu) {\r\n        getMenuInflater().inflate(R.menu.navigationdrawermenu, menu);\r\n        return true;\r\n    }\r\n\r\n    @Override\r\n    public boolean onOptionsItemSelected(MenuItem item) {\r\n        // toggle nav drawer on selecting action bar app icon/title\r\n        if (mDrawerToggle.onOptionsItemSelected(item)) {\r\n            return true;\r\n        }\r\n        return false;\r\n    }\r\n\r\n    /**\r\n     * Called when invalidateOptionsMenu() is triggered\r\n     */\r\n    @Override\r\n    public boolean onPrepareOptionsMenu(Menu menu) {\r\n        // if nav drawer is opened, hide the action items\r\n        boolean drawerOpen = mDrawerLayout.isDrawerOpen(mDrawerList);\r\n        return super.onPrepareOptionsMenu(menu);\r\n    }\r\n\r\n    @Override\r\n    public void setTitle(CharSequence title) {\r\n        mAppTitle = title;\r\n        getActionBar().setTitle(mAppTitle);\r\n    }\r\n\r\n    /**\r\n     * When using the ActionBarDrawerToggle, you must call it during onPostCreate() and onConfigurationChanged()...\r\n     */\r\n\r\n    @Override\r\n    protected void onPostCreate(Bundle savedInstanceState) {\r\n        super.onPostCreate(savedInstanceState);\r\n        // Sync the toggle state after onRestoreInstanceState has occurred.\r\n        mDrawerToggle.syncState();\r\n    }\r\n\r\n    @Override\r\n    public void onConfigurationChanged(Configuration newConfig) {\r\n        super.onConfigurationChanged(newConfig);\r\n        // Pass any configuration change to the drawer toggls\r\n        mDrawerToggle.onConfigurationChanged(newConfig);\r\n    }\r\n\r\n    @Override\r\n    public void onItemClick(AdapterView<?> parent, View view, int position, long id) {\r\n        // Fragment newFragment;\r\n        // FragmentTransaction transaction = getFragmentManager().beginTransaction();\r\n        Toast.makeText(MainActivity.this, "position: " + position + " id:" + id + "", Toast.LENGTH_LONG).show();\r\n\r\n        switch (position) {\r\n            case 0:\r\n                /* Example call to a Fragment\r\n                newFragment = new f1();\r\n                transaction.replace(R.id.content_frame, newFragment);\r\n                transaction.addToBackStack(null);\r\n                transaction.commit();\r\n                */\r\n\r\n                break;\r\n\r\n            case 1:\r\n                /*\r\n                newFragment = new f2();\r\n                transaction.replace(R.id.content_frame, newFragment);\r\n       