(67, ' android', 'PhoneX', ' \r\n\r\n// manifest\r\n    <uses-permission android:name="android.permission.CALL_PHONE" />\r\n    <uses-permission android:name="android.permission.READ_PHONE_STATE" />\r\n\r\n// XML\r\n<FrameLayout xmlns:android="http://schemas.android.com/apk/res/android"\r\n    xmlns:tools="http://schemas.android.com/tools"\r\n    android:id="@+id/container"\r\n    android:layout_width="match_parent"\r\n    android:layout_height="match_parent"\r\n    tools:context="com.example.phonex.MainActivity"\r\n    tools:ignore="MergeRootFrame" >\r\n\r\n    <LinearLayout\r\n        android:orientation="vertical"\r\n        android:layout_width="fill_parent"\r\n        android:layout_height="fill_parent"\r\n        android:layout_gravity="left|center_vertical">\r\n\r\n        <Button\r\n            android:id="@+id/button"\r\n            android:layout_width="wrap_content"\r\n            android:layout_height="wrap_content"\r\n            android:text="Call 123456" />\r\n\r\n        </LinearLayout>\r\n</FrameLayout>\r\n\r\n\r\n// JAVA\r\n\r\n\r\npublic class MainActivity extends Activity implements View.OnClickListener{\r\n\r\n    private Button btn;\r\n\r\n\r\n    @Override\r\n    protected void onCreate(Bundle savedInstanceState) {\r\n        super.onCreate(savedInstanceState);\r\n        setContentView(R.layout.activity_main);\r\n\r\n        btn = (Button) findViewById(R.id.button);\r\n\r\n        PhoneCallListener phoneCallListener = new PhoneCallListener();\r\n        TelephonyManager telManager = (TelephonyManager) this.getSystemService(Context.TELEPHONY_SERVICE);\r\n        telManager.listen(phoneCallListener, PhoneStateListener.LISTEN_CALL_STATE);\r\n\r\n        btn.setOnClickListener(MainActivity.this);\r\n\r\n\r\n    }\r\n\r\n    @Override\r\n    public void onClick(View v) {\r\n        Intent phoneCallIntent = new Intent(Intent.ACTION_CALL);\r\n        phoneCallIntent.setData(Uri.parse("tel:123456"));\r\n        startActivity(phoneCallIntent);\r\n    }\r\n\r\n\r\n    // monitor phone call states\r\n    private class PhoneCallListener extends PhoneStateListener {\r\n\r\n        String TAG = "LOGGING PHONE CALL";\r\n\r\n        private boolean phoneCalling = false;\r\n\r\n        @Override\r\n        public void onCallStateChanged(int state, String incomingNumber) {\r\n\r\n            if (TelephonyManager.CALL_STATE_RINGING == state) {\r\n                // phone ringing\r\n                Log.i(TAG, "RINGING, number: " + incomingNumber);\r\n            }\r\n\r\n            if (TelephonyManager.CALL_STATE_OFFHOOK == state) {\r\n                // active\r\n                Log.i(TAG, "OFFHOOK");\r\n\r\n                phoneCalling = true;\r\n            }\r\n\r\n            // When the call ends launch the main activity again\r\n            if (TelephonyManager.CALL_STATE_IDLE == state) {\r\n\r\n                Log.i(TAG, "IDLE");\r\n\r\n                if (phoneCalling) {\r\n\r\n                    Log.i(TAG, "restart app");\r\n\r\n                    // restart app\r\n                    Intent i = getBaseContext().getPackageManager()\r\n                            .getLaunchIntentForPackage(\r\n                                    getBaseContext().getPackageName());\r\n\r\n                    i.addFlags(Intent.FLAG_ACTIVITY_CLEAR_TOP);\r\n                    startActivity(i);\r\n\r\n                    phoneCalling = false;\r\n                }\r\n\r\n            }\r\n        }\r\n    }\r\n\r\n\r\n\r\n}\r\n\r\n\r\n', '2014-03-03 21:10:52');
