(68, ' android', 'SMSX', ' \r\n\r\n//manifest\r\n <uses-permission android:name="android.permission.SEND_SMS" />\r\n\r\n // JAVA\r\n\r\n\r\npublic class MainActivity extends Activity implements View.OnClickListener {\r\n\r\n	Button button;\r\n	EditText editPhoneNum;\r\n	EditText editSMS;\r\n\r\n	@Override\r\n	protected void onCreate(Bundle savedInstanceState) {\r\n		super.onCreate(savedInstanceState);\r\n		setContentView(R.layout.activity_main);\r\n\r\n		button = (Button) findViewById(R.id.button);\r\n		editPhoneNum = (EditText) findViewById(R.id.editPhoneNum);\r\n		editSMS = (EditText) findViewById(R.id.editSMS);\r\n		button.setOnClickListener(this);\r\n\r\n	}\r\n\r\n	@Override\r\n	public void onClick(View v) {\r\n		String phoneNo = editPhoneNum.getText().toString();\r\n		String sms = editSMS.getText().toString();\r\n\r\n		try {\r\n			SmsManager smsManager = SmsManager.getDefault();\r\n			smsManager.sendTextMessage(phoneNo, null, sms, null, null);\r\n			Toast.makeText(getApplicationContext(), "SMS Sent!", Toast.LENGTH_LONG).show();\r\n		}\r\n		catch (Exception e) {\r\n			Toast.makeText(getApplicationContext(), "SMS faild, please try again later!", Toast.LENGTH_LONG).show();\r\n			e.printStackTrace();\r\n		}\r\n	}\r\n}\r\n\r\n\r\n', '2014-03-03 21:13:03'),
(69, ' android', 'TestX', '\r\n//TESTX : unit testing and automation\r\n\r\n\r\n// Gradle : remember to sync gradle afterwards.\r\ndependencies {\r\n    compile ---com.jayway.android.robotium:robotium-solo:4.3.1@jar---   REPLACE  --- with single apostrophe\r\n}\r\n\r\n\r\n\r\n defaultConfig {\r\n\r\n        testPackageName "com.example.testx.test"\r\n        testInstrumentationRunner "com.example.testx.test.Runner"\r\n\r\n        OR\r\n\r\n         testPackageName "com.mocura.moduleseventests.test"\r\n                testInstrumentationRunner "com.mocura.moduleseventests.test.Runner"\r\n    }\r\n\r\n\r\n\r\n\r\n// JAVA : RUNNER\r\n    package com.mocura.moduleseventests.test;\r\n\r\n    import android.test.InstrumentationTestRunner;\r\n    import android.test.InstrumentationTestSuite;\r\n\r\n    import com.mocura.moduleseventests.MainActivityTest;\r\n\r\n    import junit.framework.TestSuite;\r\n\r\n    /**\r\n     * Created by swav on 21/08/13.\r\n     */\r\n    public class Runner extends InstrumentationTestRunner {\r\n\r\n    	@Override\r\n    	public TestSuite getAllTests() {\r\n    		InstrumentationTestSuite suite = new InstrumentationTestSuite(this);\r\n    		suite.addTestSuite(MainActivityTest.class);\r\n\r\n    		//suite.addTestSuite(DatabaseTest.class);\r\n    		//suite.addTestSuite(InstrumentationTest.class);\r\n\r\n    		return suite;\r\n    	}\r\n\r\n    	@Override\r\n    	public ClassLoader getLoader() {\r\n    		return Runner.class.getClassLoader();\r\n    	}\r\n\r\n    }\r\n\r\n\r\n\r\n// JAVA : DATABASE TEST\r\npackage com.mocura.moduleseventests.test;\r\n\r\nimport android.content.Context;\r\nimport android.database.sqlite.SQLiteDatabase;\r\nimport android.database.sqlite.SQLiteException;\r\nimport android.test.AndroidTestCase;\r\nimport android.util.Log;\r\n\r\nimport com.mocura.moduleseven.helper.Helper;\r\nimport com.mocura.moduleseven.models.Hour;\r\n\r\nimport java.util.List;\r\n\r\n/**\r\n * Created by BForte on 1/16/14.\r\n */\r\n\r\n/**\r\n * This class is a unit test for the CRUD methods of the Hours class.\r\n */\r\n\r\npublic class DatabaseTest extends AndroidTestCase {\r\n\r\n	private Context mContext;\r\n	private boolean mDatabaseExists = false;\r\n	public Helper mXHelper;\r\n\r\n	/**\r\n	 * This method makes sure the database exists before trying to do any tests by creating a boolean flag, mDatabaseExists.\r\n	 *\r\n	 * @throws Exception\r\n	 */\r\n	@Override\r\n	protected void setUp() throws Exception {\r\n		super.setUp();\r\n		mContext = getContext();\r\n\r\n		mXHelper = new Helper(mContext);\r\n		mXHelper.open();\r\n\r\n		String pathToDatabase = String.valueOf(mContext.getDatabasePath("Hour"));\r\n		SQLiteDatabase checkDB = null;\r\n		// SQLiteDatabase mDatabase;\r\n		try {\r\n			checkDB = SQLiteDatabase.openDatabase(pathToDatabase, null, SQLiteDatabase.OPEN_READONLY);\r\n			checkDB.close();\r\n		}\r\n		catch (SQLiteException e) {\r\n			// database does not exist yet.\r\n		}\r\n\r\n		if (checkDB != null) {\r\n			mDatabaseExists = true;\r\n			//mDatabase = checkDB;\r\n		} else {\r\n			mDatabaseExists = false;\r\n		}\r\n	}\r\n\r\n	@Override\r\n	protected void tearDown() throws Exception {\r\n		super.tearDown();\r\n		mXHelper.close();\r\n	}\r\n\r\n	/**\r\n	 * This method tests whether an Hours object was created by verifying that the company name sent in, equals the company name that gets sent out.\r\n	 */\r\n	public void testCRUD() {\r\n		if (mDatabaseExists) {\r\n\r\n			try {\r\n\r\n				//create\r\n				Hour xhour1 = new Hour("TestCompanyName", 1.2, "1/22/2014");\r\n				mXHelper.createXHour(xhour1);\r\n\r\n				//read\r\n				List<Hour> hourList = mXHelper.readAllXHour();\r\n				Hour lastHour = hourList.get(hourList.size() - 1);\r\n				assertEquals(lastHour.getCompany(), "TestCompanyName");\r\n\r\n				//update\r\n				lastHour.setCompany("UpdatedCompany");\r\n				mXHelper.updateHours(lastHour);\r\n				List<Hour> xUpdatedHourList = mXHelper.readAllXHour();\r\n				Hour lastUpdatedHour = hourList.get(xUpdatedHourList.size() - 1);\r\n				assertEquals(lastUpdatedHour.getCompany(), "UpdatedCompany");\r\n\r\n				//delete\r\n				int countBeforeDelete = xUpdatedHourList.size() - 1;\r\n				mXHelper.deleteXHour(lastUpdatedHour);\r\n				List<Hour> xDeletedHourList = mXHelper.readAllXHour();\r\n				int countAfterDelete = xDeletedHourList.size() - 1;\r\n				assertTrue("Delete Test Failed:", countBeforeDelete - 1 == countAfterDelete);\r\n\r\n			}\r\n			catch (Exception e) {\r\n				Log.d("DEBUG", e.toString());\r\n			}\r\n\r\n		} else {\r\n			String database = "database";\r\n			assertEquals("databaseDoesNotExist", database);\r\n		}\r\n	}\r\n\r\n}\r\n\r\n\r\n// JAVA : Robotium Automation\r\npackage com.mocura.moduleseventests.test;\r\n\r\nimport android.test.ActivityInstrumentationTestCase2;\r\nimport android.widget.ListView;\r\n\r\nimport com.jayway.android.robotium.solo.Solo;\r\nimport com.mocura.moduleseven.AddWorkActivity;\r\nimport com.mocura.moduleseven.EditActivity;\r\nimport com.mocura.moduleseven.MainActivity;\r\n\r\n/**\r\n * Created by BForte on 1/20/14.\r\n */\r\n\r\n/**\r\n * This class is a test to automate the application and verify that certain tasks are completed correctly. Your instrumentationTests should not be a subpackage\r\n * -tests-; rather they should be their own directory and package entirely.\r\n */\r\n\r\npublic class InstrumentationTest extends ActivityInstrumentationTestCase2<MainActivity> {\r\n\r\n	private Solo solo;\r\n	public int countBeforeDeletion;\r\n	public int countAfterDeletion;\r\n\r\n	public InstrumentationTest() {\r\n\r\n		super(MainActivity.class);\r\n	}\r\n\r\n	public void setUp() throws Exception {\r\n		solo = new Solo(getInstrumentation(), getActivity());\r\n\r\n	}\r\n\r\n	@Override\r\n	public void tearDown() throws Exception {\r\n\r\n		solo.finishOpenedActivities();\r\n\r\n	}\r\n\r\n	public void testCreateButton1() {\r\n		solo.assertCurrentActivity("wrong activity", MainActivity.class);\r\n		solo.clickOnActionBarItem(com.mocura.moduleseven.R.id.addWork);\r\n		solo.assertCurrentActivity("on the Add Activity screen", AddWorkActivity.class);\r\n		solo.enterText(0, "Indatus");\r\n		solo.enterText(1, "22.7");\r\n		solo.enterText(2, "1/19/2014");\r\n		solo.clickOnButton("OK");\r\n		solo.sleep(1000);\r\n	}\r\n\r\n	public void testCreateButton2() {\r\n		solo.assertCurrentActivity("wrong activity", MainActivity.class);\r\n		solo.clickOnActionBarItem(com.mocura.moduleseven.R.id.addWork);\r\n		solo.assertCurrentActivity("on the Add Activity screen", AddWorkActivity.class);\r\n		solo.enterText(0, "Mocura");\r\n		solo.enterText(1, "11.02");\r\n		solo.enterText(2, "1/20/2014");\r\n		solo.clickOnButton("OK");\r\n		solo.sleep(1000);\r\n	}\r\n\r\n	public void testCreateButton3() {\r\n		solo.assertCurrentActivity("wrong activity", MainActivity.class);\r\n		solo.clickOnActionBarItem(com.mocura.moduleseven.R.id.addWork);\r\n		solo.assertCurrentActivity("on the Add Activity screen", AddWorkActivity.class);\r\n		solo.enterText(0, "Google");\r\n		solo.enterText(1, "26.54");\r\n		solo.enterText(2, "1/21/2014");\r\n		solo.clickOnButton("OK");\r\n		solo.sleep(1000);\r\n	}\r\n\r\n	public void testDelete() {\r\n		ListView list = (ListView) solo.getView(com.mocura.moduleseven.R.id.main_mHoursList);\r\n		countBeforeDeletion = list.getAdapter().getCount();\r\n		/* Long click 3 rows and Delete them */\r\n		solo.clickLongInList(countAfterDeletion);\r\n		solo.clickOnButton("Yes");\r\n		solo.clickLongInList(countAfterDeletion - 1);\r\n		solo.clickOnButton("Yes");\r\n		solo.clickLongInList(countAfterDeletion - 2);\r\n		solo.clickOnButton("Yes");\r\n		solo.sleep(3000); //wait and make sure process is completed.\r\n		/* verify that they were deleted */\r\n		int countAfterDeletionOf3Records = list.getAdapter().getCount();\r\n		if (countBeforeDeletion == countAfterDeletionOf3Records + 3) {\r\n			//force a success\r\n			solo.assertCurrentActivity("TEST-SUCCESS ", MainActivity.class);\r\n		} else {\r\n			//force a fail\r\n			solo.assertCurrentActivity("TEST-FAIL ", EditActivity.class);\r\n		}\r\n	}\r\n\r\n}\r\n\r\n// JAVA : MainActivity Test\r\npackage com.mocura.moduleseventests;\r\n\r\nimport android.test.ActivityInstrumentationTestCase2;\r\nimport android.test.suitebuilder.annotation.MediumTest;\r\n\r\nimport com.example.testx.MainActivity;\r\n\r\n/**\r\n * Created by swav on 21/08/13.\r\n */\r\npublic class MainActivityTest extends ActivityInstrumentationTestCase2<MainActivity> {\r\n	public MainActivityTest() {\r\n		super(MainActivity.class);\r\n	}\r\n\r\n	@MediumTest\r\n	public void testToFail() {\r\n		fail("nil");\r\n	}\r\n\r\n}\r\n\r\n\r\n// Run > Edit Configurations >\r\n    select the module and the runner appropriately.\r\n\r\n\r\n\r\n', '2014-03-03 21:15:04'),
(70, ' android', 'ThreadX', ' \r\n\r\n\r\n// manifest\r\n\r\n <uses-permission android:name="android.permission.INTERNET" />\r\n\r\n <activity\r\n             android:name="com.example.app.MainActivity"\r\n             android:label="@string/app_name" >\r\n\r\n         </activity>\r\n         <activity\r\n             android:name="com.example.app.ThreadPoolExecutorActivity"\r\n             android:label="@string/app_name" >\r\n\r\n         </activity>\r\n         <activity\r\n             android:name="com.example.app.FuturesAndCallablesActivity"\r\n             android:label="@string/app_name" >\r\n\r\n         </activity>\r\n         <activity\r\n             android:name="com.example.app.ConcurrencySynchronization"\r\n             android:label="@string/app_name" >\r\n             <intent-filter>\r\n                 <action android:name="android.intent.action.MAIN" />\r\n\r\n                 <category android:name="android.intent.category.LAUNCHER" />\r\n             </intent-filter>\r\n\r\n         </activity>\r\n\r\n\r\n// XML\r\n<FrameLayout xmlns:android="http://schemas.android.com/apk/res/android"\r\n    xmlns:tools="http://schemas.android.com/tools"\r\n    android:id="@+id/container"\r\n    android:layout_width="match_parent"\r\n    android:layout_height="match_parent"\r\n    tools:context="com.example.app.MainActivity"\r\n    tools:ignore="MergeRootFrame" >\r\n\r\n    <LinearLayout\r\n        android:orientation="vertical"\r\n        android:layout_width="fill_parent"\r\n        android:layout_height="fill_parent"\r\n        android:layout_gravity="center_horizontal|top">\r\n\r\n        <TextView\r\n            android:layout_width="wrap_content"\r\n            android:layout_height="wrap_content"\r\n            android:textAppearance="?android:attr/textAppearanceSmall"\r\n            android:text="This app contains 3 threading techniques.ConcurrencySynchronization,Futures and Callables, and Thread Pool Executor."\r\n            android:id="@+id/textView"\r\n            android:layout_gravity="center_horizontal" />\r\n    </LinearLayout>\r\n</FrameLayout>\r\n\r\n\r\n// JAVA\r\n\r\npublic class ConcurrencySynchronization extends Activity {\r\n\r\n    /**\r\n     * http://www.vogella.com/tutorials/JavaConcurrency/article.html#threads\r\n     *\r\n     */\r\n\r\n    /**\r\n      Locks and thread synchronization\r\n     Java provides locks to protect certain parts of the code to be executed by several threads at the same time.\r\n     The simplest way of locking a certain method or Java class is to define the method or class with the synchronized keyword.\r\n     The synchronized keyword in Java ensures:\r\n     that only a single thread can execute a block of code at the same time\r\n     that each thread entering a synchronized block of code sees the effects of all previous modifications that were guarded by the same lock\r\n     Synchronization is necessary for mutually exclusive access to blocks of and for reliable communication between threads.\r\n     You can use the synchronized keyword for the definition of a method. This would ensure that only one thread can enter this method at the same time.\r\n     Another threads which is calling this method would wait until the first threads leaves this method.\r\n\r\n     public synchronized void critial() {\r\n        // some thread critical stuff\r\n        // here\r\n     }\r\n\r\n     You can also use the synchronized keyword to protect blocks of code within a method. This block is guarded by a key, which can be either a string or an object.\r\n     This key is called the lock. All code which is protected by the same lock can only be executed by one thread at the same time\r\n     For example the following datastructure will ensure that only one thread can access the inner block of the add() and next() methods.\r\n\r\n     */\r\n\r\n    @Override\r\n    protected void onCreate(Bundle savedInstanceState) {\r\n        super.onCreate(savedInstanceState);\r\n        setContentView(R.layout.activity_main);\r\n\r\n        CrawledSites crawled = new CrawledSites();\r\n        crawled.add("http://www.forteworks.com");\r\n        crawled.next();\r\n\r\n\r\n\r\n    }\r\n\r\n\r\n\r\n\r\n\r\n}\r\n\r\n\r\n// JAVA\r\n\r\n\r\npublic class CrawledSites {\r\n    private List<String> crawledSites = new ArrayList<String>();\r\n    private List<String> linkedSites = new ArrayList<String>();\r\n\r\n    public void add(String site) {\r\n        synchronized (this) {\r\n            if (!crawledSites.contains(site)) {\r\n                linkedSites.add(site);\r\n            }\r\n        }\r\n    }\r\n\r\n\r\n    /**\r\n     * Get next site to crawl. Can return null (if nothing to crawl)\r\n     */\r\n\r\n    public String next() {\r\n        if (linkedSites.size() == 0) {\r\n            return null;\r\n        }\r\n        synchronized (this) {\r\n            // Need to check again if size has changed\r\n            if (linkedSites.size() > 0) {\r\n                String s = linkedSites.get(0);\r\n                linkedSites.remove(0);\r\n                crawledSites.add(s);\r\n                Log.d("DEBUG",">crawled through: "+ s);\r\n\r\n                return s;\r\n            }\r\n            return null;\r\n        }\r\n    }\r\n\r\n\r\n}\r\n\r\n// JAVA\r\n\r\npublic class FuturesAndCallablesActivity extends Activity {\r\n\r\n\r\n    private static final int NTHREDS = 10;\r\n\r\n    /**\r\n     * Futures and Callables\r\n     The code examples for this section are created in a Java project called de.vogella.concurrency.callables.\r\n     The executor framework presented in the last chapter works with Runnables. Runnable do not return result.\r\n     In case you expect your threads to return a computed result you can use java.util.concurrent.Callable. The Callable object allows to return values after completion.\r\n     The Callable object uses generics to define the type of object which is returned.\r\n     If you submit a Callable object to an Executor the framework returns an object of type java.util.concurrent.Future.\r\n     This Future object can be used to check the status of a Callable and to retrieve the result from the Callable.\r\n     On the Executor you can use the method submit to submit a Callable and to get a future. To retrieve the result of the future use the get() method.\r\n     *\r\n     */\r\n\r\n\r\n    @Override\r\n    protected void onCreate(Bundle savedInstanceState) {\r\n        super.onCreate(savedInstanceState);\r\n        setContentView(R.layout.activity_main);\r\n\r\n       // LEFT OFF HERE ----\r\n        ExecutorService executor = Executors.newFixedThreadPool(NTHREDS);\r\n        List<Future<Long>> list = new ArrayList<Future<Long>>();\r\n        for (int i = 0; i < 20000; i++) {\r\n            Callable<Long> worker = new MyCallable();\r\n            Future<Long> submit = executor.submit(worker);\r\n            list.add(submit);\r\n        }\r\n        long sum = 0;\r\n        System.out.println(list.size());\r\n        // now retrieve the result\r\n        for (Future<Long> future : list) {\r\n            try {\r\n                sum += future.get();\r\n                Log.d("DEBUG","sum :"+ sum + " future.get():"+ future.get());\r\n            } catch (InterruptedException e) {\r\n                e.printStackTrace();\r\n            } catch (ExecutionException e) {\r\n                e.printStackTrace();\r\n            }\r\n        }\r\n        System.out.println(sum);\r\n        executor.shutdown();\r\n\r\n    }\r\n\r\n\r\n    public class MyCallable implements Callable<Long> {\r\n        @Override\r\n        public Long call() throws Exception {\r\n            long sum = 0;\r\n            for (long i = 0; i <= 100; i++) {\r\n                sum += i;\r\n            }\r\n            return sum;\r\n        }\r\n\r\n    }\r\n\r\n}\r\n\r\n\r\n\r\n//JAVA\r\n\r\npublic class ThreadPoolExecutorActivity extends Activity {\r\n\r\n\r\n    /**\r\n     Threads pools with the Executor Framework\r\n     You find this examples in the source section in Java project called de.vogella.concurrency.threadpools.\r\n     Thread pools manage a pool of worker threads. The thread pools contains a work queue which holds tasks waiting to get executed.\r\n     A thread pool can be described as a collection of Runnable objects (work queue) and a connections of running threads.\r\n     These threads are constantly running and are checking the work query for new work.\r\n     If there is new work to be done they execute this Runnable.\r\n     The Thread class itself provides a method, e.g. execute(Runnable r) to add a new Runnable object to the work queue.\r\n     The Executor framework provides example implementation of the java.util.concurrent.Executor interface,\r\n     e.g. Executors.newFixedThreadPool(int n) which will create n worker threads.\r\n     The ExecutorService adds life cycle methods to the Executor,\r\n     which allows to shutdown the Executor and to wait for termination.\r\n     */\r\n\r\n    private static final int NTHREDS = 10;\r\n\r\n    @Override\r\n    protected void onCreate(Bundle savedInstanceState) {\r\n        super.onCreate(savedInstanceState);\r\n        setContentView(R.layout.activity_main);\r\n\r\n\r\n        ExecutorService executor = Executors.newFixedThreadPool(NTHREDS);\r\n        for (int i = 0; i < 500; i++) {\r\n            Runnable worker = new ThreadPoolRunnable(10000000L + i);\r\n            executor.execute(worker);\r\n        }\r\n        // This will make the executor accept no new threads\r\n        // and finish all existing threads in the queue\r\n        executor.shutdown();\r\n        // Wait until all threads are finish\r\n        //----->PROBLEM HERE:  executor.awaitTermination();\r\n        System.out.println("Finished all threads");\r\n        Log.d("DEBUG","Finished ALl Threads, maybe");\r\n\r\n\r\n    }\r\n\r\n    public class ThreadPoolRunnable  implements Runnable {\r\n        private final long countUntil;\r\n\r\n        ThreadPoolRunnable(long countUntil) {\r\n            this.countUntil = countUntil;\r\n        }\r\n\r\n        @Override\r\n        public void run() {\r\n            long sum = 0;\r\n            for (long i = 1; i < countUntil; i++) {\r\n                sum += i;\r\n            }\r\n            System.out.println(sum);\r\n            Log.d("DEBUG","sum:"+ sum);\r\n        }\r\n    }\r\n\r\n\r\n}\r\n\r\n', '2014-03-03 21:18:58'),
(71, ' android', 'VideoX', '\r\n\r\n// manifest\r\n <!-- This app records A/V content from camera and stores it to disk -->\r\n    <uses-permission android:name="android.permission.WRITE_EXTERNAL_STORAGE" />\r\n    <uses-permission android:name="android.permission.RECORD_VIDEO" />\r\n    <uses-permission android:name="android.permission.RECORD_AUDIO"/>\r\n    <uses-permission android:name="android.permission.CAMERA" />\r\n    <uses-feature android:name="android.hardware.camera" />\r\n\r\n\r\n\r\n    // XML\r\n    sample_main.xml\r\n    <LinearLayout xmlns:android="http://schemas.android.com/apk/res/android"\r\n        xmlns:tools="http://schemas.android.com/tools"\r\n        android:layout_width="match_parent"\r\n        android:layout_height="match_parent"\r\n        android:paddingLeft="@dimen/activity_horizontal_margin"\r\n        android:paddingRight="@dimen/activity_horizontal_margin"\r\n        android:paddingTop="@dimen/activity_vertical_margin"\r\n        android:paddingBottom="@dimen/activity_vertical_margin"\r\n        android:orientation="vertical"\r\n        tools:context=".MainActivity">\r\n\r\n\r\n        <LinearLayout\r\n            android:orientation="horizontal"\r\n            android:layout_width="wrap_content"\r\n            android:layout_height="wrap_content"\r\n            android:layout_marginTop = "20dp"\r\n            android:layout_gravity="center_horizontal"\r\n            >\r\n        <TextureView\r\n            android:layout_width="200dp"\r\n            android:layout_height="200dp"\r\n            android:id="@+id/surface_view"\r\n            android:layout_gravity="center_horizontal"\r\n             />\r\n\r\n        <VideoView\r\n            android:layout_width="110dp"\r\n            android:layout_height="110dp"\r\n            android:layout_marginTop = "10dp"\r\n            android:layout_marginLeft = "10dp"\r\n            android:layout_gravity="center_horizontal"\r\n            android:id="@+id/videoView"\r\n             />\r\n        </LinearLayout>\r\n\r\n        <LinearLayout\r\n            android:orientation="horizontal"\r\n            android:layout_width="wrap_content"\r\n            android:layout_height="wrap_content"\r\n            android:layout_marginTop = "20dp"\r\n            android:layout_gravity="center_horizontal|bottom"\r\n            >\r\n            <Button\r\n                android:layout_width="100dp"\r\n                android:layout_height="wrap_content"\r\n                android:id="@+id/button_capture"\r\n                android:layout_gravity="left"\r\n                android:onClick="onCaptureClick"\r\n                android:text="capture"/>\r\n            <Button\r\n                android:layout_width="174dp"\r\n                android:layout_height="wrap_content"\r\n                android:id="@+id/button_play"\r\n                android:layout_gravity="right"\r\n                android:onClick="onPlayClick"\r\n                android:text="play"/>\r\n            </LinearLayout>\r\n    </LinearLayout>\r\n\r\n\r\n\r\n// JAVA\r\n\r\npublic class CameraHelper {\r\n\r\n    public static final int MEDIA_TYPE_IMAGE = 1;\r\n    public static final int MEDIA_TYPE_VIDEO = 2;\r\n\r\n    /**\r\n     * Iterate over supported camera preview sizes to see which one best fits the\r\n     * dimensions of the given view while maintaining the aspect ratio. If none can,\r\n     * be lenient with the aspect ratio.\r\n     *\r\n     * @param sizes Supported camera preview sizes.\r\n     * @param w The width of the view.\r\n     * @param h The height of the view.\r\n     * @return Best match camera preview size to fit in the view.\r\n     */\r\n    public static  Camera.Size getOptimalPreviewSize(List<Camera.Size> sizes, int w, int h) {\r\n        // Use a very small tolerance because we want an exact match.\r\n        final double ASPECT_TOLERANCE = 0.1;\r\n        double targetRatio = (double) w / h;\r\n        if (sizes == null)\r\n            return null;\r\n\r\n        Camera.Size optimalSize = null;\r\n\r\n        // Start with max value and refine as we iterate over available preview sizes. This is the\r\n        // minimum difference between view and camera height.\r\n        double minDiff = Double.MAX_VALUE;\r\n\r\n        // Target view height\r\n        int targetHeight = h;\r\n\r\n        // Try to find a preview size that matches aspect ratio and the target view size.\r\n        // Iterate over all available sizes and pick the largest size that can fit in the view and\r\n        // still maintain the aspect ratio.\r\n        for (Camera.Size size : sizes) {\r\n            double ratio = (double) size.width / size.height;\r\n            if (Math.abs(ratio - targetRatio) > ASPECT_TOLERANCE)\r\n                continue;\r\n            if (Math.abs(size.height - targetHeight) < minDiff) {\r\n                optimalSize = size;\r\n                minDiff = Math.abs(size.height - targetHeight);\r\n            }\r\n        }\r\n\r\n        // Cannot find preview size that matches the aspect ratio, ignore the requirement\r\n        if (optimalSize == null) {\r\n            minDiff = Double.MAX_VALUE;\r\n            for (Camera.Size size : sizes) {\r\n                if (Math.abs(size.height - targetHeight) < minDiff) {\r\n                    optimalSize = size;\r\n                    minDiff = Math.abs(size.height - targetHeight);\r\n                }\r\n            }\r\n        }\r\n        return optimalSize;\r\n    }\r\n\r\n    /**\r\n     * @return the default camera on the device. Return null if there is no camera on the device.\r\n     */\r\n    public static Camera getDefaultCameraInstance() {\r\n        return Camera.open();\r\n    }\r\n\r\n\r\n    /**\r\n     * @return the default rear/back facing camera on the device. Returns null if camera is not\r\n     * available.\r\n     */\r\n    public static Camera getDefaultBackFacingCameraInstance() {\r\n        return getDefaultCamera(Camera.CameraInfo.CAMERA_FACING_BACK);\r\n    }\r\n\r\n    /**\r\n     * @return the default front facing camera on the device. Returns null if camera is not\r\n     * available.\r\n     */\r\n    public static Camera getDefaultFrontFacingCameraInstance() {\r\n        return getDefaultCamera(Camera.CameraInfo.CAMERA_FACING_FRONT);\r\n    }\r\n\r\n\r\n    /**\r\n     *\r\n     * @param position Physical position of the camera i.e Camera.CameraInfo.CAMERA_FACING_FRONT\r\n     *                 or Camera.CameraInfo.CAMERA_FACING_BACK.\r\n     * @return the default camera on the device. Returns null if camera is not available.\r\n     */\r\n    @TargetApi(Build.VERSION_CODES.GINGERBREAD)\r\n    private static Camera getDefaultCamera(int position) {\r\n        // Find the total number of cameras available\r\n        int  mNumberOfCameras = Camera.getNumberOfCameras();\r\n\r\n        // Find the ID of the back-facing ("default") camera\r\n        Camera.CameraInfo cameraInfo = new Camera.CameraInfo();\r\n        for (int i = 0; i < mNumberOfCameras; i++) {\r\n            Camera.getCameraInfo(i, cameraInfo);\r\n            if (cameraInfo.facing == position) {\r\n                return Camera.open(i);\r\n\r\n            }\r\n        }\r\n\r\n        return null;\r\n    }\r\n\r\n    /**\r\n     * Creates a media file in the {@code Environment.DIRECTORY_PICTURES} directory. The directory\r\n     * is persistent and available to other applications like gallery.\r\n     *\r\n     * @param type Media type. Can be video or image.\r\n     * @return A file object pointing to the newly created file.\r\n     */\r\n    public  static File getOutputMediaFile(int type){\r\n        // To be safe, you should check that the SDCard is mounted\r\n        // using Environment.getExternalStorageState() before doing this.\r\n        if (!Environment.getExternalStorageState().equalsIgnoreCase(Environment.MEDIA_MOUNTED)) {\r\n            return  null;\r\n        }\r\n\r\n        File mediaStorageDir = new File(Environment.getExternalStoragePublicDirectory(\r\n                Environment.DIRECTORY_PICTURES), "CameraSample");\r\n        // This location works best if you want the created images to be shared\r\n        // between applications and persist after your app has been uninstalled.\r\n\r\n        // Create the storage directory if it does not exist\r\n        if (! mediaStorageDir.exists()){\r\n            if (! mediaStorageDir.mkdirs()) {\r\n                Log.d("CameraSample", "failed to create directory");\r\n                return null;\r\n            }\r\n        }\r\n\r\n        // Create a media file name\r\n        String timeStamp = new SimpleDateFormat("yyyyMMdd_HHmmss").format(new Date());\r\n        File mediaFile;\r\n        if (type == MEDIA_TYPE_IMAGE){\r\n            mediaFile = new File(mediaStorageDir.getPath() + File.separator +\r\n                    "IMG_"+ timeStamp + ".jpg");\r\n        } else if(type == MEDIA_TYPE_VIDEO) {\r\n            mediaFile = new File(mediaStorageDir.getPath() + File.separator +\r\n                    "VID_"+ timeStamp + ".mp4");\r\n        } else {\r\n            return null;\r\n        }\r\n\r\n        return mediaFile;\r\n    }\r\n\r\n}\r\n\r\n\r\n// JAVA\r\n\r\n\r\npublic class MainActivity extends Activity {\r\n\r\n	private Camera mCamera;\r\n	private TextureView mPreview;\r\n	private MediaRecorder mMediaRecorder;\r\n\r\n	private boolean isRecording = false;\r\n	private static final String TAG = "Recorder";\r\n	private Button captureButton;\r\n\r\n	private String outputFile;\r\n\r\n	@Override\r\n	protected void onCreate(Bundle savedInstanceState) {\r\n		super.onCreate(savedInstanceState);\r\n        //\r\n        requestWindowFeature(Window.FEATURE_NO_TITLE);\r\n        getWindow().setFlags(WindowManager.LayoutParams.FLAG_FULLSCREEN,\r\n                WindowManager.LayoutParams.FLAG_FULLSCREEN);\r\n        setRequestedOrientation(ActivityInfo.SCREEN_ORIENTATION_LANDSCAPE);\r\n        //\r\n\r\n		setContentView(R.layout.sample_main);\r\n\r\n		mPreview = (TextureView) findViewById(R.id.surface_view);\r\n		captureButton = (Button) findViewById(R.id.button_capture);\r\n	}\r\n\r\n	public void onCaptureClick(View view) {\r\n		if (isRecording) {\r\n			mMediaRecorder.stop();\r\n            //not here mMediaRecorder.setOrientationHint(90);\r\n            // change to this in manifest ? android:screenOrientation="landscape"\r\n			releaseMediaRecorder();\r\n			mCamera.lock(); // take camera access back from MediaRecorder\r\n			setCaptureButtonText("Capture");\r\n			isRecording = false;\r\n			releaseCamera();\r\n\r\n		} else {\r\n\r\n			new MediaPrepareTask().execute(null, null, null);\r\n\r\n		}\r\n	}\r\n\r\n	public void onPlayClick(View view) {\r\n		Uri uri = Uri.parse(outputFile);\r\n		VideoView mVideoView = (VideoView) findViewById(R.id.videoView);\r\n		mVideoView.setMediaController(new MediaController(this));\r\n		mVideoView.setVideoURI(uri);\r\n		mVideoView.requestFocus();\r\n		mVideoView.start();\r\n	}\r\n\r\n	private void setCaptureButtonText(String title) {\r\n		captureButton.setText(title);\r\n	}\r\n\r\n	@Override\r\n	protected void onPause() {\r\n		super.onPause();\r\n		releaseMediaRecorder();// if we are using MediaRecorder, release it first\r\n		releaseCamera();// release the camera immediately on pause event\r\n	}\r\n\r\n	private void releaseMediaRecorder() {\r\n		if (mMediaRecorder != null) {\r\n			mMediaRecorder.reset();\r\n			mMediaRecorder.release();\r\n			mMediaRecorder = null;\r\n			// Lock camera for later use i.e taking it back from MediaRecorder.\r\n			// MediaRecorder does not need it anymore and we will release it if the activity pauses.\r\n			mCamera.lock();\r\n		}\r\n	}\r\n\r\n	private void releaseCamera() {\r\n		if (mCamera != null) {\r\n			// release the camera for other applications\r\n			mCamera.release();\r\n			mCamera = null;\r\n		}\r\n	}\r\n\r\n	@TargetApi(Build.VERSION_CODES.HONEYCOMB)\r\n	private boolean prepareVideoRecorder() {\r\n		mCamera = CameraHelper.getDefaultCameraInstance();\r\n		// We need to make sure that our preview and recording video size are supported by the\r\n		// camera. Query camera to find all the sizes and choose the optimal size given the\r\n		// dimensions of our preview surface.\r\n		Camera.Parameters parameters = mCamera.getParameters();\r\n		List<Camera.Size> mSupportedPreviewSizes = parameters.getSupportedPreviewSizes();\r\n		Camera.Size optimalSize = CameraHelper.getOptimalPreviewSize(mSupportedPreviewSizes, mPreview.getWidth(), mPreview.getHeight());\r\n		// Use the same size for recording profile.\r\n		CamcorderProfile profile = CamcorderProfile.get(CamcorderProfile.QUALITY_HIGH);\r\n		profile.videoFrameWidth = optimalSize.width;\r\n		profile.videoFrameHeight = optimalSize.height;\r\n		// likewise for the camera object itself.\r\n		parameters.setPreviewSize(profile.videoFrameWidth, profile.videoFrameHeight);\r\n		mCamera.setParameters(parameters);\r\n		try {\r\n			mCamera.setPreviewTexture(mPreview.getSurfaceTexture());\r\n		}\r\n		catch (IOException e) {\r\n			Log.e(TAG, "Surface texture is unavailable or unsuitable" + e.getMessage());\r\n			return false;\r\n		}\r\n\r\n		mMediaRecorder = new MediaRecorder();\r\n		mCamera.unlock();\r\n       // causes a crash !? mCamera.setDisplayOrientation(90);\r\n\r\n		mMediaRecorder.setCamera(mCamera);\r\n\r\n        //NOT HERE->\r\n        //\r\n        //mMediaRecorder.setOrientationHint(90);\r\n		mMediaRecorder.setAudioSource(MediaRecorder.AudioSource.DEFAULT);\r\n		mMediaRecorder.setVideoSource(MediaRecorder.VideoSource.CAMERA);\r\n        //mMediaRecorder.setOrientationHint(90);\r\n\r\n		mMediaRecorder.setProfile(profile);\r\n		mMediaRecorder.setOutputFile(CameraHelper.getOutputMediaFile(CameraHelper.MEDIA_TYPE_VIDEO).toString());\r\n		Log.d("DEBUG: mMediaRecorder.setOutputFile ", CameraHelper.getOutputMediaFile(CameraHelper.MEDIA_TYPE_VIDEO).toString());\r\n		outputFile = CameraHelper.getOutputMediaFile(CameraHelper.MEDIA_TYPE_VIDEO).toString();\r\n		try {\r\n			mMediaRecorder.prepare();\r\n		}\r\n		catch (IllegalStateException e) {\r\n			Log.d(TAG, "IllegalStateException preparing MediaRecorder: " + e.getMessage());\r\n			releaseMediaRecorder();\r\n			return false;\r\n		}\r\n		catch (IOException e) {\r\n			Log.d(TAG, "IOException preparing MediaRecorder: " + e.getMessage());\r\n			releaseMediaRecorder();\r\n			return false;\r\n		}\r\n		return true;\r\n	}\r\n\r\n	/**\r\n	 * Asynchronous task for preparing the {@link android.media.MediaRecorder} since it is a long blocking operation.\r\n	 */\r\n	class MediaPrepareTask extends AsyncTask<Void, Void, Boolean> {\r\n		@Override\r\n		protected Boolean doInBackground(Void... voids) {\r\n			if (prepareVideoRecorder()) {\r\n				mMediaRecorder.start();\r\n                //NOT HERE mMediaRecorder.setOrientationHint(90);\r\n				isRecording = true;\r\n			} else {\r\n				releaseMediaRecorder();\r\n				return false;\r\n			}\r\n			return true;\r\n		}\r\n\r\n		@Override\r\n		protected void onPostExecute(Boolean result) {\r\n			if (!result) {\r\n				MainActivity.this.finish();\r\n			}\r\n			setCaptureButtonText("Stop");\r\n		}\r\n	}\r\n\r\n}\r\n\r\n', '2014-03-03 21:27:33'),
(72, ' android', 'PopUpX', ' //popupX\r\n\r\n//XML\r\n<FrameLayout xmlns:android="http://schemas.android.com/apk/res/android"\r\n    xmlns:tools="http://schemas.android.com/tools"\r\n    android:id="@+id/container"\r\n    android:layout_width="match_parent"\r\n    android:layout_height="match_parent"\r\n    tools:context="com.example.areyousurex.MainActivity"\r\n    tools:ignore="MergeRootFrame" >\r\n\r\n    <Button\r\n        android:layout_width="wrap_content"\r\n        android:layout_height="wrap_content"\r\n        android:text="Delete"\r\n        android:id="@+id/mBtnDelete"\r\n        android:layout_gravity="left|top" />\r\n</FrameLayout>\r\n\r\n\r\n// JAVA : main activity\r\n\r\n\r\nimport android.app.Activity;\r\nimport android.app.AlertDialog;\r\nimport android.content.DialogInterface;\r\nimport android.os.Bundle;\r\nimport android.util.Log;\r\nimport android.view.View;\r\nimport android.widget.Button;\r\n\r\nimport utility.CancelOnClickListener;\r\n\r\npublic class MainActivity extends Activity implements View.OnClickListener {\r\n\r\n	Button mBtnDelete;\r\n\r\n	@Override\r\n	protected void onCreate(Bundle savedInstanceState) {\r\n		super.onCreate(savedInstanceState);\r\n		setContentView(R.layout.activity_main);\r\n		mBtnDelete = (Button) findViewById(R.id.mBtnDelete);\r\n		mBtnDelete.setOnClickListener((View.OnClickListener) this);\r\n\r\n	}\r\n\r\n	/**\r\n	 * This method is to confirm the users intention of deleting a record.\r\n	 */\r\n	public void areYouSureDialog() {\r\n		AlertDialog.Builder areYouSureDialog = new AlertDialog.Builder(MainActivity.this);\r\n		areYouSureDialog.setTitle("Delete Item");\r\n		areYouSureDialog.setMessage("Are you sure you want to delete this item?");\r\n		areYouSureDialog.setCancelable(true);\r\n		areYouSureDialog.setPositiveButton("Yes", new DialogInterface.OnClickListener() {\r\n			public void onClick(DialogInterface dialog, int which) {\r\n				deleteItem();\r\n			}\r\n		});\r\n		areYouSureDialog.setNegativeButton("No", new CancelOnClickListener(MainActivity.this));\r\n		areYouSureDialog.show();\r\n	}\r\n\r\n	public void deleteItem() {\r\n		Log.d("DEBUG", "There is nothing to delete");\r\n	}\r\n\r\n	@Override\r\n	public void onClick(View v) {\r\n		areYouSureDialog();\r\n	}\r\n}\r\n\r\n\r\n// JAVA : UTILITY CLASSES\r\n// JAVA AreYouSureDialog\r\n\r\n\r\nimport android.app.AlertDialog;\r\nimport android.content.Context;\r\nimport android.content.DialogInterface;\r\n\r\nimport com.example.areyousurex.MainActivity;\r\n\r\n/**\r\n * Created by BForte on 2/6/14.\r\n */\r\npublic class AreYouSureDialog extends AlertDialog.Builder {\r\n\r\n    private Context context;\r\n    private AlertDialog.Builder areYouSureDialog;\r\n\r\n    public AreYouSureDialog(Context context) {\r\n        super(context);\r\n        areYouSureDialog = new AlertDialog.Builder(context);\r\n        areYouSureDialog.setTitle("Delete Item");\r\n        areYouSureDialog.setMessage("Are you sure you want to delete this item?");\r\n        areYouSureDialog.setCancelable(true);\r\n        areYouSureDialog.setPositiveButton("Yes", new DialogInterface.OnClickListener() {\r\n            public void onClick(DialogInterface dialog, int which) {\r\n                new MainActivity().deleteItem();\r\n            }\r\n        });\r\n        areYouSureDialog.setNegativeButton("No", new CancelOnClickListener(context));\r\n        areYouSureDialog.show();\r\n    }\r\n}\r\n\r\n\r\n// JAVA : CancelOnClickListener :\r\n\r\nimport android.content.Context;\r\nimport android.content.DialogInterface;\r\nimport android.widget.Toast;\r\n\r\n/**\r\n * Created by BForte on 2/6/14.\r\n */\r\npublic class CancelOnClickListener implements DialogInterface.OnClickListener {\r\n	private Context context;\r\n	private String message;\r\n\r\n    public CancelOnClickListener(Context context) {\r\n        this.context = context;\r\n         this.message = "continue";\r\n    }\r\n\r\n	public CancelOnClickListener(Context context, String message) {\r\n		this.context = context;\r\n		this.message = message;\r\n	}\r\n\r\n	public void onClick(DialogInterface dialog, int which) {\r\n		Toast.makeText(context, message, Toast.LENGTH_LONG).show();\r\n	}\r\n}\r\n\r\n\r\n', '2014-03-03 21:30:40'),
(73, ' iOS', ' TwilioX', ' \r\n// Twilio\r\n\r\n// APP SET UP\r\n\r\n-Add the static libraries (files ending in .a) in the ${TWILIO_CLIENT_HOME}/Libraries/ folder to your project.\r\n-Add the ${TWILIO_CLIENT_HOME}/Headers/ folder to "Header Search Paths" in your target''s Build Settings.\r\n\r\nAdd the following iOS SDK frameworks to your project:\r\n-AudioToolbox.framework\r\n-AVFoundation.framework\r\n-CFNetwork.framework\r\n-SystemConfiguration.framework\r\n\r\nAdd -ObjC -all_load to "Other Linker Flags" in your target''s Build Settings.\r\n\r\n\r\n// appdelegate.h\r\n\r\n@class TwilioObject;\r\n\r\n@interface FBFAppDelegate : UIResponder <UIApplicationDelegate>\r\n{\r\n\r\n	TwilioObject *_twilioObject;\r\n}\r\n\r\n@property (nonatomic, retain) TwilioObject *twilioObject;\r\n\r\n@end\r\n\r\n\r\n// appdelegate.m\r\n#import "TwilioObject.h"\r\n\r\n@synthesize twilioObject = _twilioObject;\r\n\r\n- (BOOL)application:(UIApplication *)application didFinishLaunchingWithOptions:(NSDictionary *)launchOptions {\r\n	self.twilioObject = [[TwilioObject alloc] init];\r\n	return YES;\r\n}\r\n\r\n// VC.h\r\n\r\n#import <UIKit/UIKit.h>\r\n#import "AppDelegate.h"\r\n#import "TwilioObject.h"\r\n\r\n@interface ViewController : UIViewController\r\n{\r\n	UIButton *_dialButton;\r\n	UIButton *_hangupButton;\r\n	UITextField *_numberField;\r\n    NSString * _recordedFile;\r\n}\r\n\r\n@property (nonatomic, retain) IBOutlet UIButton *dialButton;\r\n@property (nonatomic, retain) IBOutlet UIButton *hangupButton;\r\n@property (nonatomic, retain) IBOutlet UITextField *numberField;\r\n@property (nonatomic, retain) IBOutlet NSString *recordedFile;\r\n\r\n- (IBAction)dialButtonPressed:(id)sender;\r\n- (IBAction)hangupButtonPressed:(id)sender;\r\n\r\n@end\r\n\r\n\r\n// VC.m\r\n\r\n#import "ViewController.h"\r\n\r\n@interface ViewController ()\r\n\r\n@end\r\n\r\n@implementation ViewController\r\n\r\n@synthesize dialButton = _dialButton;\r\n@synthesize hangupButton = _hangupButton;\r\n@synthesize numberField = _numberField;\r\n@synthesize recordedFile = _recordedFile;\r\n\r\n\r\n- (void)viewDidUnload {\r\n	self.dialButton = nil;\r\n	self.hangupButton = nil;\r\n	self.numberField = nil;\r\n    \r\n	[super viewDidUnload];\r\n}\r\n\r\n- (IBAction)dialButtonPressed:(id)sender {\r\n	FBFAppDelegate *appDelegate = (FBFAppDelegate *)[UIApplication sharedApplication].delegate;\r\n	TwilioObject *twilioObject = appDelegate.twilioObject;\r\n	// THIS WORKS:: [twilioObject connect:self.numberField.text];\r\n    [twilioObject ticket:self.numberField.text recordingFilePath:@"http://www.villagegeek.com/downloads/webwavs/affirmative.wav"];\r\n}\r\n\r\n- (IBAction)hangupButtonPressed:(id)sender {\r\n	FBFAppDelegate *appDelegate = (FBFAppDelegate *)[UIApplication sharedApplication].delegate;\r\n	TwilioObject *twilioObject = appDelegate.twilioObject;\r\n	[twilioObject disconnect];\r\n}\r\n\r\n\r\n@end\r\n\r\n\r\n// SERVER SIDE:\r\ngo to your downloaded twilio > Helper Libs > php > Services\r\ninstall Services direcory on your server\r\n\r\n\r\n// PHP auth.php\r\n<?php\r\n    \r\n    include "Services/Twilio/Capability.php";\r\n    \r\n    $accountSid = "AC287f34fb31874ee272ef858fa1636721";\r\n    //account SID\r\n    $authToken = "f9ce6a206bf5ba343e87051f846d4563";\r\n    // TOKEN\r\n    // The app outgoing connections will use:\r\n    $appSid = "AP0abf58d924997f07e4f34a02ead19bde";\r\n    // the app id created in twilio.\r\n    \r\n    // The client name for incoming connections:\r\n    $clientName = "monkey";\r\n    \r\n    $capability = new Services_Twilio_Capability($accountSid, $authToken);\r\n    \r\n    // This allows incoming connections as $clientName:\r\n    $capability -> allowClientIncoming($clientName);\r\n    \r\n    // This allows outgoing connections to $appSid with the "From"\r\n    // parameter being the value of $clientName\r\n    $capability -> allowClientOutgoing($appSid, array(), $clientName);\r\n    \r\n    // This returns a token to use with Twilio based on\r\n    // the account and capabilities defined above\r\n    $token = $capability -> generateToken();\r\n    echo $token;\r\n    ?>\r\n    \r\n\r\n// PHP hello-client-monkey-twiml.php\r\n<?php header(''Content-type: text/xml'');\r\n    // put a phone number you''ve verified with Twilio to use as a caller ID number:w\r\n    // the  Twilio  phone number associated with app\r\n    $callerId = "5022526231";\r\n    ?>\r\n\r\n<!-- DIAL NUMBER\r\n<Response>\r\n<Dial callerId="<?php echo $callerId ?>">\r\n<Number><?php echo $_REQUEST["PhoneNumber"]; ?></Number>\r\n</Dial>\r\n</Response>\r\n\r\n-->\r\n\r\n<!-- Say Zendesk Ticket Number -->\r\n<Response>\r\n<Say voice="woman" language="en-US">ZenDesk Ticket Number <?php echo $_REQUEST["TicketNumber"]; ?></Say>\r\n<Play><?php echo $_REQUEST["RecordedFile"]; ?></Play> <!-- -->\r\n</Response>\r\n\r\n\r\n\r\n//  GENERAL INFO\r\n\r\nMy Personal Twilio Trial Account\r\n\r\nhttps://www.twilio.com/user/account\r\n personal twilio info:\r\n account sid: AC287f34fb31874ee272ef858fa1636721\r\n token: f9ce6a206bf5ba343e87051f846d4563\r\n app sid: AP0abf58d924997f07e4f34a02ead19bde\r\n phone #: 502 252 6231\r\n', '2014-03-05 19:44:11');
