(83, ' iOS', 'DP Observer', ' \r\n//ObserverPattern\r\n\r\n// VC.h\r\n\r\n#import <UIKit/UIKit.h>\r\n\r\n@interface FBFViewController : UIViewController\r\n\r\n@end\r\n\r\n\r\n//VC.m\r\n\r\n#import "FBFViewController.h"\r\n\r\n\r\n#import "Subject.h"\r\n#import "Observer.h"\r\n#import "ObserverX.h"\r\n\r\n\r\n@interface FBFViewController ()\r\n\r\n@end\r\n\r\n@implementation FBFViewController\r\n\r\n- (void)viewDidLoad\r\n{\r\n    [super viewDidLoad];\r\n    \r\n    Subject *sub = [[Subject alloc]init];\r\n    Observer *obs = [[Observer alloc]init];\r\n    ObserverX *obsX = [[ObserverX alloc]init];\r\n    \r\n    [sub attach:obs];\r\n    [sub attach:obsX];\r\n    \r\n    [sub postMessage:@"foo"];\r\n    [sub postMessage:@"gone"];\r\n    // DUPLICATE ARE PREVENTED: [sub attach:obsX];\r\n    // Works[sub remove:obsX];\r\n    [sub postMessage:@"wild"];\r\n}\r\n\r\n- (void)didReceiveMemoryWarning\r\n{\r\n    [super didReceiveMemoryWarning];\r\n}\r\n\r\n@end\r\n\r\n// Subject.h\r\n#import <Foundation/Foundation.h>\r\n\r\n@interface Subject : NSObject\r\n\r\n@property (nonatomic, retain) NSMutableArray *observers;\r\n\r\n- (void)postMessage:(NSString *)message;\r\n- (void)attach:(id)sender;\r\n- (void)remove:(id)sender;\r\n\r\n@end\r\n\r\n\r\n//Subject.m\r\n\r\n#import "Subject.h"\r\n#import "ObserverInterface.h"\r\n\r\n@implementation Subject\r\n@synthesize observers;\r\n- (id)init\r\n{\r\n    self = [super init];\r\n    if (self) {\r\n        observers = [[NSMutableArray alloc]init];\r\n    }\r\n    return self;\r\n}\r\n-(void)attach:(id)sender{\r\n     ObserverInterface *obi = (ObserverInterface*)sender;\r\n    if(![observers containsObject:obi])\r\n    {\r\n    [observers addObject:obi];\r\n     NSLog(@"\\n ATTACHING observer named: %@",obi.name);\r\n    }else{\r\n        NSLog(@"ALREADY AN OBSERVER");\r\n    }\r\n}\r\n-(void)remove:(id)sender{\r\n    ObserverInterface *obi = (ObserverInterface*)sender;\r\n    [observers removeObject:obi];\r\n}\r\n-(void)postMessage:(NSString *)message{\r\n     for(ObserverInterface * obi in observers){\r\n         [obi update:message];\r\n    }\r\n}\r\n\r\n@end\r\n\r\n\r\n//ObserverInterface.h\r\n#import <Foundation/Foundation.h>\r\n\r\n@interface ObserverInterface : NSObject\r\n{\r\n	NSString *name;\r\n}\r\n\r\n@property (nonatomic, retain) NSString *name;\r\n\r\n- (void)update:(NSString *)message;\r\n@end\r\n\r\n\r\n//ObserverInterface.m\r\n#import "ObserverInterface.h"\r\n\r\n@implementation ObserverInterface\r\n@synthesize name;\r\n- (id)init\r\n{\r\n    self = [super init];\r\n    if (self) {\r\n        self.name = @"unnamed";\r\n    }\r\n    return self;\r\n}\r\n-(void)update:(NSString *)message{\r\n    \r\n    NSLog(@"updated %@ with message %@",self.name,message);\r\n}\r\n@end\r\n\r\n// BasicObserver.h\r\n#import <Foundation/Foundation.h>\r\n#import "ObserverInterface.h"\r\n\r\n@interface Observer : ObserverInterface\r\n\r\n@end\r\n\r\n\r\n//BasicObserver.m\r\n#import "Observer.h"\r\n\r\n@implementation Observer\r\n- (id)init\r\n{\r\n    self = [super init];\r\n    if (self) {\r\n         self.name=@"Generic Observer 1";\r\n    }\r\n    return self;\r\n}\r\n\r\n@end\r\n\r\n\r\n\r\n\r\n\r\n', '2014-03-12 20:22:20'),
(84, ' iOS', ' DP State', ' \r\n// State Design Pattern\r\n//VC.h\r\n\r\n#import <UIKit/UIKit.h>\r\n\r\n@interface FBFViewController : UIViewController\r\n\r\n@end\r\n\r\n//VC.m\r\n\r\n#import "FBFViewController.h"\r\n#import "SeasonContext.h"\r\n\r\n\r\n@interface FBFViewController ()\r\n\r\n@end\r\n\r\n@implementation FBFViewController\r\n\r\n- (void)viewDidLoad\r\n{\r\n   \r\n    [super viewDidLoad];\r\n    /*\r\n     * State - Allow an object to alter its behavior when its internal state changes. The object will appear to change its class.\r\n     */\r\n    \r\n    /*\r\n     * SeasonContext, a class that contains a "Season" class and has a constructor that accepts a "Season" as a parameter. It can "set" the season and\r\n     * describe the season.\r\n     *\r\n     * Season, is an INTERFACE that accepts a context as a parameter.\r\n     *\r\n     * All the various seasons implement the Season constructor, and using its context, sets the next season after declaring itself the season in text.\r\n     */\r\n    \r\n    SeasonContext *seasonContext = [[SeasonContext alloc]init];\r\n    [seasonContext whatIsTheSeason];\r\n    [seasonContext whatIsTheSeason];\r\n    [seasonContext whatIsTheSeason];\r\n    [seasonContext whatIsTheSeason];\r\n    \r\n}\r\n\r\n- (void)didReceiveMemoryWarning\r\n{\r\n    [super didReceiveMemoryWarning];\r\n    // Dispose of any resources that can be recreated.\r\n}\r\n\r\n@end\r\n\r\n\r\n//SeasonContext.h\r\n\r\n#import <Foundation/Foundation.h>\r\n#import "Season.h"\r\n@class Season;\r\n\r\n@interface SeasonContext : NSObject\r\n{\r\n    Season *season;\r\n}\r\n@property (nonatomic, retain) Season *season;\r\n-(void) whatIsTheSeason;\r\n\r\n@end\r\n\r\n\r\n// SeasonContext.m\r\n\r\n#import "SeasonContext.h"\r\n#import "Winter.h"\r\n\r\n@implementation SeasonContext\r\n\r\n@synthesize season = _season;\r\n\r\n- (id)init\r\n{\r\n    self = [super init];\r\n    if (self) {\r\n        self.season = [[Winter alloc]init];\r\n    }\r\n    return self;\r\n}\r\n-(void)setSeason:(Season *)seasonToSet{\r\n    _season = seasonToSet;\r\n}\r\n\r\n-(void) whatIsTheSeason{\r\n    [_season theSeason:self];\r\n     \r\n    //NSLog(@"The season is your face.");\r\n    \r\n    \r\n}\r\n@end\r\n\r\n\r\n//Season.h\r\n\r\n#import <Foundation/Foundation.h>\r\n#import "SeasonContext.h"\r\n@class SeasonContext;\r\n\r\n@interface Season : NSObject\r\n{\r\n    SeasonContext *context;\r\n}\r\n@property (nonatomic, retain) SeasonContext *context;\r\n-(void) theSeason:(SeasonContext *)ctx;\r\n@end\r\n\r\n\r\n//Season.m\r\n#import "Season.h"\r\n\r\n\r\n@implementation Season\r\n@synthesize context;\r\n\r\n- (id)initWith:(SeasonContext *)ctx\r\n{\r\n    self = [super init];\r\n    if (self) {\r\n        self.context = ctx;\r\n    }\r\n    return self;\r\n}\r\n\r\n-(void) theSeason:(SeasonContext *)ctx{\r\n    self.context = ctx;\r\n}\r\n\r\n@end\r\n\r\n\r\n//Winter.h\r\n\r\n#import <Foundation/Foundation.h>\r\n#import "Season.h"\r\n\r\n@interface Winter : Season\r\n-(void)theSeason:(SeasonContext *)context;\r\n@end\r\n\r\n\r\n//Winter.m\r\n\r\n#import "Winter.h"\r\n#import "Spring.h"\r\n@class Spring;\r\n\r\n@implementation Winter\r\n-(void)theSeason:(SeasonContext *)context{\r\n    NSLog(@"SEASON: is Winter");\r\n    [context setSeason:[[Spring alloc]init]];\r\n}\r\n@end\r\n\r\n\r\n//Spring.h\r\n\r\n#import <Foundation/Foundation.h>\r\n#import "Season.h"\r\n\r\n@interface Spring : Season\r\n-(void)theSeason:(SeasonContext *)context;\r\n@end\r\n\r\n\r\n\r\n//Spring.m\r\n\r\n#import "Spring.h"\r\n#import "Summer.h"\r\n@class Summer;\r\n\r\n@implementation Spring\r\n\r\n-(void)theSeason:(SeasonContext *)context{\r\n    NSLog(@"SEASON: is Spring");\r\n    [context setSeason:[[Summer alloc]init]];\r\n}\r\n\r\n@end\r\n\r\n\r\n// etc etc etc.....\r\n\r\n\r\n', '2014-03-12 21:37:54'),
(85, ' iOS', ' DP Strategy', ' \r\n\r\n//VC.h\r\n\r\n#import <UIKit/UIKit.h>\r\n#import "TacticContext.h"\r\n#import "FootballStrategy.h"\r\n\r\n@interface FBFViewController : UIViewController\r\n{\r\n    NSString *team1;\r\n    NSString *team2;\r\n    TacticContext *tacticContext;\r\n}\r\n@property (nonatomic,retain) NSString *team1;\r\n@property (nonatomic,retain) NSString *team2;\r\n@property (nonatomic,retain) TacticContext *tacticContext;\r\n\r\n@end\r\n\r\n\r\n//VC.m\r\n\r\n#import "FBFViewController.h"\r\n#import "AttackTactic.h"\r\n#import "DefenceTactic.h"\r\n\r\n@interface FBFViewController ()\r\n\r\n@end\r\n\r\n@implementation FBFViewController\r\n@synthesize  team1, team2, tacticContext;\r\n\r\n- (void)viewDidLoad {\r\n	[super viewDidLoad];\r\n\r\n	team1 = @"Barcelona";\r\n	team2 = @"Real Madrid";\r\n\r\n	tacticContext = [[TacticContext alloc] init];\r\n\r\n	AttackTactic *offense = [[AttackTactic alloc]init];\r\n	[tacticContext setStrategy:offense];\r\n	[tacticContext selectTacticForTeam:(team1)];\r\n\r\n	DefenceTactic *defence = [[DefenceTactic alloc]init];\r\n	[tacticContext setStrategy:defence];\r\n	[tacticContext selectTacticForTeam:(team2)];\r\n}\r\n\r\n- (void)didReceiveMemoryWarning {\r\n	[super didReceiveMemoryWarning];\r\n}\r\n\r\n@end\r\n\r\n//TacticContext.h\r\n\r\n#import <Foundation/Foundation.h>\r\n#import "FootballStrategy.h"\r\n\r\n@class FootballStrategy;\r\n\r\n@interface TacticContext : NSObject{\r\n    FootballStrategy *strategy;\r\n}\r\n@property (nonatomic, retain) FootballStrategy *strategy;\r\n\r\n-(void) selectTacticForTeam:(NSString *)team;\r\n-(FootballStrategy *) getStrategy;\r\n-(void) setStrategy:(FootballStrategy *)strategy;\r\n\r\n@end\r\n\r\n\r\n//TacticContext.m\r\n\r\n#import "TacticContext.h"\r\n\r\n@implementation TacticContext\r\n@synthesize strategy;\r\n\r\n-(void) selectTacticForTeam:(NSString *)team{\r\n    [strategy adhereTacticToTeam:team ];\r\n}\r\n-(FootballStrategy *) getStrategy{\r\n    return strategy;\r\n}\r\n-(void)setStrategy:(FootballStrategy *)strgy{\r\n    strategy = strgy;\r\n}\r\n\r\n@end\r\n\r\n\r\n//FootballStrategy.h\r\n\r\n#import <Foundation/Foundation.h>\r\n\r\n@interface FootballStrategy : NSObject\r\n-(void) adhereTacticToTeam: (NSString *)team;\r\n@end\r\n\r\n\r\n//FootballStrategy.m\r\n\r\n#import "FootballStrategy.h"\r\n\r\n@implementation FootballStrategy\r\n-(void) adhereTacticToTeam: (NSString *)team{\r\n    NSLog(@"Strategy: %@ will be using GENERIC strategy? ",team);\r\n}\r\n@end\r\n\r\n\r\n//AttackStrategy.h\r\n\r\n#import <Foundation/Foundation.h>\r\n#import "FootballStrategy.h"\r\n\r\n@interface AttackTactic : FootballStrategy\r\n- (void)adhereTacticToTeam:(NSString *)team;\r\n@end\r\n\r\n\r\n//AttackStrategy.m\r\n\r\n#import "AttackTactic.h"\r\n\r\n@implementation AttackTactic\r\n\r\n- (void)adhereTacticToTeam:(NSString *)team {\r\n	NSLog(@"Strategy: %@ will be playing offense", team);\r\n}\r\n\r\n@end\r\n\r\n\r\n//DefenceStrategy.h\r\n\r\n#import <Foundation/Foundation.h>\r\n#import "FootballStrategy.h"\r\n\r\n@interface DefenceTactic : FootballStrategy\r\n- (void)adhereTacticToTeam:(NSString *)team;\r\n@end\r\n\r\n\r\n//DefenceStrategy.m\r\n\r\n#import "DefenceTactic.h"\r\n\r\n@implementation DefenceTactic\r\n\r\n- (void)adhereTacticToTeam:(NSString *)team {\r\n	NSLog(@"Strategy: %@ will be playing defense", team);\r\n}\r\n\r\n@end\r\n\r\n\r\n\r\n', '2014-03-12 22:22:16'),
(86, ' iOS', ' DP Decorator', ' Design Pattern: Decorator\r\n\r\n\r\n//VC.h\r\n\r\n//VC.m\r\n\r\n#import "FBFViewController.h"\r\n\r\n#import "Icecream.h"\r\n#import "SimpleIcecream.h"\r\n#import "NuttyDecorator.h"\r\n#import "HoneyDecorator.h"\r\n\r\n@interface FBFViewController ()\r\n\r\n@end\r\n\r\n@implementation FBFViewController\r\n\r\n- (void)viewDidLoad\r\n{\r\n    [super viewDidLoad];\r\n    /*\r\n     \r\n     Icecream is an interface that declares a method makeIceCream that returns a string.\r\n     SimpleIcecream implements Icecream and overrides uses the makeIcecream method to retuen a string describing itself.\r\n     \r\n     IcecreamDecorator is the "head decorator" that implements Icecream and in addition to implementing Icecream methods, it has a constructor\r\n     that accepts an Icecream object and stores it as a property.\r\n     \r\n     NuttyDecorator extends IcecreamDecorator and overrides its makeIcecream method and adds additional functionality via more custom methods.\r\n     And the HoneyDecorator does the same thing that the NUtyDecorator does.\r\n     \r\n     When the ultimate icecream is created below, it displays the results from inner parenthesis outward.\r\n     */\r\n    \r\n    Icecream *icecream = [[ HoneyDecorator alloc] initWithIcecream: [[NuttyDecorator alloc]initWithIcecream:[[SimpleIcecream alloc]init]]];\r\n    \r\n    \r\n    \r\n    NSLog(@"%@",icecream.makeIcecream);\r\n }\r\n\r\n- (void)didReceiveMemoryWarning\r\n{\r\n    [super didReceiveMemoryWarning];\r\n\r\n}\r\n\r\n@end\r\n\r\n//Icecream.h\r\n\r\n#import <Foundation/Foundation.h>\r\n\r\n@interface Icecream : NSObject\r\n-(NSString *) makeIcecream;\r\n@end\r\n\r\n\r\n//Icecream.m\r\n\r\n#import "Icecream.h"\r\n\r\n@implementation Icecream\r\n-(NSString *) makeIcecream{\r\n    return @"nothing";\r\n}\r\n@end\r\n\r\n\r\n\r\n//SimpleIcecream.h\r\n\r\n#import <Foundation/Foundation.h>\r\n#import "Icecream.h"\r\n\r\n@interface SimpleIcecream : Icecream\r\n \r\n\r\n@end\r\n\r\n\r\n// SimpleIcecream.m\r\n\r\n#import "SimpleIcecream.h"\r\n\r\n@implementation SimpleIcecream\r\n-(NSString *) makeIcecream{\r\n    return @"Simple Icecream";\r\n}\r\n@end\r\n\r\n\r\n\r\n//IcecreamDecorator.h\r\n\r\n#import <Foundation/Foundation.h>\r\n#import "Icecream.h"\r\n\r\n// should be an abstract class ?\r\n@interface IcecreamDecorator : Icecream\r\n{\r\n    Icecream *specialIcecream;\r\n}\r\n@property (nonatomic, retain) Icecream *specialIcecream;\r\n@end\r\n\r\n\r\n//IcecreamDecorator.m\r\n\r\n#import "IcecreamDecorator.h"\r\n\r\n@implementation IcecreamDecorator\r\n@synthesize specialIcecream;\r\n- (id)initWithIcecream:(Icecream *)icecream\r\n{\r\n    self = [super init];\r\n    if (self) {\r\n        self.specialIcecream =icecream;\r\n    }\r\n    return self;\r\n}\r\n-(NSString *)makeIcecream{\r\n    return [specialIcecream makeIcecream];\r\n}\r\n@end\r\n\r\n\r\n//HoneyDecorator.h\r\n\r\n#import <Foundation/Foundation.h>\r\n#import "IcecreamDecorator.h"\r\n#import "Icecream.h"\r\n\r\n@interface HoneyDecorator : IcecreamDecorator\r\n- (id)initWithIcecream:(Icecream *)icecream;\r\n@end\r\n\r\n//HoneyDecorator.m\r\n\r\n#import "HoneyDecorator.h"\r\n\r\n@implementation HoneyDecorator\r\n- (id)initWithIcecream:(Icecream *)icecream\r\n{\r\n    self = [super init];\r\n    if (self) {\r\n        super.specialIcecream = icecream;\r\n    }\r\n    return self;\r\n}\r\n\r\n-(NSString *)makeIcecream{\r\n    NSString *givenIcecream = [specialIcecream makeIcecream];\r\n    NSString *changedIcecream = [self addHoney];\r\n    NSString *processedIcecream = [NSString stringWithFormat:@"%@ %@",givenIcecream, changedIcecream];\r\n    return processedIcecream;\r\n}\r\n\r\n-(NSString *)addHoney{\r\n    return @" + HONEY";\r\n}\r\n@end\r\n\r\n\r\n//NutsDecorator.h\r\n\r\n#import <Foundation/Foundation.h>\r\n#import "IcecreamDecorator.h"\r\n#import "Icecream.h"\r\n\r\n@interface NuttyDecorator : IcecreamDecorator\r\n- (id)initWithIcecream:(Icecream *)icecream;\r\n@end\r\n\r\n\r\n//NutsDecorator.m\r\n\r\n#import "NuttyDecorator.h"\r\n\r\n@implementation NuttyDecorator\r\n- (id)initWithIcecream:(Icecream *)icecream\r\n{\r\n    self = [super init];\r\n    if (self) {\r\n        super.specialIcecream = icecream;\r\n    }\r\n    return self;\r\n}\r\n\r\n-(NSString *)makeIcecream{\r\n    NSString *givenIcecream = [specialIcecream makeIcecream];\r\n    NSString *changedIcecream = [self addNuts];\r\n    NSString *processedIcecream = [NSString stringWithFormat:@"%@ %@",givenIcecream, changedIcecream];\r\n    return processedIcecream;\r\n}\r\n\r\n-(NSString *)addNuts{\r\n    return @" + NUTS";\r\n}\r\n@end\r\n', '2014-03-12 23:03:26'),
(87, ' iOS', ' DP Factory', ' // Design Pattern : Factory\r\n\r\n//VC.h\r\n\r\n\r\n//VC.m\r\n\r\n#import "FBFViewController.h"\r\n#import "LaughFactory.h"\r\n\r\n@interface FBFViewController ()\r\n\r\n@end\r\n\r\n@implementation FBFViewController\r\n\r\n- (void)viewDidLoad\r\n{\r\n    [super viewDidLoad];\r\n    LaughFactory *laugh = [[LaughFactory alloc]initWithLaughter:3];\r\n    [laugh laugh];\r\n}\r\n\r\n- (void)didReceiveMemoryWarning\r\n{\r\n    [super didReceiveMemoryWarning];\r\n}\r\n\r\n@end\r\n\r\n\r\n//LaughFactory.h\r\n\r\n#import <Foundation/Foundation.h>\r\n\r\n#define kLaughWithGuffaw  1\r\n#define kLaughWithGiggle  2\r\n#define kLaughWithSnicker  3\r\n\r\n@interface LaughFactory : NSObject\r\n\r\n- (LaughFactory *) initWithLaughter:(NSUInteger) laughterType;\r\n\r\n- (void) laugh;\r\n\r\n@end\r\n\r\n\r\n//LaughFactory.m\r\n\r\n#import "LaughFactory.h"\r\n\r\n@interface Guffaws : LaughFactory {}\r\n- (void)laugh;\r\n@end\r\n\r\n@interface Giggles : LaughFactory {}\r\n- (void)laugh;\r\n@end\r\n\r\n@interface Snicker : LaughFactory {}\r\n- (void)laugh;\r\n@end\r\n\r\n@implementation LaughFactory\r\n\r\n- (LaughFactory *)initWithLaughter:(NSUInteger)laughterType {\r\n	id instanceReturn = nil;\r\n\r\n	if (laughterType == kLaughWithGuffaw)\r\n		instanceReturn = [[Guffaws alloc]init];\r\n	else if (laughterType == kLaughWithGiggle)\r\n		instanceReturn = [[Giggles alloc]init];\r\n    else if (laughterType == kLaughWithSnicker)\r\n		instanceReturn = [[Snicker alloc]init];\r\n	else\r\n		; // deal with this\r\n	return instanceReturn;\r\n}\r\n\r\n- (void)laugh {\r\n	NSLog(@"Humbug");\r\n}\r\n\r\n@end\r\n\r\n\r\n@implementation Guffaws\r\n- (void)laugh {\r\n	NSLog(@"OH HA HA HOWAH HA HA HA");\r\n}\r\n\r\n@end\r\n\r\n@implementation Giggles\r\n- (void)laugh {\r\n	NSLog(@"Tee hee");\r\n}\r\n@end\r\n\r\n@implementation Snicker\r\n- (void)laugh {\r\n	NSLog(@"ttttttssssssssss");\r\n}\r\n\r\n@end\r\n', '2014-03-12 23:38:17'),
(88, ' any', ' SOLID GRASP', 'SOLID\r\nSingle responsibility,\r\nOpen-closed,\r\nLiskov substitution,\r\nInterface segregation and\r\nDependency inversion\r\n\r\nS-SRP-Single responsibility principle\r\naclassshould have only a single responsibility (i.e. only one potential change in the software-s specification should be able to affect the specification of the class)\r\n\r\nO-OCP-Open/closed principle\r\nsoftware entities  should be open for extension, but closed for modification.\r\nIn other words you dont touch the existing modules thereby not disturbing the existing functionality, instead you extend the modules to implement the new requirement. So your code is less rigid and fragile and also extensible.\r\n\r\n\r\nL-LSP-Liskov substitution principle\r\nobjects in a program should be replaceable with instances of their subtypes without altering the correctness of that program. See alsodesign by contract.\r\n\r\nI-ISP-Interface segregation principle\r\nmany client-specific interfaces are better than one general-purpose interface.[4]\r\n\r\nD-DIP-Dependency inversion principle\r\none should Depend upon Abstractions. Do not depend upon concretions.[4]\r\nDependency injectionis one method of following this principle.\r\n	1.	High-level modules should not depend on low-level modules. Both should depend on abstractions.\r\n	2.	Abstractions should not depend upon details. Details should depend upon abstractions.\r\n	 \r\n\r\n\r\n====================================================================================================\r\nGRASP: ==========================================================================================\r\nGeneral Responsibility Assignment Software Patterns\r\n\r\nController,\r\nCreator,\r\nIndirection,\r\nInformation Expert,\r\nHighCohesion,\r\nLowCoupling,\r\nPolymorphism,\r\nProtected Variations, and\r\nPure Fabrication\r\n\r\n\r\nController\r\n\r\nTheControllerpattern assigns the responsibility of dealing with system events to a non-UIclass that represents the overall system or ause case scenario. A Controller object is a non-user interface object responsible for receiving or handling a system event.\r\nA use case controller should be used to deal withallsystem events of a use case, and may be used for more than one use case (for instance, for use casesCreate UserandDelete User, one can have a singleUserController, instead of two separate use case controllers).\r\nIt is defined as the first object beyond the UI layer that receives and coordinates ("controls") a system operation. The controller should delegate the work that needs to be done to other objects; it coordinates or controls the activity. It should not do much work itself. The GRASP Controller can be thought of as being a part of the Application/Service layer (assuming that the application has made an explicit distinction between the application/service layer and thedomain layer) in an object-oriented system with Common layers in an information system logical architecture.\r\n\r\nCreator\r\nSee also:Factory pattern\r\nCreation of objects is one of the most common activities in an object-oriented system. Which class is responsible for creating objects is a fundamental property of the relationship between objects of particular classes. Simply, "Creator pattern is responsible for creating an object of class".\r\nIn general, a classBshould be responsible for creating instances of classAif one, or preferably more, of the following apply:\r\n		Instances ofBcontain or compositely aggregate instances ofA\r\n		Instances ofBrecord instances ofA\r\n		Instances ofBclosely use instances ofA\r\n		Instances ofBhave the initializing information for instances ofAand pass it on creation.\r\n\r\n\r\nHigh Cohesion\r\nHigh Cohesionis an evaluative pattern that attempts to keep objects appropriately focused, manageable and understandable. High cohesion is generally used in support of Low Coupling. High cohesion means that the responsibilities of a given element are strongly related and highly focused. Breaking programs into classes and subsystems is an example of activities that increase the cohesive properties of a system. Alternatively, low cohesion is a situation in which a given element has too many unrelated responsibilities. Elements with low cohesion often suffer from being hard to comprehend, hard to reuse, hard to maintain and averse to change.\r\n\r\n\r\nIndirection\r\nSee also:Delegation pattern\r\nTheIndirectionpattern supports low coupling (and reuse potential) between two elements by assigning the responsibility of mediation between them to an intermediate object. An example of this is the introduction of a controller component for mediation between data (model) and its representation (view) in theModel-view-controllerpattern.\r\n\r\nInformation Expert\r\nSee also:Information hiding\r\nInformation Expert(alsoExpertor theExpert Principle) is a principle used to determine where to delegate responsibilities. These responsibilities include methods, computed fields, and so on.\r\nUsing the principle of Information Expert, a general approach to assigning responsibilities is to look at a given responsibility, determine the information needed to fulfill it, and then determine where that information is stored.\r\nInformation Expert will lead to placing the responsibility on the class with the most information required to fulfill it.\r\n\r\nLow Coupling\r\nMain article:Loose coupling\r\nLow Couplingis an evaluative pattern, which dictates how to assign responsibilities to support:\r\n		lower dependency between the classes,\r\n		change in one class having lower impact on other classes,\r\n		higher reuse potential.\r\n\r\n\r\nPolymorphism\r\nAccording toPolymorphism, responsibility of defining the variation of behaviors based on type is assigned to the types for which this variation happens. This is achieved usingpolymorphicoperations.\r\n\r\nProtected Variations\r\nTheProtected Variationspattern protects elements from the variations on other elements (objects, systems, subsystems) by wrapping the focus of instability with aninterfaceand usingpolymorphismto create various implementations of this interface.\r\n\r\nPure Fabrication\r\nAPure Fabricationis a class that does not represent a concept in the problem domain, specially made up to achieve low coupling, high cohesion, and the reuse potential thereof derived (when a solution presented by theInformation Expertpattern does not). This kind of class is called "Service" inDomain-driven design.', '2014-03-20 22:50:34'),
