r\n		-(void)fly;\r\n		@end\r\n    .m\r\n		@implementation Crow\r\n		- (id)init\r\n		{\r\n		self = [super init];\r\n		if (self) {\r\n		self.name = @"Crow";\r\n		}\r\n		return self;\r\n		}\r\n		-(void)fly{\r\n		\r\n		NSLog(@"\\n %@ flying : \\n",self.name );\r\n		}\r\n		@end\r\n\r\n>>>>>>>>>>  java\r\n		public class Crow extends Bird\r\n		{\r\n		private String name;\r\n\r\n		public Crow(){\r\n		this.name = "Crow";\r\n		}\r\n\r\n		public void fly(){\r\n		Log.d("DEBUG", this.name + " is flying....!");\r\n		}\r\n		}\r\n\r\n>>>>>>>>>>  php\r\n    <?php\r\n        class ParentClass {\r\n\r\n            public $parentVar = "parent var";\r\n            public function getColor() {\r\n                return ''RED'';\r\n            }\r\n\r\n        }\r\n\r\n        class ChildClass extends ParentClass {\r\n            //@override\r\n            public function getColor() {\r\n\r\n                return ''BLUE'';\r\n            }\r\n\r\n            //access the super\r\n            public function getParentColor() {\r\n                return parent::getColor();\r\n            }\r\n\r\n        }\r\n\r\n       \r\n    ?>\r\n\r\n>>>>>>>>>>  javascript\r\n    <script>\r\n			 \r\n			alert("js: Basic INHERITENCE");\r\n			// define the Person Class\r\n			function Person() {\r\n			}\r\n\r\n\r\n			Person.prototype.walk = function() {\r\n				alert(''I am walking!'');\r\n			};\r\n			Person.prototype.sayHello = function() {\r\n				alert(''hello'');\r\n			};\r\n\r\n			// define the Student class\r\n			function Student() {\r\n				// Call SUPER constuctor\r\n				Person.call(this);\r\n			};\r\n			// inherit Person\r\n			Student.prototype = new Person();\r\n			// !!!!! correct the constructor pointer because it points to Person !!!!!\r\n			Student.prototype.constructor = Student;\r\n			//@override  replace the sayHello method\r\n			Student.prototype.sayHello = function() {\r\n				alert(''hi, I am a student'');\r\n			};\r\n			// add sayGoodBye method\r\n			Student.prototype.sayGoodBye = function() {\r\n				alert(''goodBye'');\r\n			};\r\n\r\n			\r\n    </script>\r\n\r\nABSTRACT\r\n>>>>>>>>>>  obj-c\r\n\r\n>>>>>>>>>>  java\r\n\r\n>>>>>>>>>>  php\r\n    <?php\r\n        abstract class AbstractClass {\r\n            abstract public function getName();\r\n            abstract public function setName($nameIn);\r\n        }\r\n\r\n        class AbstractImplementor extends AbstractClass {\r\n            private $instanceName;\r\n            //OOPHPAbstractClass  DELEGATE METHODS\r\n            public function getName() {\r\n                return $this -> instanceName;\r\n            }\r\n\r\n            public function setName($nameIn) {\r\n                $this -> instanceName = $nameIn;\r\n            }\r\n\r\n        }\r\n\r\n        $abstracto = new AbstractImplementor();\r\n        $abstracto -> setName(" >Abstracted Harold");\r\n        echo("\\n <TEST ABSTRACT>");\r\n        echo $abstracto -> getName();\r\n    ?>\r\n\r\n>>>>>>>>>>  javascript\r\n            // does javascript have an abstract...?\r\n            \r\n\r\nSTATIC\r\n>>>>>>>>>>  obj-c\r\n            +(void) myStaticMethod;\r\n>>>>>>>>>>  java\r\n\r\n>>>>>>>>>>  php\r\n    <?php\r\n        class StaticClass {\r\n            //a static variable,\r\n            static $red = ''RED'';\r\n            public function getStaticVarRed() {\r\n                return self::$red;\r\n            }\r\n\r\n            public static function staticGetStaticVarRed() {\r\n                return self::$red;\r\n            }\r\n\r\n        }\r\n\r\n        $statico = new StaticClass();\r\n        echo("\\n <TEST STATIC>");\r\n        echo '' >red: '' . $statico -> getStaticVarRed();\r\n        echo '' >test  - show the static var using a static function'';\r\n        echo '' >red: '' . StaticClass::staticGetStaticVarRed();\r\n    ?>\r\n\r\n>>>>>>>>>>  javascript\r\n		\r\n		  Does javascript have static ????\r\n		  \r\n		  ', '2014-03-21 17:11:25'),
