(90, 'js', 'oo cheatsheet', ' <?php /**/ ?>\r\n\r\n<script>\r\n/* ///////////////////// BASIC OBJECT //////////////////////////// */\r\nalert("js: Basic Object");\r\n    function Person(gender) {\r\n        this.gender = gender;\r\n    }\r\n\r\n    Person.prototype.gender = '''';\r\n\r\n    Person.prototype.sayHello = function() {\r\n        alert(''hello'');\r\n        alert(this.gender);\r\n    };\r\n\r\n    var person1 = new Person(''Male'');\r\n    var person2 = new Person(''Female'');\r\n\r\n    // call the Person sayHello method.\r\n    person1.sayHello();\r\n    // hello\r\n</script>\r\n\r\n\r\n<script>\r\n/* ///////////////////// BASIC EXTENDS //////////////////////////// */\r\nalert("js: Basic Extends");\r\n    var extend = function(OBJ, extendsOBJ) {\r\n        for (var property in extendsOBJ) {\r\n            if (OBJ[property] && ( typeof (OBJ[property]) == ''object'') && (OBJ[property].toString() == ''[object Object]'') && extendsOBJ[property])\r\n                extend(OBJ[property], extendsOBJ[property]);\r\n            else\r\n                OBJ[property] = extendsOBJ[property];\r\n        }\r\n        return OBJ;\r\n    }\r\n    \r\n    var child = {\r\n        child : ''test''\r\n    };\r\n     \r\n    var parent = {\r\n        newFoo : function() {\r\n            alert(''hi how do you like the genes I gave you?'');\r\n        }\r\n    };\r\n    \r\n    // the addition\r\n    extend(child, parent);\r\n    // extend it\r\n    child.newFoo(); \r\n</script>\r\n\r\n\r\n\r\n\r\n\r\n<script>\r\n/* ///////////////////// BASIC INHERITENCE //////////////////////////// */\r\nalert("js: Basic INHERITENCE");\r\n    // define the Person Class\r\n    function Person() {\r\n    }\r\n    Person.prototype.walk = function() {\r\n        alert(''I am walking!'');\r\n    };\r\n    Person.prototype.sayHello = function() {\r\n        alert(''hello'');\r\n    };\r\n\r\n    // define the Student class\r\n    function Student() {\r\n        // Call SUPER constuctor\r\n        Person.call(this);\r\n    };\r\n    // inherit Person\r\n    Student.prototype = new Person();\r\n    // !!!!! correct the constructor pointer because it points to Person !!!!!\r\n    Student.prototype.constructor = Student;\r\n    //@override  replace the sayHello method\r\n    Student.prototype.sayHello = function() {\r\n        alert(''hi, I am a student'');\r\n    };\r\n    // add sayGoodBye method\r\n    Student.prototype.sayGoodBye = function() {\r\n        alert(''goodBye'');\r\n    };\r\n\r\n\r\n// USAGE \r\n\r\n    var student1 = new Student();\r\n    student1.sayHello();\r\n    student1.walk();\r\n    student1.sayGoodBye();\r\n\r\n    // check inheritance\r\n    alert( student1 instanceof Person);\r\n    // true\r\n    alert( student1 instanceof Student);\r\n    // true\r\n    \r\n    //Using Object.create the inheritance line would instead be:\r\n    //Student.prototype = Object.create(Person.prototype);\r\n</script>\r\n\r\n<script>\r\n    /* ///////////////////// BASIC INTERFACE //////////////////////////// */\r\n    alert("js: Basic INTERFACE");\r\n    \r\n     function MyObject() {\r\n    }\r\n\r\n    MyObject.prototype.sayHello = function() {\r\n        alert(''hello'');\r\n    };\r\n\r\n    var myObject = new MyObject();\r\n  \r\n /* this IS necessary ?*/\r\n     MyObject.prototype.can = function(methodName) {\r\n        return (( typeof this[methodName]) == "function");\r\n    };\r\n    \r\n\r\n    if (myObject.can("sayHello")) {\r\n        myObject.sayHello();\r\n    }\r\n    if(myObject.can("sayHiYall")) {\r\n        myObject.sayHiYall();\r\n    }else{\r\n        alert(''myObject: you cant do that'');\r\n    }\r\n\r\n</script>', '2014-03-20 00:28:34'),
(91, ' php', ' oo cheatsheet', ' <?php\r\n////////INTERFACE//////////////////////////////////////\r\ninterface InterfaceOne {\r\n    function getNameOne();\r\n    function setNameOne($nameIn);\r\n}\r\nclass ImplementorOfInterface implements InterfaceOne {\r\n    private $instanceNameOne;\r\n    //@override  \r\n    public function getNameOne() {\r\n        return $this -> instanceNameOne;\r\n    }\r\n    public function setNameOne($nameIn) {\r\n        $this -> instanceNameOne = $nameIn;\r\n    }\r\n\r\n}\r\n/////////TEST INTERFACE/////////////////////////////////\r\necho("\\n <TEST INTERFACE>");\r\n$interfacer = new ImplementorOfInterface();\r\n$interfacer -> setNameOne("Harold");\r\necho ''Name One: '' . $interfacer -> getNameOne();\r\n////////INHERITENCE//////////////////////////////////////\r\nclass ParentClass {\r\n    \r\n    public $parentVar = "parent var";\r\npublic function __construct(){\r\n\r\n}\r\n    public function getColor() {\r\n        return ''RED'';\r\n    }\r\n}\r\nclass ChildClass extends ParentClass {\r\n\r\npublic function __construct(){\r\n\r\n    parent::__construct(){\r\n}\r\n    //@override \r\n    public function getColor() {\r\n     \r\n        return ''BLUE'';\r\n    }\r\n    //access the super\r\n    public function getParentColor() {\r\n        return parent::getColor();\r\n    }\r\n\r\n}\r\n/////////TEST INHERITENCE//////////////////////////////\r\necho("\\n <TEST INHERITENCE>");\r\n$inheritor = new ChildClass();\r\necho '' >test 1 - show the child color'' ;\r\necho $inheritor -> getColor();\r\necho '' >test 1 - show the parent color'' ;\r\necho $inheritor -> getParentColor();\r\n//////////////ABSTRACT/////////////////////////////\r\nabstract class AbstractClass {\r\n    abstract public function getName();\r\n    abstract public function setName($nameIn);\r\n}\r\nclass AbstractImplementor extends AbstractClass {\r\n    private $instanceName;\r\n    //OOPHPAbstractClass  DELEGATE METHODS\r\n    public function getName() {\r\n        return $this -> instanceName;\r\n    }\r\n    public function setName($nameIn) {\r\n        $this -> instanceName = $nameIn;\r\n    }\r\n}\r\n/////////TEST ABSTRACT//////////////////////\r\n$abstracto = new AbstractImplementor();\r\n$abstracto -> setName(" >Abstracted Harold");\r\necho("\\n <TEST ABSTRACT>");\r\necho $abstracto -> getName();\r\n//////////////STATIC/////////////////////////////\r\nclass StaticClass {\r\n    //a static variable,\r\n    static $red = ''RED'';\r\n    public function getStaticVarRed() {\r\n        return self::$red;\r\n    }\r\n    public static function staticGetStaticVarRed() {\r\n        return self::$red;\r\n    }\r\n}\r\n/////////TEST STATIC//////////////////////////\r\n$statico = new StaticClass();\r\necho("\\n <TEST STATIC>");\r\necho '' >red: '' . $statico -> getStaticVarRed();\r\necho '' >test  - show the static var using a static function'';\r\necho '' >red: '' . StaticClass::staticGetStaticVarRed();\r\n\r\n?>', '2014-06-02 00:23:27'),
(92, ' js', ' SOLID SRP', ' <?php\r\n?>\r\n<script>\r\n	alert("SOLID: js : SRP");\r\n	function User(username, password) {\r\n		this.username = username;\r\n		this.password = password;\r\n	}\r\n\r\n\r\n	User.prototype.username = "";\r\n	User.prototype.password = "";\r\n\r\n	function AccessChecker() {\r\n		this.username = "test";\r\n		this.password = "1234";\r\n	}\r\n\r\n\r\n	AccessChecker.prototype.username = "";\r\n	AccessChecker.prototype.password = "";\r\n	AccessChecker.prototype.checkAccess = function(user) {\r\n		var result = false;\r\n		if ((user.username == this.username) && (this.password == user.password)) {\r\n			result = true;\r\n		}\r\n		return result;\r\n	}\r\n	function EmailChanger(accessChecker) {\r\n		this.accessChecker = accessChecker;\r\n	}\r\n\r\n\r\n	EmailChanger.prototype.accessChecker = "";\r\n	EmailChanger.prototype.changeEmail = function(user) {\r\n		if (this.accessChecker.checkAccess(user)) {\r\n			alert("Grant Email Change");\r\n		} else {\r\n			alert("Reject Email Change");\r\n		}\r\n	}\r\n	user = new User("test", "1234");\r\n	accessChecker = new AccessChecker();\r\n	emailChanger = new EmailChanger(accessChecker);\r\n	emailChanger.changeEmail(user);\r\n\r\n</script>', '2014-03-20 21:41:28'),
(93, ' js', ' SOLID OCP', ' <?php ?>\r\n<script>\r\n	alert("SOLID: js : OCP");\r\n	function Validator() {\r\n	}\r\n\r\n\r\n	Validator.prototype.isValid = function(credentials) {\r\n		alert("creds:" + credentials);\r\n		var result = false;\r\n		if (credentials == "good") {\r\n			result = true;\r\n		}\r\n		return result;\r\n	};\r\n\r\n	function CarLoanValidator() {\r\n		// @super\r\n		Validator.call(this);\r\n	};\r\n	CarLoanValidator.prototype = new Validator();\r\n	CarLoanValidator.prototype.constructor = CarLoanValidator;\r\n	//@override\r\n	CarLoanValidator.prototype.isValid = function(credentials) {\r\n		alert(''Car creds:'' + credentials);\r\n		var result = false;\r\n		if (credentials == "GoodCarCredentials") {\r\n			result = true;\r\n		}\r\n		return result;\r\n	};\r\n\r\n	function PersonalLoanValidator() {\r\n		// @super\r\n		Validator.call(this);\r\n	};\r\n	PersonalLoanValidator.prototype = new Validator();\r\n	PersonalLoanValidator.prototype.constructor = PersonalLoanValidator;\r\n	//@override\r\n	PersonalLoanValidator.prototype.isValid = function(credentials) {\r\n		alert(''Personal creds:'' + credentials);\r\n		var result = false;\r\n		if (credentials == "GoodPersonalCredentials") {\r\n			result = true;\r\n		}\r\n		return result;\r\n	};\r\n\r\n	function LoanApprovalHandler(carLoanValidator, personalLoanValidator) {\r\n		this.carLoanValidator = carLoanValidator;\r\n		this.personalLoanValidator = personalLoanValidator;\r\n\r\n	}\r\n\r\n\r\n	LoanApprovalHandler.prototype.getValidationWithLoanTypeAndCredentials = function(loanType, credentials) {\r\n		var result = false;\r\n		if (loanType == "car") {\r\n			result = this.carLoanValidator.isValid(credentials);\r\n		}\r\n		if (loanType == "personal") {\r\n			result = this.personalLoanValidator.isValid(credentials);\r\n		}\r\n		return result;\r\n	}\r\n	////////////////  IMPLEMENTATION\r\n	clv = new CarLoanValidator();\r\n	plv = new PersonalLoanValidator();\r\n\r\n	lah = new LoanApprovalHandler(clv, plv);\r\n	result = lah.getValidationWithLoanTypeAndCredentials("personal", "GoodPersonalCredentials");\r\n\r\n	if (result) {\r\n		alert("Approved");\r\n	} else {\r\n		alert("Rejected");\r\n	}\r\n	\r\n\r\n</script>', '2014-03-20 21:42:29'),
(94, ' js', ' SOLID LSP', ' <?php\r\n\r\n?>\r\n<script>\r\n	alert("SOLID: js : LSP");\r\n	//Use inheritence for the substitution, NOT extends.\r\n\r\n	function Bird() {\r\n	}\r\n\r\n	Bird.prototype.fly = function() {\r\n		alert(''hello Im a bird'');\r\n	};\r\n\r\n	function Crow() {\r\n		// @super\r\n		Bird.call(this);\r\n	};\r\n	Crow.prototype = new Bird();\r\n	Crow.prototype.constructor = Crow;\r\n	//@override\r\n	Crow.prototype.fly = function() {\r\n		alert(''...as the crow flies.'');\r\n	};\r\n	\r\n	function Ostrich() {\r\n        // @super\r\n        Bird.call(this);\r\n    };\r\n    Ostrich.prototype = new Bird();\r\n    Ostrich.prototype.constructor = Ostrich;\r\n    //@override\r\n    Ostrich.prototype.fly = function() {\r\n        alert(''...as the ostrich...flies?'');\r\n    };\r\n\r\n	// USAGE///////////////////////////////////////\r\n	var crow = new Crow();\r\n	var ostrich = new Ostrich();\r\n    \r\n    /////  SUBSTITUTION\r\n    var arrayOfBirds = [crow,ostrich];\r\n    \r\n    for(x in arrayOfBirds){\r\n       var thisBird = arrayOfBirds[x];\r\n       thisBird.fly();\r\n    }\r\n    \r\n    \r\n    \r\n\r\n</script>', '2014-03-20 21:43:08');
