(97, ' php', ' SOLID SRP', ' <?php\r\n\r\nclass User {\r\n\r\n    private $username;\r\n    private $password;\r\n\r\n    public function __construct() {\r\n\r\n    }\r\n\r\n    public function setUsername($username) {\r\n        $this -> username = $username;\r\n    }\r\n\r\n    public function getUsername() {\r\n        return $this -> username;\r\n    }\r\n\r\n    public function setPassword($password) {\r\n        $this -> password = $password;\r\n    }\r\n\r\n    public function getPassword() {\r\n        echo("<br>get password!!!");\r\n        return $this -> password;\r\n    }\r\n\r\n}\r\n\r\nclass EmailChanger {\r\n\r\n    private $accessChecker;\r\n\r\n    public function __construct($accessChecker) {\r\n        $this -> accessChecker = $accessChecker;\r\n    }\r\n\r\n    // Single Responsibility Principle: one class one responsibility\r\n    public function changeEmail($user) {\r\n        if ($this -> accessChecker -> checkAccess($user)) {\r\n            //grant permission\r\n            echo("<br>success");\r\n        } else {\r\n            echo("<br>fail");\r\n        }\r\n    }\r\n\r\n}\r\n\r\nclass AccessChecker {\r\n    public function __construct() {\r\n        echo("<br>AccessChecker constructed...");\r\n    }\r\n\r\n    // Single Responsibility Principle: one class one responsibility\r\n    public function checkAccess($user) {\r\n\r\n        $bool_result = false;\r\n        // code to verify if user is valid.\r\n        if ($user -> getPassword() == "1234") {\r\n            $bool_result = true;\r\n        }\r\n\r\n        return $bool_result;\r\n    }\r\n\r\n}\r\n\r\n$user = new User();\r\n$user -> setUsername("Joe");\r\n$user -> setPassword("1234");\r\necho("<br>User:" . $user -> getUsername() . ":" . $user -> getPassword() . "!");\r\n\r\n$accessChecker = new AccessChecker();\r\n$emailChanger = new EmailChanger($accessChecker);\r\n\r\n$emailChanger -> changeEmail($user);\r\n?>', '2014-03-20 22:33:31'),
(98, ' php', ' SOLID OCP', ' <?php\r\ninterface Validator {\r\n\r\n      public function isValid($credentials);\r\n}\r\n\r\n\r\nclass CarLoanValidator implements Validator {\r\n\r\n    private $bool_result;\r\n\r\n    public function __construct() {\r\n\r\n    }\r\n\r\n    public function isValid($credentials) {\r\n        $bool_result = false;\r\n        echo("<br>Here is the custom validation code for CAR Loans with credentials:" . $credentials);\r\n        if ($credentials == "carLoanPlease") {\r\n            $bool_result = true;\r\n        }\r\n        return $bool_result;\r\n    }\r\n\r\n}\r\n\r\n\r\n\r\nclass PersonalLoanValidator implements Validator {\r\n\r\n    private $bool_result;\r\n\r\n    public function __construct() {\r\n\r\n    }\r\n\r\n    public function isValid($credentials) {\r\n        $bool_result = false;\r\n        echo("<br>Here is the custom validation code for Personal Loans with credentials:" . $credentials);\r\n        if ($credentials == "personalLoanPlease") {\r\n            $bool_result = true;\r\n        }\r\n        return $bool_result;\r\n    }\r\n\r\n}\r\n\r\nclass LoanApprovalHandler {\r\n\r\n    private $result;\r\n\r\n    public function __construct() {\r\n        $this -> result = false;\r\n    }\r\n\r\n    public function getValidationForLoanTypeAndCredentials($loanType, $credentials) {\r\n        if ($loanType == "car") {\r\n            $CLV = new CarLoanValidator();\r\n            $result = $CLV -> isValid($credentials);\r\n        }\r\n        if ($loanType == "personal") {\r\n            $PLV = new PersonalLoanValidator();\r\n            $result = $PLV -> isValid($credentials);\r\n        }\r\n        return $result;\r\n    }\r\n\r\n}\r\n\r\n\r\necho("<br>OCP: Open/Closed Principle<br>");\r\n\r\n$lah = new LoanApprovalHandler();\r\n\r\n$result =  $lah -> getValidationForLoanTypeAndCredentials("car","carLoanPlease");\r\n \r\nif($result){\r\n    echo("Validation COMPLETE and ACCEPTED");\r\n}else{\r\n    echo("Validation COMPLETE and REJECTED");\r\n}\r\n\r\n\r\n?>', '2014-03-20 22:34:02'),
(99, ' php', ' SOLID LSP', ' <?php\r\nclass Bird {\r\n    \r\n     private $name;\r\n\r\n     public function __construct() {\r\n     $this -> name = "Bird";\r\n     }\r\n     \r\n    public function fly() {\r\n        echo("fly method");\r\n    }\r\n\r\n    public function eat() {\r\n        echo("eat method");\r\n    }\r\n\r\n}\r\n\r\n\r\nclass Crow extends Bird {\r\n\r\n    public function __construct() {\r\n        $this -> name = "Crow";\r\n    }\r\n\r\n    //@override\r\n    public function fly() {\r\n        echo("<br>...as the crow flies...." . $this -> name . "");\r\n    }\r\n\r\n}\r\n\r\n\r\nclass Ostrich extends Bird {\r\n\r\n    public function __construct() {\r\n        $this -> name = "Ostrich";\r\n    }\r\n\r\n    //@override\r\n    public function fly() {\r\n        echo("<br>...I''ll just walk, thanks...." . $this -> name . "");\r\n    }\r\n\r\n}\r\n\r\necho("<br>LSP: Liskov Substitution Principle<br>");\r\n$crow = new Crow();\r\n$ostrich = new Ostrich();\r\n\r\n$array = array($crow, $ostrich);\r\n\r\nforeach ($array as $bird) {\r\n\r\n    $bird -> fly();\r\n}\r\n\r\n?>', '2014-03-20 22:34:37'),
(100, ' php', ' SOLID ISP', ' <?php \r\n\r\ninterface Feedable extends Workable{\r\n    public function feed();\r\n}\r\n\r\ninterface Workable {\r\n    public function work();\r\n}\r\n\r\n\r\n\r\nclass Manager {\r\n    private $worker;\r\n    private $robot;\r\n\r\n    public function __construct() {\r\n        // allocate your stuff\r\n        //PHP doesn''t support multiple constructors.\r\n        //workaround is public static functions\r\n        /*\r\n         * public static function withRobot($robot){\r\n         * $instance = new self();\r\n         * $instance->constructWithRobot($robot);\r\n         * return $instance;\r\n         * }\r\n         *\r\n         * //usage:\r\n         * $manager = Manager::withRobot($robot);\r\n         */\r\n    }\r\n\r\n    public static function withWorker($worker) {\r\n        $instance = new self();\r\n        $instance -> constructWithWorker($worker);\r\n        return $instance;\r\n    }\r\n\r\n    public static function withRobot($robot) {\r\n        $instance = new self();\r\n        $instance -> constructWithRobot($robot);\r\n        return $instance;\r\n    }\r\n\r\n    public function constructWithWorker($worker) {\r\n        $this -> worker = $worker;\r\n    }\r\n\r\n    public function constructWithRobot($robot) {\r\n        $this -> robot = $robot;\r\n    }\r\n\r\n    public function manageWorker() {\r\n        $this -> worker -> work();\r\n        $this -> worker -> feed();\r\n    }\r\n\r\n    public function manageRobot() {\r\n        $this -> robot -> work();\r\n    }\r\n\r\n}\r\n\r\n\r\n \r\n \r\nclass Robot implements Workable{\r\n    \r\n    public function work(){\r\n        echo("<br>Robot working...no need to implement the ''feedable'' interface for robot.");   \r\n    }\r\n     \r\n}\r\n\r\n\r\n \r\nclass Worker implements Workable, Feedable{\r\n    \r\n    public function work(){\r\n        echo("<br>Worker working...");  \r\n    }\r\n    \r\n    public function feed(){\r\n        echo("<br>Worker feeding...");  \r\n    }\r\n}\r\n\r\necho("SOLID: ISP : Interface Segregation Principle");\r\n\r\n\r\n$worker = new Worker();\r\n$robot = new Robot();\r\n\r\n$manager = Manager::withWorker($worker);\r\n$manager -> manageWorker();\r\n\r\n$managerOfRobot = Manager::withRobot($robot);\r\n$managerOfRobot -> manageRobot();\r\n?>', '2014-03-20 22:35:02'),
(101, ' php', ' SOLID DIP', ' <?php\r\n////////INTERFACE//////////////////////////////////////\r\ninterface StoreInterface {\r\n\r\n    function callWarehouse($location);\r\n}\r\n\r\ninterface WarehouseInterface {\r\n\r\n    function isClosest($location);\r\n\r\n}\r\n\r\nclass ImplementorOfInterface implements StoreInterface , WarehouseInterface {\r\n    //@override Store Methods\r\n    public function callWarehouse($location) {\r\n        $result = $this -> isClosest($location);\r\n        return $result;\r\n    }\r\n\r\n    //@override Warehouse Methods\r\n    function isClosest($location) {\r\n        $warehouseE = new WarehouseE();\r\n        $distanceE = $warehouseE -> distanceTo($location);\r\n\r\n        $warehouseF = new WarehouseF();\r\n        $distanceF = $warehouseF -> distanceTo($location);\r\n\r\n        $warehouseG = new WarehouseG();\r\n        $distanceG = $warehouseG -> distanceTo($location);\r\n\r\n        $warehouses = array("warehouse e" => $distanceE, "warehouse f" => $distanceF, "warehouse g" => $distanceG);\r\n        $closest = array_keys($warehouses, min($warehouses));\r\n       \r\n        return $closest[0];\r\n        \r\n    }\r\n\r\n}\r\n\r\nclass Location {\r\n    public $name;\r\n    public $implementor;\r\n    public function __construct($implementor, $name) {\r\n        $this -> name = $name;\r\n        $this -> implementor = $implementor;\r\n    }\r\n\r\n    public function whatIsTheClosestWarehouseToLocation($location) {\r\n        echo("\\nLocation object calls the Concrete Interface for stores and warehouses");\r\n        $closest = $this -> implementor -> callWarehouse($location);\r\n        echo("\\n The closest is: ''" . $closest . "''");\r\n    }\r\n\r\n}\r\n\r\nclass WarehouseE {\r\n    public function distanceTo($location) {\r\n        if ($location == "myStoreLocation") {\r\n            $distance = 3.5;\r\n        }\r\n        if ($location == "target") {\r\n            $distance = 1.7;\r\n        }\r\n        return $distance;\r\n    }\r\n\r\n}\r\n\r\nclass WarehouseF {\r\n    public function distanceTo($location) {\r\n        if ($location == "myStoreLocation") {\r\n            $distance = 1.5;\r\n        }\r\n        if ($location == "target") {\r\n            $distance = 4.7;\r\n        }\r\n        return $distance;\r\n    }\r\n\r\n}\r\n\r\nclass WarehouseG {\r\n    public function distanceTo($location) {\r\n        if ($location == "myStoreLocation") {\r\n            $distance = 18.6;\r\n        }\r\n        if ($location == "target") {\r\n            $distance = 23.7;\r\n        }\r\n        return $distance;\r\n    }\r\n\r\n}\r\n\r\n$implementor = new ImplementorOfInterface();\r\n$myStoreLocation = "myStoreLocation";\r\n$target = "target";\r\n$location = new Location($implementor,$target);\r\n$location -> whatIsTheClosestWarehouseToLocation($location->name);\r\n?>', '2014-03-20 22:35:29'),
