(122, ' js ajax', ' postAjaxForm', ' function postAjaxForm(dataString,controller,receiverId) {\r\n	//alert(dataString + controller + receiverId);\r\n	 /*\r\n	  * This method \r\n	  * 1) passes the dataString to the controller\r\n	  * 2) receives whatever html response the controller generates \r\n	  * 3) returns the html to the element with the id = ''receiverId''.\r\n	  */\r\n	var xmlhttp;\r\n	if (window.XMLHttpRequest) {// code for IE7+, Firefox, Chrome, Opera, Safari\r\n		xmlhttp = new XMLHttpRequest();\r\n	} else {// code for IE6, IE5\r\n		xmlhttp = new ActiveXObject("Microsoft.XMLHTTP");\r\n	}\r\n	xmlhttp.onreadystatechange = function() {\r\n		//alert(''ready state:''+xmlhttp.readyState+''status:''+xmlhttp.status);\r\n		if (xmlhttp.readyState == 4 && xmlhttp.status == 200) {\r\n			document.getElementById(receiverId).innerHTML = xmlhttp.responseText;\r\n			//alert(''response:''+xmlhttp.responseText);\r\n		}\r\n	}\r\n	//alert(''controller:''+controller);\r\n	xmlhttp.open("POST", controller, true);\r\n	xmlhttp.setRequestHeader("Content-type", "application/x-www-form-urlencoded");\r\n	//alert(''data string:''+dataString);\r\n	xmlhttp.send(dataString);\r\n}\r\n\r\n \r\n \r\n\r\n/* for GET add data arguments to the controller filename...?fname=Henry&lname=Ford" */\r\nfunction loadDoc(filename,receiverId) {\r\n	var xmlhttp;\r\n	if (window.XMLHttpRequest) {// code for IE7+, Firefox, Chrome, Opera, Safari\r\n		xmlhttp = new XMLHttpRequest();\r\n	} else {// code for IE6, IE5\r\n		xmlhttp = new ActiveXObject("Microsoft.XMLHTTP");\r\n	}\r\n	xmlhttp.onreadystatechange = function() {\r\n		if (xmlhttp.readyState == 4 && xmlhttp.status == 200) {\r\n			document.getElementById(receiverId).innerHTML = xmlhttp.responseText;\r\n		}\r\n	}\r\n	xmlhttp.open("GET", filename, true);\r\n	xmlhttp.send();\r\n}\r\n', '2014-06-19 14:40:08'),
